 27â†’/**
 28â†’ * Copyable value with a copy button.
 29â†’ * Shows the full value with word-break and a small copy button.
 30â†’ */
 31â†’function CopyableValue({ value }: { value: string }) {
 32â†’  const [copied, setCopied] = useState(false);
 33â†’  const timeoutRef = useRef<ReturnType<typeof setTimeout>>();
 34â†’
 35â†’  useEffect(() => {
 36â†’    return () => {
 37â†’      if (timeoutRef.current) clearTimeout(timeoutRef.current);
 38â†’    };
 39â†’  }, []);
 40â†’
 41â†’  const handleCopy = useCallback(async () => {
 42â†’    if (timeoutRef.current) clearTimeout(timeoutRef.current);
 43â†’    try {
 44â†’      await navigator.clipboard.writeText(value);
 45â†’    } catch {
 46â†’      // Fallback for older browsers
 47â†’      const textarea = document.createElement('textarea');
 48â†’      textarea.value = value;
 49â†’      textarea.style.position = 'fixed';
 50â†’      textarea.style.opacity = '0';
 51â†’      document.body.appendChild(textarea);
 52â†’      textarea.select();
 53â†’      document.execCommand('copy');
 54â†’      document.body.removeChild(textarea);
 55â†’    }
 56â†’    setCopied(true);
 57â†’    timeoutRef.current = setTimeout(() => setCopied(false), 2000);
 58â†’  }, [value]);
 59â†’
 60â†’  return (
 61â†’    <div className="details-copyable">
 62â†’      <span className="details-copyable-text">{value}</span>
 63â†’      <button
 64â†’        type="button"
 65â†’        className={`details-copy-btn ${copied ? 'details-copy-btn--copied' : ''}`}
 66â†’        onClick={handleCopy}
 67â†’        aria-label={copied ? 'Copied to clipboard' : 'Copy to clipboard'}
 68â†’        aria-pressed={copied}
 69â†’      >
 70â†’        {copied ? 'ok' : 'cp'}
 71â†’      </button>
 72â†’    </div>
 73â†’  );
 74â†’}
