102â†’/**
103â†’ * Version history component for files with past versions.
104â†’ * Shows version entries with download, restore, and delete actions.
105â†’ */
106â†’function VersionHistory({
107â†’  versions,
108â†’  fileName,
109â†’  folderKey,
110â†’  parentFolderId,
111â†’  fileId,
112â†’  onRestored,
113â†’}: {
114â†’  versions: VersionEntry[];
115â†’  fileName: string;
116â†’  folderKey: Uint8Array;
117â†’  parentFolderId: string;
118â†’  fileId: string;
119â†’  onRestored: () => void;
120â†’}) {
121â†’  const { restoreVersion, deleteVersion } = useFolder();
122â†’  const [confirmingRestore, setConfirmingRestore] = useState<number | null>(null);
123â†’  const [confirmingDelete, setConfirmingDelete] = useState<number | null>(null);
124â†’  const [loadingAction, setLoadingAction] = useState<string | null>(null);
125â†’  const [actionError, setActionError] = useState<string | null>(null);
126â†’
127â†’  const handleDownloadVersion = useCallback(
128â†’    async (version: VersionEntry) => {
129â†’      const privateKey = useAuthStore.getState().vaultKeypair?.privateKey;
130â†’      if (!privateKey) return;
131â†’
132â†’      setLoadingAction('download');
133â†’      setActionError(null);
134â†’      try {
135â†’        const decrypted = await downloadFile(
136â†’          {
137â†’            cid: version.cid,
138â†’            iv: version.fileIv,
139â†’            wrappedKey: version.fileKeyEncrypted,
140â†’            originalName: fileName,
141â†’            encryptionMode: version.encryptionMode,
142â†’          },
143â†’          privateKey
144â†’        );
145â†’        triggerBrowserDownload(decrypted, fileName);
146â†’      } catch {
147â†’        setActionError('Failed to download version');
148â†’      } finally {
149â†’        setLoadingAction(null);
150â†’      }
151â†’    },
152â†’    [fileName]
153â†’  );
154â†’
155â†’  const handleRestore = useCallback(
156â†’    async (versionIndex: number) => {
157â†’      setLoadingAction(`restore-${versionIndex}`);
158â†’      setActionError(null);
159â†’      setConfirmingRestore(null);
160â†’      try {
161â†’        await restoreVersion(parentFolderId, fileId, versionIndex);
162â†’        onRestored();
163â†’      } catch {
164â†’        setActionError('Failed to restore version');
165â†’      } finally {
166â†’        setLoadingAction(null);
167â†’      }
168â†’    },
169â†’    [restoreVersion, parentFolderId, fileId, onRestored]
170â†’  );
171â†’
172â†’  const handleDelete = useCallback(
173â†’    async (versionIndex: number) => {
174â†’      setLoadingAction(`delete-${versionIndex}`);
175â†’      setActionError(null);
176â†’      setConfirmingDelete(null);
177â†’      try {
178â†’        await deleteVersion(parentFolderId, fileId, versionIndex);
179â†’        onRestored();
180â†’      } catch {
181â†’        setActionError('Failed to delete version');
182â†’      } finally {
183â†’        setLoadingAction(null);
184â†’      }
185â†’    },
186â†’    [deleteVersion, parentFolderId, fileId, onRestored]
187â†’  );
188â†’
189â†’  // Dismiss unused param lint -- folderKey is passed for future use
190â†’  void folderKey;
191â†’
192â†’  return (
193â†’    <div className="details-version-section">
194â†’      <div className="details-section-header">{'// version history'}</div>
195â†’
196â†’      {actionError && (
197â†’        <div className="details-version-error" role="alert">
198â†’          {actionError}
199â†’        </div>
200â†’      )}
201â†’
202â†’      <div className="details-version-list">
203â†’        {versions.map((version, index) => {
204â†’          // v1 = oldest, vN = newest (reversed display numbering)
205â†’          const versionNumber = versions.length - index;
206â†’          const isLoading = loadingAction !== null;
207â†’
208â†’          return (
209â†’            <div key={`${version.cid}-${version.timestamp}`} className="details-version-entry">
210â†’              <div className="details-version-info">
211â†’                <span className="details-version-number">v{versionNumber}</span>
212â†’                <span className="details-version-date">
213â†’                  {formatDateWithTime(version.timestamp)}
214â†’                </span>
215â†’                <span className="details-version-size">{formatBytes(version.size)}</span>
216â†’                <span className="details-version-mode">{version.encryptionMode}</span>
217â†’              </div>
218â†’
219â†’              {/* Inline confirm for restore */}
220â†’              {confirmingRestore === index ? (
221â†’                <div className="details-version-confirm" role="alert">
222â†’                  <span className="details-version-confirm-text">
223â†’                    Restore version from {formatDate(version.timestamp)}? Current version will be
224â†’                    saved as a past version.
225â†’                  </span>
226â†’                  <div className="details-version-confirm-actions">
227â†’                    <button
228â†’                      type="button"
229â†’                      className="details-version-confirm-btn details-version-confirm-btn--yes"
230â†’                      onClick={() => handleRestore(index)}
231â†’                      aria-label="Confirm restore"
232â†’                    >
233â†’                      confirm
234â†’                    </button>
235â†’                    <button
236â†’                      type="button"
237â†’                      className="details-version-confirm-btn details-version-confirm-btn--no"
238â†’                      onClick={() => setConfirmingRestore(null)}
239â†’                      aria-label="Cancel restore"
240â†’                    >
241â†’                      cancel
242â†’                    </button>
243â†’                  </div>
244â†’                </div>
245â†’              ) : confirmingDelete === index ? (
246â†’                <div className="details-version-confirm" role="alert">
247â†’                  <span className="details-version-confirm-text">
248â†’                    Delete this version? This cannot be undone.
249â†’                  </span>
250â†’                  <div className="details-version-confirm-actions">
251â†’                    <button
252â†’                      type="button"
253â†’                      className="details-version-confirm-btn details-version-confirm-btn--yes"
254â†’                      onClick={() => handleDelete(index)}
255â†’                      aria-label="Confirm delete"
256â†’                    >
257â†’                      confirm
258â†’                    </button>
259â†’                    <button
260â†’                      type="button"
261â†’                      className="details-version-confirm-btn details-version-confirm-btn--no"
262â†’                      onClick={() => setConfirmingDelete(null)}
263â†’                      aria-label="Cancel delete"
264â†’                    >
265â†’                      cancel
266â†’                    </button>
267â†’                  </div>
268â†’                </div>
269â†’              ) : (
270â†’                <div className="details-version-actions">
271â†’                  <button
272â†’                    type="button"
273â†’                    className="details-version-btn"
274â†’                    onClick={() => handleDownloadVersion(version)}
275â†’                    disabled={isLoading}
276â†’                    aria-label={`Download version ${versionNumber}`}
277â†’                  >
278â†’                    {loadingAction === 'download' ? '...' : 'dl'}
279â†’                  </button>
280â†’                  <button
281â†’                    type="button"
282â†’                    className="details-version-btn details-version-btn--restore"
283â†’                    onClick={() => {
284â†’                      setConfirmingRestore(index);
285â†’                      setConfirmingDelete(null);
286â†’                    }}
287â†’                    disabled={isLoading}
288â†’                    aria-label={`Restore version ${versionNumber}`}
289â†’                  >
290â†’                    {loadingAction === `restore-${index}` ? '...' : 'restore'}
291â†’                  </button>
292â†’                  <button
293â†’                    type="button"
294â†’                    className="details-version-btn details-version-btn--delete"
295â†’                    onClick={() => {
296â†’                      setConfirmingDelete(index);
297â†’                      setConfirmingRestore(null);
298â†’                    }}
299â†’                    disabled={isLoading}
300â†’                    aria-label={`Delete version ${versionNumber}`}
301â†’                  >
302â†’                    {loadingAction === `delete-${index}` ? '...' : 'rm'}
303â†’                  </button>
304â†’                </div>
305â†’              )}
306â†’            </div>
307â†’          );
308â†’        })}
309â†’      </div>
310â†’    </div>
311â†’  );
312â†’}
