510â†’/**
511â†’ * Details dialog for file/folder metadata.
512â†’ *
513â†’ * Shows technical information about the selected item:
514â†’ * - Files: Content CID, metadata CID, encryption mode, IV, wrapped key, version history
515â†’ * - Folders: IPNS name, metadata CID, sequence number, wrapped keys
516â†’ *
517â†’ * Resolves the parent folder's IPNS record on open to get the live
518â†’ * metadata CID. Sensitive key material is displayed in redacted form.
519â†’ */
520â†’export function DetailsDialog({
521â†’  open,
522â†’  onClose,
523â†’  item,
524â†’  folderKey,
525â†’  parentFolderId,
526â†’}: DetailsDialogProps) {
527â†’  const [metadataCid, setMetadataCid] = useState<string | null>(null);
528â†’  const [metadataLoading, setMetadataLoading] = useState(false);
529â†’  const [fileMeta, setFileMeta] = useState<FileMetadata | null>(null);
530â†’  const [fileMetaLoading, setFileMetaLoading] = useState(false);
531â†’  // Counter to force re-fetch after version restore/delete
532â†’  const [metadataRefresh, setMetadataRefresh] = useState(0);
533â†’
534â†’  // For folders, also look up the folder node for sequence number and child count
535â†’  const folderNode = useFolderStore((state) =>
536â†’    item?.type === 'folder' ? state.folders[item.id] : undefined
537â†’  );
538â†’
539â†’  // Resolve folder IPNS to get metadata CID (folders only)
540â†’  useEffect(() => {
541â†’    if (!open || !item || item.type !== 'folder') {
542â†’      if (!item || item.type !== 'file') {
543â†’        setMetadataCid(null);
544â†’        setMetadataLoading(false);
545â†’      }
546â†’      return;
547â†’    }
548â†’
549â†’    if (!item.ipnsName) {
550â†’      setMetadataLoading(false);
551â†’      setMetadataCid(null);
552â†’      return;
553â†’    }
554â†’
555â†’    let cancelled = false;
556â†’    setMetadataLoading(true);
557â†’
558â†’    resolveIpnsRecord(item.ipnsName)
559â†’      .then((result) => {
560â†’        if (!cancelled) {
561â†’          setMetadataCid(result?.cid ?? null);
562â†’        }
563â†’      })
564â†’      .catch(() => {
565â†’        if (!cancelled) {
566â†’          setMetadataCid(null);
567â†’        }
568â†’      })
569â†’      .finally(() => {
570â†’        if (!cancelled) {
571â†’          setMetadataLoading(false);
572â†’        }
573â†’      });
574â†’
575â†’    return () => {
576â†’      cancelled = true;
577â†’    };
578â†’  }, [open, item]);
579â†’
580â†’  // Resolve per-file metadata and CID in a single IPNS call (files only)
581â†’  useEffect(() => {
582â†’    if (!open || !item || item.type !== 'file' || !folderKey) {
583â†’      setFileMeta(null);
584â†’      setFileMetaLoading(false);
585â†’      // Only reset shared metadataCid when dialog is closed, not when viewing a folder
586â†’      if (!open || !item) {
587â†’        setMetadataCid(null);
588â†’        setMetadataLoading(false);
589â†’      }
590â†’      return;
591â†’    }
592â†’
593â†’    const fileItem = item as FilePointer;
594â†’    if (!fileItem.fileMetaIpnsName) {
595â†’      setFileMeta(null);
596â†’      setFileMetaLoading(false);
597â†’      setMetadataCid(null);
598â†’      setMetadataLoading(false);
599â†’      return;
600â†’    }
601â†’
602â†’    let cancelled = false;
603â†’    setFileMetaLoading(true);
604â†’    setMetadataLoading(true);
605â†’
606â†’    resolveFileMetadata(fileItem.fileMetaIpnsName, folderKey)
607â†’      .then(({ metadata, metadataCid: cid }) => {
608â†’        if (!cancelled) {
609â†’          setFileMeta(metadata);
610â†’          setMetadataCid(cid);
611â†’        }
612â†’      })
613â†’      .catch(() => {
614â†’        if (!cancelled) {
615â†’          setFileMeta(null);
616â†’          setMetadataCid(null);
617â†’        }
618â†’      })
619â†’      .finally(() => {
620â†’        if (!cancelled) {
621â†’          setFileMetaLoading(false);
622â†’          setMetadataLoading(false);
623â†’        }
624â†’      });
625â†’
626â†’    return () => {
627â†’      cancelled = true;
628â†’    };
629â†’  }, [open, item, folderKey, metadataRefresh]);
630â†’
631â†’  // Callback to refresh metadata after version restore/delete
632â†’  const handleVersionAction = useCallback(() => {
633â†’    setMetadataRefresh((prev) => prev + 1);
634â†’  }, []);
635â†’
636â†’  if (!item) return null;
637â†’
638â†’  const title = item.type === 'folder' ? 'Folder Details' : 'File Details';
639â†’
640â†’  return (
641â†’    <Modal open={open} onClose={onClose} title={title}>
642â†’      {item.type === 'file' ? (
643â†’        <FileDetails
644â†’          item={item as FilePointer}
645â†’          metadataCid={metadataCid}
646â†’          metadataLoading={metadataLoading}
647â†’          fileMeta={fileMeta}
648â†’          fileMetaLoading={fileMetaLoading}
649â†’          folderKey={folderKey}
650â†’          parentFolderId={parentFolderId}
651â†’          onVersionAction={handleVersionAction}
652â†’        />
653â†’      ) : (
654â†’        <FolderDetails
655â†’          item={item}
656â†’          metadataCid={metadataCid}
657â†’          metadataLoading={metadataLoading}
658â†’          sequenceNumber={folderNode?.sequenceNumber ?? null}
659â†’          childCount={folderNode ? folderNode.children.length : null}
660â†’        />
661â†’      )}
662â†’    </Modal>
663â†’  );
664â†’}
