  1â†’import { ReactNode, useEffect, useRef, useCallback } from 'react';
  2â†’import { Portal } from './Portal';
  3â†’import { MatrixBackground } from '../MatrixBackground';
  4â†’import { incrementModalCount, decrementModalCount } from '../../hooks/useModalOpen';
  5â†’import '../../styles/modal.css';
  6â†’
  7â†’type ModalProps = {
  8â†’  open: boolean;
  9â†’  onClose?: () => void;
 10â†’  children: ReactNode;
 11â†’  title?: string;
 12â†’  /** Optional class applied to the backdrop for variant styling */
 13â†’  className?: string;
 14â†’};
 15â†’
 16â†’/**
 17â†’ * Reusable modal dialog component.
 18â†’ * Features:
 19â†’ * - Renders in portal outside component tree
 20â†’ * - Backdrop with optional click-to-close
 21â†’ * - Close button (X) when onClose provided
 22â†’ * - Escape key closes when onClose provided
 23â†’ * - Focus trap: prevents tab from leaving modal
 24â†’ * - Accessible: aria-modal, role="dialog"
 25â†’ */
 26â†’export function Modal({ open, onClose, children, title, className }: ModalProps) {
 27â†’  const modalRef = useRef<HTMLDivElement>(null);
 28â†’  const previousActiveElement = useRef<Element | null>(null);
 29â†’
 30â†’  // Handle escape key
 31â†’  const handleKeyDown = useCallback(
 32â†’    (event: KeyboardEvent) => {
 33â†’      if (event.key === 'Escape' && onClose) {
 34â†’        onClose();
 35â†’      }
 36â†’    },
 37â†’    [onClose]
 38â†’  );
 39â†’
 40â†’  // Focus trap: keep focus within modal
 41â†’  const handleFocusTrap = useCallback((event: KeyboardEvent) => {
 42â†’    if (event.key !== 'Tab' || !modalRef.current) return;
 43â†’
 44â†’    const focusableElements = modalRef.current.querySelectorAll<HTMLElement>(
 45â†’      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
 46â†’    );
 47â†’
 48â†’    if (focusableElements.length === 0) return;
 49â†’
 50â†’    const firstElement = focusableElements[0];
 51â†’    const lastElement = focusableElements[focusableElements.length - 1];
 52â†’
 53â†’    if (event.shiftKey) {
 54â†’      // Shift+Tab: if on first element, move to last
 55â†’      if (document.activeElement === firstElement) {
 56â†’        event.preventDefault();
 57â†’        lastElement.focus();
 58â†’      }
 59â†’    } else {
 60â†’      // Tab: if on last element, move to first
 61â†’      if (document.activeElement === lastElement) {
 62â†’        event.preventDefault();
 63â†’        firstElement.focus();
 64â†’      }
 65â†’    }
 66â†’  }, []);
 67â†’
 68â†’  // Track modal open state for app-level matrix pause
 69â†’  useEffect(() => {
 70â†’    if (!open) return;
 71â†’    incrementModalCount();
 72â†’    return () => decrementModalCount();
 73â†’  }, [open]);
 74â†’
 75â†’  useEffect(() => {
 76â†’    if (!open) return;
 77â†’
 78â†’    // Store currently focused element
 79â†’    previousActiveElement.current = document.activeElement;
 80â†’
 81â†’    // Focus first focusable element in modal
 82â†’    const focusableElements = modalRef.current?.querySelectorAll<HTMLElement>(
 83â†’      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
 84â†’    );
 85â†’    if (focusableElements && focusableElements.length > 0) {
 86â†’      focusableElements[0].focus();
 87â†’    }
 88â†’
 89â†’    // Add event listeners
 90â†’    document.addEventListener('keydown', handleKeyDown);
 91â†’    document.addEventListener('keydown', handleFocusTrap);
 92â†’
 93â†’    // Cleanup
 94â†’    return () => {
 95â†’      document.removeEventListener('keydown', handleKeyDown);
 96â†’      document.removeEventListener('keydown', handleFocusTrap);
 97â†’      // Restore focus on close
 98â†’      if (previousActiveElement.current instanceof HTMLElement) {
 99â†’        previousActiveElement.current.focus();
100â†’      }
101â†’    };
102â†’  }, [open, handleKeyDown, handleFocusTrap]);
103â†’
104â†’  if (!open) {
105â†’    return null;
106â†’  }
107â†’
108â†’  const handleBackdropClick = (event: React.MouseEvent) => {
109â†’    // Only close if clicking the backdrop itself, not the modal content
110â†’    if (event.target === event.currentTarget && onClose) {
111â†’      onClose();
112â†’    }
113â†’  };
114â†’
115â†’  return (
116â†’    <Portal>
117â†’      <div
118â†’        className={`modal-backdrop${className ? ` ${className}` : ''}`}
119â†’        onClick={handleBackdropClick}
120â†’      >
121â†’        <MatrixBackground opacity={0.35} className="matrix-canvas--modal" frameInterval={50} />
122â†’        <div
123â†’          ref={modalRef}
124â†’          className="modal-container"
125â†’          role="dialog"
126â†’          aria-modal="true"
127â†’          aria-labelledby={title ? 'modal-title' : undefined}
128â†’        >
129â†’          {(title || onClose) && (
130â†’            <div className="modal-header">
131â†’              {title && (
132â†’                <h2 id="modal-title" className="modal-title">
133â†’                  {title}
134â†’                </h2>
135â†’              )}
136â†’              {onClose && (
137â†’                <button
138â†’                  type="button"
139â†’                  className="modal-close"
140â†’                  onClick={onClose}
141â†’                  aria-label="Close modal"
142â†’                >
143â†’                  &times;
144â†’                </button>
145â†’              )}
146â†’            </div>
147â†’          )}
148â†’          <div className="modal-body">{children}</div>
149â†’        </div>
150â†’      </div>
151â†’    </Portal>
152â†’  );
153â†’}
