  9â†’type SettingsTabId = 'linked-methods' | 'security';
 10â†’
 11â†’const TAB_IDS: SettingsTabId[] = ['linked-methods', 'security'];
 12â†’
 13â†’function handleTabKeyDown(
 14â†’  e: KeyboardEvent,
 15â†’  activeTab: SettingsTabId,
 16â†’  setActiveTab: (id: SettingsTabId) => void
 17â†’) {
 18â†’  const idx = TAB_IDS.indexOf(activeTab);
 19â†’  let newIdx: number | null = null;
 20â†’  if (e.key === 'ArrowRight') newIdx = (idx + 1) % TAB_IDS.length;
 21â†’  else if (e.key === 'ArrowLeft') newIdx = (idx - 1 + TAB_IDS.length) % TAB_IDS.length;
 22â†’  else if (e.key === 'Home') newIdx = 0;
 23â†’  else if (e.key === 'End') newIdx = TAB_IDS.length - 1;
 24â†’  if (newIdx !== null) {
 25â†’    e.preventDefault();
 26â†’    setActiveTab(TAB_IDS[newIdx]);
 27â†’    document.getElementById(`tab-${TAB_IDS[newIdx]}`)?.focus();
 28â†’  }
 29â†’}
