 1â†’import { create } from 'zustand';
 2â†’
 3â†’type TeeKeys = {
 4â†’  currentEpoch: number;
 5â†’  currentPublicKey: string;
 6â†’  previousEpoch: number | null;
 7â†’  previousPublicKey: string | null;
 8â†’};
 9â†’
10â†’/**
11â†’ * User's secp256k1 vault keypair from Core Kit TSS export.
12â†’ * Memory-only, never persisted.
13â†’ */
14â†’type VaultKeypair = {
15â†’  publicKey: Uint8Array;
16â†’  privateKey: Uint8Array;
17â†’};
18â†’
19â†’type AuthState = {
20â†’  accessToken: string | null;
21â†’  isAuthenticated: boolean;
22â†’  lastAuthMethod: string | null;
23â†’  userEmail: string | null;
24â†’  teeKeys: TeeKeys | null;
25â†’
26â†’  /** User's vault keypair (memory-only, from Core Kit TSS export) */
27â†’  vaultKeypair: VaultKeypair | null;
28â†’
29â†’  setAccessToken: (token: string) => void;
30â†’  setLastAuthMethod: (method: string) => void;
31â†’  setUserEmail: (email: string) => void;
32â†’  setTeeKeys: (keys: TeeKeys) => void;
33â†’  setVaultKeypair: (keypair: VaultKeypair) => void;
34â†’  logout: () => void;
35â†’};
36â†’
37â†’export const useAuthStore = create<AuthState>((set, get) => ({
38â†’  // State - stored in memory only, NOT localStorage (XSS prevention)
39â†’  accessToken: null,
40â†’  isAuthenticated: false,
41â†’  lastAuthMethod: null,
42â†’  userEmail: null,
43â†’  teeKeys: null,
44â†’
45â†’  /** User's vault keypair (memory-only) */
46â†’  vaultKeypair: null,
47â†’
48â†’  // Actions
49â†’  setAccessToken: (token) => set({ accessToken: token, isAuthenticated: true }),
50â†’  setLastAuthMethod: (method) => set({ lastAuthMethod: method }),
51â†’  setUserEmail: (email) => set({ userEmail: email }),
52â†’  setTeeKeys: (keys) => set({ teeKeys: keys }),
53â†’
54â†’  /** Store vault keypair from Core Kit TSS export */
55â†’  setVaultKeypair: (keypair) => set({ vaultKeypair: keypair }),
56â†’
57â†’  logout: () => {
58â†’    // [SECURITY: MEDIUM-02] Zero-fill sensitive key material before clearing
59â†’    const state = get();
60â†’    if (state.vaultKeypair) {
61â†’      if (state.vaultKeypair.privateKey) {
62â†’        state.vaultKeypair.privateKey.fill(0);
63â†’      }
64â†’      if (state.vaultKeypair.publicKey) {
65â†’        state.vaultKeypair.publicKey.fill(0);
66â†’      }
67â†’    }
68â†’
69â†’    set({
70â†’      accessToken: null,
71â†’      isAuthenticated: false,
72â†’      userEmail: null,
73â†’      teeKeys: null,
74â†’      vaultKeypair: null,
75â†’    });
76â†’  },
77â†’}));
