 10â†’  deriveFileIpnsKeypair,
 11â†’  generateFileIpnsKeypair,
 12â†’  encryptFileMetadata,
 13â†’  decryptFileMetadata,
 14â†’  createIpnsRecord,
 15â†’  marshalIpnsRecord,
 16â†’  wrapKey,
 17â†’  unwrapKey,
 18â†’  bytesToHex,
 19â†’  hexToBytes,
 20â†’  type FileMetadata,
 21â†’  type FilePointer,
