 26â†’import { resolveIpnsRecord } from './ipns.service';
 27â†’import { useAuthStore } from '../stores/auth.store';
 28â†’
 29â†’/** IPNS record lifetime: 24 hours in milliseconds */
 30â†’const IPNS_LIFETIME_MS = 24 * 60 * 60 * 1000;
 31â†’
 32â†’/** Maximum number of past versions retained per file (VER-04) */
 33â†’const MAX_VERSIONS_PER_FILE = 10;
 34â†’
 35â†’/** Cooldown period for automatic version creation (15 minutes in ms) */
