 37â†’
 38â†’/** Safe base64 encoding that avoids call stack overflow from spread operator */
 39â†’function uint8ToBase64(bytes: Uint8Array): string {
 40â†’  let binary = '';
 41â†’  for (let i = 0; i < bytes.length; i++) {
 42â†’    binary += String.fromCharCode(bytes[i]);
 43â†’  }
 44â†’  return btoa(binary);
 45â†’}
