 47â†’/** Record payload ready for batch publish */
 48â†’export type FileIpnsRecordPayload = {
 49â†’  ipnsName: string;
 50â†’  recordBase64: string;
 51â†’  metadataCid: string;
 52â†’  encryptedIpnsPrivateKey?: string;
 53â†’  keyEpoch?: number;
