 81â†’  }
 82â†’  // Fallback: HKDF derivation for legacy FilePointers without encrypted key
 83â†’  const derived = await deriveFileIpnsKeypair(userPrivateKey, filePointer.id);
 84â†’
 85â†’  // Wrap derived key for lazy migration if public key is available
 86â†’  let migratedIpnsPrivateKeyEncrypted: string | undefined;
