100â†’ * Returns the record payload ready for batch publish, plus the ECIES-wrapped
101â†’ * IPNS private key for storage in the FilePointer.
102â†’ *
103â†’ * @param params.fileId - Unique file identifier (UUID)
104â†’ * @param params.cid - IPFS CID of the encrypted file content
105â†’ * @param params.fileKeyEncrypted - Hex-encoded ECIES-wrapped file key
106â†’ * @param params.fileIv - Hex-encoded IV used for file encryption
107â†’ * @param params.size - Original file size in bytes
108â†’ * @param params.mimeType - MIME type of the original file
109â†’ * @param params.folderKey - Parent folder's decrypted AES-256 key
110â†’ * @param params.userPublicKey - User's secp256k1 public key (for ECIES wrapping)
111â†’ * @param params.encryptionMode - Encryption mode ('GCM' or 'CTR', defaults to 'GCM')
112â†’ * @returns File IPNS name, record payload for batch publish, and ECIES-wrapped IPNS private key
113â†’ */
114â†’export async function createFileMetadata(params: {
115â†’  fileId: string;
116â†’  cid: string;
117â†’  fileKeyEncrypted: string;
118â†’  fileIv: string;
119â†’  size: number;
120â†’  mimeType: string;
121â†’  folderKey: Uint8Array;
122â†’  userPublicKey: Uint8Array;
123â†’  encryptionMode?: 'GCM' | 'CTR';
124â†’}): Promise<{
125â†’  fileMetaIpnsName: string;
126â†’  ipnsRecord: FileIpnsRecordPayload;
127â†’  ipnsPrivateKeyEncrypted: string;
128â†’}> {
129â†’  // 1. Generate random Ed25519 IPNS keypair for this file
130â†’  const ipnsKeypair = await generateFileIpnsKeypair();
131â†’
132â†’  // 2. ECIES-wrap the IPNS private key with user's public key for storage in FilePointer
133â†’  const wrappedIpnsKey = await wrapKey(ipnsKeypair.privateKey, params.userPublicKey);
134â†’  const ipnsPrivateKeyEncrypted = bytesToHex(wrappedIpnsKey);
135â†’
136â†’  // 3. Create FileMetadata object
137â†’  const now = Date.now();
138â†’  const metadata: FileMetadata = {
139â†’    version: 'v1',
140â†’    cid: params.cid,
141â†’    fileKeyEncrypted: params.fileKeyEncrypted,
142â†’    fileIv: params.fileIv,
143â†’    size: params.size,
144â†’    mimeType: params.mimeType,
145â†’    encryptionMode: params.encryptionMode ?? 'GCM',
146â†’    createdAt: now,
147â†’    modifiedAt: now,
148â†’  };
149â†’
150â†’  // 4. Encrypt with parent folderKey
151â†’  const encrypted: EncryptedFileMetadata = await encryptFileMetadata(metadata, params.folderKey);
152â†’
153â†’  // 5. Upload encrypted metadata to IPFS
154â†’  const blob = new Blob([JSON.stringify(encrypted)], { type: 'application/json' });
155â†’  const { cid: metadataCid } = await addToIpfs(blob);
156â†’
157â†’  // 6. Create IPNS record (sequence number 1 for new records)
158â†’  const record = await createIpnsRecord(
159â†’    ipnsKeypair.privateKey,
160â†’    `/ipfs/${metadataCid}`,
161â†’    1n,
162â†’    IPNS_LIFETIME_MS
163â†’  );
164â†’
165â†’  // 7. Marshal and base64 encode the record
166â†’  const recordBytes = marshalIpnsRecord(record);
167â†’  const recordBase64 = uint8ToBase64(recordBytes);
168â†’
169â†’  // 8. TEE enrollment: encrypt IPNS private key with TEE public key
170â†’  let teeEncryptedIpnsPrivateKey: string | undefined;
171â†’  let keyEpoch: number | undefined;
172â†’
173â†’  const teeKeys = useAuthStore.getState().teeKeys;
174â†’  if (teeKeys?.currentPublicKey) {
175â†’    const teePublicKey = hexToBytes(teeKeys.currentPublicKey);
176â†’    const encryptedKey = await wrapKey(ipnsKeypair.privateKey, teePublicKey);
177â†’    teeEncryptedIpnsPrivateKey = bytesToHex(encryptedKey);
178â†’    keyEpoch = teeKeys.currentEpoch;
179â†’  }
180â†’
181â†’  // Zero the private key now that wrapping, signing, and TEE enrollment are done
182â†’  ipnsKeypair.privateKey.fill(0);
183â†’
184â†’  return {
185â†’    fileMetaIpnsName: ipnsKeypair.ipnsName,
186â†’    ipnsRecord: {
187â†’      ipnsName: ipnsKeypair.ipnsName,
188â†’      recordBase64,
189â†’      metadataCid,
190â†’      encryptedIpnsPrivateKey: teeEncryptedIpnsPrivateKey,
191â†’      keyEpoch,
192â†’    },
193â†’    ipnsPrivateKeyEncrypted,
194â†’  };
195â†’}
