197â†’/**
198â†’ * Resolve a file's per-IPNS metadata record.
199â†’ *
200â†’ * Resolves the file's IPNS name to get the current metadata CID,
201â†’ * fetches the encrypted metadata from IPFS, and decrypts with the
202â†’ * parent folder's key.
203â†’ *
204â†’ * @param fileMetaIpnsName - IPNS name of the file's metadata record
205â†’ * @param folderKey - Parent folder's decrypted AES-256 key
206â†’ * @returns Decrypted file metadata and the resolved metadata CID
207â†’ * @throws Error if IPNS resolution fails or metadata cannot be decrypted
208â†’ */
209â†’export async function resolveFileMetadata(
210â†’  fileMetaIpnsName: string,
211â†’  folderKey: Uint8Array
212â†’): Promise<{ metadata: FileMetadata; metadataCid: string }> {
213â†’  // 1. Resolve IPNS to get current metadata CID
214â†’  const resolved = await resolveIpnsRecord(fileMetaIpnsName);
215â†’
216â†’  if (!resolved) {
