257â†’ * and creates a new IPNS record with an incremented sequence number.
258â†’ * Does NOT re-enroll with TEE (already enrolled from creation).
259â†’ *
260â†’ * @param params.fileIpnsPrivateKey - Decrypted Ed25519 IPNS private key for this file
261â†’ * @param params.fileMetaIpnsName - IPNS name for this file's metadata record
262â†’ * @param params.folderKey - Parent folder's decrypted AES-256 key
263â†’ * @param params.currentMetadata - Current file metadata to update
264â†’ * @param params.updates - Partial updates to apply (cid, fileKeyEncrypted, fileIv, size)
265â†’ * @param params.createVersion - Whether to push current metadata into versions array before updating
266â†’ * @returns Updated IPNS record payload for publish, plus CIDs of pruned versions to unpin
267â†’ */
268â†’export async function updateFileMetadata(params: {
269â†’  fileIpnsPrivateKey: Uint8Array;
270â†’  fileMetaIpnsName: string;
271â†’  folderKey: Uint8Array;
272â†’  currentMetadata: FileMetadata;
273â†’  updates: Partial<Pick<FileMetadata, 'cid' | 'fileKeyEncrypted' | 'fileIv' | 'size'>>;
274â†’  createVersion: boolean;
275â†’}): Promise<{
276â†’  ipnsRecord: FileIpnsRecordPayload;
277â†’  prunedCids: string[];
278â†’}> {
279â†’  // 1. Build version history
280â†’  let versions: VersionEntry[] | undefined;
281â†’  let prunedCids: string[] = [];
282â†’
283â†’  if (params.createVersion) {
284â†’    // Push current state into versions array (newest first)
285â†’    const versionEntry: VersionEntry = {
286â†’      cid: params.currentMetadata.cid,
287â†’      fileKeyEncrypted: params.currentMetadata.fileKeyEncrypted,
288â†’      fileIv: params.currentMetadata.fileIv,
289â†’      size: params.currentMetadata.size,
290â†’      timestamp: Date.now(),
291â†’      encryptionMode: params.currentMetadata.encryptionMode ?? 'GCM',
292â†’    };
293â†’    const allVersions = [versionEntry, ...(params.currentMetadata.versions ?? [])];
294â†’
295â†’    // Prune excess versions beyond limit
296â†’    versions = allVersions.slice(0, MAX_VERSIONS_PER_FILE);
297â†’    prunedCids = allVersions.slice(MAX_VERSIONS_PER_FILE).map((v) => v.cid);
298â†’  } else {
299â†’    // Preserve existing versions as-is
300â†’    versions = params.currentMetadata.versions;
301â†’  }
302â†’
303â†’  // 2. Merge updates into current metadata
304â†’  const updatedMetadata: FileMetadata = {
305â†’    ...params.currentMetadata,
306â†’    ...params.updates,
307â†’    // Only include versions field when there are actual versions (backward compat)
308â†’    ...(versions && versions.length > 0 ? { versions } : { versions: undefined }),
309â†’    modifiedAt: Date.now(),
310â†’  };
311â†’
312â†’  // 3. Resolve current IPNS to get sequence number
313â†’  const resolved = await resolveIpnsRecord(params.fileMetaIpnsName);
314â†’  if (!resolved) {
315â†’    throw new Error(
316â†’      `Cannot update file metadata: existing IPNS record not found for ${params.fileMetaIpnsName}`
317â†’    );
318â†’  }
319â†’  const currentSeq = resolved.sequenceNumber;
320â†’  const newSeq = currentSeq + 1n;
321â†’
322â†’  // 4. Encrypt updated metadata with folderKey
323â†’  const encrypted = await encryptFileMetadata(updatedMetadata, params.folderKey);
324â†’
325â†’  // 5. Upload to IPFS
326â†’  const blob = new Blob([JSON.stringify(encrypted)], { type: 'application/json' });
327â†’  const { cid: metadataCid } = await addToIpfs(blob);
328â†’
329â†’  // 6. Create new IPNS record with incremented sequence number
330â†’  const record = await createIpnsRecord(
331â†’    params.fileIpnsPrivateKey,
332â†’    `/ipfs/${metadataCid}`,
333â†’    newSeq,
334â†’    IPNS_LIFETIME_MS
335â†’  );
336â†’
337â†’  const recordBytes = marshalIpnsRecord(record);
338â†’  const recordBase64 = uint8ToBase64(recordBytes);
339â†’
340â†’  return {
341â†’    ipnsRecord: {
342â†’      ipnsName: params.fileMetaIpnsName,
343â†’      recordBase64,
344â†’      metadataCid,
345â†’    },
346â†’    prunedCids,
347â†’  };
348â†’}
