362â†’export async function restoreVersion(params: {
363â†’  fileIpnsPrivateKey: Uint8Array;
364â†’  fileMetaIpnsName: string;
365â†’  folderKey: Uint8Array;
366â†’  currentMetadata: FileMetadata;
367â†’  versionIndex: number;
368â†’}): Promise<{ ipnsRecord: FileIpnsRecordPayload; prunedCids: string[] }> {
369â†’  const versions = params.currentMetadata.versions;
370â†’  if (!versions || params.versionIndex < 0 || params.versionIndex >= versions.length) {
371â†’    throw new Error('Invalid version index');
372â†’  }
373â†’
374â†’  const versionToRestore = versions[params.versionIndex];
375â†’
376â†’  // Build a version entry from CURRENT metadata (it becomes a past version)
377â†’  const currentAsVersion: VersionEntry = {
378â†’    cid: params.currentMetadata.cid,
379â†’    fileKeyEncrypted: params.currentMetadata.fileKeyEncrypted,
380â†’    fileIv: params.currentMetadata.fileIv,
381â†’    size: params.currentMetadata.size,
382â†’    timestamp: Date.now(),
383â†’    encryptionMode: params.currentMetadata.encryptionMode ?? 'GCM',
384â†’  };
385â†’
386â†’  // Remove restored version from array, prepend current as new version entry
387â†’  const remainingVersions = versions.filter((_, i) => i !== params.versionIndex);
388â†’  const newVersions = [currentAsVersion, ...remainingVersions];
389â†’
390â†’  // Prune if exceeds max
391â†’  const prunedVersions = newVersions.slice(0, MAX_VERSIONS_PER_FILE);
392â†’  const prunedCids = newVersions.slice(MAX_VERSIONS_PER_FILE).map((v) => v.cid);
393â†’
394â†’  // Build updated metadata with restored version's data as current
395â†’  const updatedMetadata: FileMetadata = {
396â†’    ...params.currentMetadata,
397â†’    cid: versionToRestore.cid,
398â†’    fileKeyEncrypted: versionToRestore.fileKeyEncrypted,
399â†’    fileIv: versionToRestore.fileIv,
400â†’    size: versionToRestore.size,
401â†’    encryptionMode: versionToRestore.encryptionMode,
402â†’    versions: prunedVersions.length > 0 ? prunedVersions : undefined,
403â†’    modifiedAt: Date.now(),
404â†’  };
405â†’
406â†’  // Resolve current IPNS to get sequence number
407â†’  const resolved = await resolveIpnsRecord(params.fileMetaIpnsName);
408â†’  if (!resolved) {
409â†’    throw new Error(
410â†’      `Cannot restore version: existing IPNS record not found for ${params.fileMetaIpnsName}`
411â†’    );
412â†’  }
413â†’  const newSeq = resolved.sequenceNumber + 1n;
414â†’
415â†’  // Encrypt updated metadata with folderKey
416â†’  const encrypted = await encryptFileMetadata(updatedMetadata, params.folderKey);
417â†’
418â†’  // Upload to IPFS
419â†’  const blob = new Blob([JSON.stringify(encrypted)], { type: 'application/json' });
420â†’  const { cid: metadataCid } = await addToIpfs(blob);
421â†’
422â†’  // Create new IPNS record with incremented sequence number
423â†’  const record = await createIpnsRecord(
424â†’    params.fileIpnsPrivateKey,
425â†’    `/ipfs/${metadataCid}`,
426â†’    newSeq,
427â†’    IPNS_LIFETIME_MS
428â†’  );
429â†’
430â†’  const recordBytes = marshalIpnsRecord(record);
431â†’  const recordBase64 = uint8ToBase64(recordBytes);
432â†’
433â†’  return {
434â†’    ipnsRecord: {
435â†’      ipnsName: params.fileMetaIpnsName,
436â†’      recordBase64,
437â†’      metadataCid,
438â†’    },
439â†’    prunedCids,
440â†’  };
441â†’}
442â†’
443â†’/**
444â†’ * Delete a specific past version from a file's version history.
445â†’ * Updates the file's IPNS metadata without the deleted version.
446â†’ *
447â†’ * @param params.fileIpnsPrivateKey - Decrypted Ed25519 IPNS private key for this file
448â†’ * @param params.fileMetaIpnsName - IPNS name for this file's metadata record
449â†’ * @param params.folderKey - Parent folder's decrypted AES-256 key
450â†’ * @param params.currentMetadata - Current file metadata
451â†’ * @param params.versionIndex - Index of version to delete
452â†’ * @returns Updated IPNS record and CID to unpin
453â†’ */
454â†’export async function deleteVersion(params: {
455â†’  fileIpnsPrivateKey: Uint8Array;
456â†’  fileMetaIpnsName: string;
457â†’  folderKey: Uint8Array;
458â†’  currentMetadata: FileMetadata;
459â†’  versionIndex: number;
460â†’}): Promise<{ ipnsRecord: FileIpnsRecordPayload; deletedCid: string }> {
461â†’  const versions = params.currentMetadata.versions;
462â†’  if (!versions || params.versionIndex < 0 || params.versionIndex >= versions.length) {
463â†’    throw new Error('Invalid version index');
464â†’  }
465â†’
466â†’  const deletedCid = versions[params.versionIndex].cid;
467â†’  const newVersions = versions.filter((_, i) => i !== params.versionIndex);
468â†’
469â†’  // Build updated metadata with filtered versions
470â†’  const updatedMetadata: FileMetadata = {
471â†’    ...params.currentMetadata,
472â†’    versions: newVersions.length > 0 ? newVersions : undefined,
473â†’  };
474â†’
475â†’  // Resolve current IPNS to get sequence number
476â†’  const resolved = await resolveIpnsRecord(params.fileMetaIpnsName);
477â†’  if (!resolved) {
478â†’    throw new Error(
479â†’      `Cannot delete version: existing IPNS record not found for ${params.fileMetaIpnsName}`
480â†’    );
481â†’  }
482â†’  const newSeq = resolved.sequenceNumber + 1n;
483â†’
484â†’  // Encrypt updated metadata with folderKey
485â†’  const encrypted = await encryptFileMetadata(updatedMetadata, params.folderKey);
486â†’
487â†’  // Upload to IPFS
488â†’  const blob = new Blob([JSON.stringify(encrypted)], { type: 'application/json' });
489â†’  const { cid: metadataCid } = await addToIpfs(blob);
490â†’
491â†’  // Create new IPNS record with incremented sequence number
492â†’  const record = await createIpnsRecord(
493â†’    params.fileIpnsPrivateKey,
494â†’    `/ipfs/${metadataCid}`,
495â†’    newSeq,
496â†’    IPNS_LIFETIME_MS
497â†’  );
498â†’
499â†’  const recordBytes = marshalIpnsRecord(record);
500â†’  const recordBase64 = uint8ToBase64(recordBytes);
501â†’
502â†’  return {
503â†’    ipnsRecord: {
504â†’      ipnsName: params.fileMetaIpnsName,
505â†’      recordBase64,
506â†’      metadataCid,
507â†’    },
508â†’    deletedCid,
509â†’  };
510â†’}
