  1→import { test, expect, Browser } from '@playwright/test';
  2→import {
  3→  createTestAccount,
  4→  closeTestAccounts,
  5→  navigateToShared,
  6→  navigateToFiles,
  7→  type TestAccount,
  8→} from '../utils/multi-account';
  9→import { createTestTextFile, cleanupTestFiles } from '../utils/test-files';
 10→import { FileListPage } from '../page-objects/file-browser/file-list.page';
 11→import { UploadZonePage } from '../page-objects/file-browser/upload-zone.page';
 12→import { ContextMenuPage } from '../page-objects/file-browser/context-menu.page';
 13→import { CreateFolderDialogPage } from '../page-objects/dialogs/create-folder-dialog.page';
 14→import { ShareDialogPage } from '../page-objects/dialogs/share-dialog.page';
 15→import { SharedFileBrowserPage } from '../page-objects/file-browser/shared-file-browser.page';
 16→
 17→/**
 18→ * Sharing Workflow E2E Test Suite
 19→ *
 20→ * Tests user-to-user sharing with multiple authenticated accounts.
 21→ * Each test account gets its own browser context (isolated cookies/storage).
 22→ *
 23→ * Accounts:
 24→ * - Alice: primary sharer (creates content and shares it)
 25→ * - Bob: primary recipient (receives and browses shared content)
 26→ * - Charlie: secondary recipient (for multi-recipient tests)
 27→ *
 28→ * Test flow:
 29→ * 1. Setup: Create 3 accounts, Alice creates test content
 30→ * 2. Basic sharing: Alice shares file with Bob, Bob receives it
 31→ * 3. Folder sharing: Alice shares folder tree, Bob navigates it
 32→ * 4. Multi-recipient: Alice adds Charlie to existing share
 33→ * 5. Revocation: Alice revokes Bob's access
 34→ * 6. Post-share mutations: Alice adds files to shared folder
 35→ * 7. Error cases: Invalid key, self-share, user not found
 36→ * 8. Hide share: Bob hides a shared item
 37→ */
 38→test.describe.serial('Sharing Workflow', () => {
 39→  let browser: Browser;
 40→  let alice: TestAccount;
 41→  let bob: TestAccount;
 42→  let charlie: TestAccount;
 43→
 44→  // Page objects per account
 45→  let aliceFileList: FileListPage;
 46→  let aliceUploadZone: UploadZonePage;
 47→  let aliceContextMenu: ContextMenuPage;
 48→  let aliceCreateFolderDialog: CreateFolderDialogPage;
 49→  let aliceShareDialog: ShareDialogPage;
 50→
 51→  let bobSharedBrowser: SharedFileBrowserPage;
 52→  let bobContextMenu: ContextMenuPage;
 53→
 54→  let charlieSharedBrowser: SharedFileBrowserPage;
 55→
 56→  // Test data - unique per run
 57→  const runId = Date.now().toString();
 58→
 59→  // Shared file
 60→  const sharedFileName = `shared-file-${runId}.txt`;
 61→  const sharedFileContent = 'This file is shared with Bob';
 62→
 63→  // Shared folder structure
 64→  const sharedFolderName = `shared-folder-${runId}`;
 65→  const subFolderName = `subfolder-${runId}`;
 66→  const folderFile1Name = `folder-file1-${runId}.txt`;
 67→  const folderFile2Name = `subfolder-file-${runId}.txt`;
 68→
 69→  // Post-share file (added after share is created)
 70→  const postShareFileName = `added-after-share-${runId}.txt`;
 71→  const postShareFileContent = 'This file was added after the share was created';
 72→
 73→  // Helper to truncate a public key for matching in the UI
 74→  function truncateKey(key: string): string {
 75→    const hex = key.startsWith('0x') ? key.slice(2) : key;
 76→    return `0x${hex.slice(0, 4)}...${hex.slice(-4)}`;
 77→  }
 78→
 79→  // ============================================
 80→  // Alice helpers
 81→  // ============================================
 82→
 83→  async function aliceCreateFolder(name: string): Promise<void> {
 84→    const newFolderButton = alice.page.locator('.file-browser-new-folder-button');
 85→    await newFolderButton.click();
 86→    await aliceCreateFolderDialog.waitForOpen();
 87→    await aliceCreateFolderDialog.createFolder(name);
 88→    await aliceFileList.waitForItemToAppear(name, { timeout: 30000 });
 89→  }
 90→
 91→  async function aliceUploadFile(fileName: string, content: string): Promise<void> {
 92→    const testFile = createTestTextFile(fileName, content);
 93→    await aliceUploadZone.uploadFile(testFile.path);
 94→
 95→    // Wait for upload: check for either the file appearing or an error alert
 96→    await Promise.race([
 97→      aliceFileList.waitForItemToAppear(fileName, { timeout: 30000 }),
 98→      alice.page
 99→        .locator('[role="alert"]')
100→        .waitFor({ state: 'visible', timeout: 30000 })
101→        .then(async () => {
102→          const alertText = await alice.page.locator('[role="alert"]').textContent();
103→          throw new Error(`Upload failed with alert: ${alertText}`);
104→        }),
105→    ]);
106→  }
107→
108→  async function aliceNavigateIntoFolder(name: string): Promise<void> {
109→    await aliceFileList.doubleClickFolder(name);
110→    // Wait for breadcrumbs to show the folder name
111→    await alice.page.locator('.breadcrumb-item', { hasText: name.toLowerCase() }).waitFor({
112→      state: 'visible',
113→      timeout: 30000,
114→    });
115→  }
116→
117→  async function aliceNavigateBack(): Promise<void> {
118→    await alice.page.locator('[data-testid="parent-dir-row"]').dblclick();
119→    await alice.page.waitForTimeout(500);
120→  }
121→
122→  async function aliceOpenShareDialog(itemName: string): Promise<void> {
123→    await aliceFileList.rightClickItem(itemName);
124→    await aliceContextMenu.waitForOpen();
125→    await aliceContextMenu.clickShare();
126→    await aliceShareDialog.waitForOpen();
127→    await aliceShareDialog.waitForRecipientsLoaded();
128→  }
129→
130→  // ============================================
131→  // Setup and Teardown
132→  // ============================================
133→
134→  test.beforeAll(async ({ browser: testBrowser }) => {
135→    browser = testBrowser;
136→  });
137→
138→  test.afterAll(async () => {
139→    cleanupTestFiles();
140→    if (alice || bob || charlie) {
141→      await closeTestAccounts([alice, bob, charlie].filter(Boolean));
142→    }
143→  });
144→
145→  // ============================================
146→  // Phase 1: Account Setup
147→  // ============================================
148→
149→  test('1.1 Create test accounts (Alice, Bob, Charlie)', async () => {
150→    // Create accounts sequentially (each needs its own context + vault init)
151→    alice = await createTestAccount(browser, 'alice', runId);
152→    bob = await createTestAccount(browser, 'bob', runId);
153→    charlie = await createTestAccount(browser, 'charlie', runId);
154→
155→    // Initialize page objects for Alice
156→    aliceFileList = new FileListPage(alice.page);
157→    aliceUploadZone = new UploadZonePage(alice.page);
158→    aliceContextMenu = new ContextMenuPage(alice.page);
159→    aliceCreateFolderDialog = new CreateFolderDialogPage(alice.page);
160→    aliceShareDialog = new ShareDialogPage(alice.page);
161→
162→    // Initialize page objects for Bob
163→    bobSharedBrowser = new SharedFileBrowserPage(bob.page);
164→    bobContextMenu = new ContextMenuPage(bob.page);
165→
166→    // Initialize page objects for Charlie
167→    charlieSharedBrowser = new SharedFileBrowserPage(charlie.page);
168→
169→    // Verify all three accounts have different public keys
170→    expect(alice.publicKey).not.toBe(bob.publicKey);
171→    expect(alice.publicKey).not.toBe(charlie.publicKey);
172→    expect(bob.publicKey).not.toBe(charlie.publicKey);
173→  });
174→
175→  // ============================================
176→  // Phase 2: Alice Creates Content
177→  // ============================================
178→
179→  test('2.1 Alice creates a test file at root', async () => {
180→    await aliceUploadFile(sharedFileName, sharedFileContent);
181→    expect(await aliceFileList.isItemVisible(sharedFileName)).toBe(true);
182→  });
183→
184→  test('2.2 Alice creates a folder with nested content', async () => {
185→    // Create top-level folder
186→    await aliceCreateFolder(sharedFolderName);
187→    expect(await aliceFileList.isItemVisible(sharedFolderName)).toBe(true);
188→
189→    // Navigate into it and add a file
190→    await aliceNavigateIntoFolder(sharedFolderName);
191→    await aliceUploadFile(folderFile1Name, 'File inside shared folder');
192→
193→    // Create subfolder
194→    await aliceCreateFolder(subFolderName);
195→
196→    // Navigate into subfolder and add a file
197→    await aliceNavigateIntoFolder(subFolderName);
198→    await aliceUploadFile(folderFile2Name, 'File inside subfolder');
199→
200→    // Navigate back to root
201→    await aliceNavigateBack(); // back to shared folder
202→    await aliceNavigateBack(); // back to root
203→  });
204→
205→  // ============================================
206→  // Phase 3: Basic File Sharing
207→  // ============================================
208→
209→  test('3.1 Alice shares a file with Bob', async () => {
210→    await aliceOpenShareDialog(sharedFileName);
211→
212→    // Should start with no recipients
213→    expect(await aliceShareDialog.getRecipientCount()).toBe(0);
214→
215→    // Share with Bob
216→    await aliceShareDialog.shareWithKey(bob.publicKey);
217→    const successText = await aliceShareDialog.waitForSuccess({ timeout: 30000 });
218→    expect(successText).toContain(truncateKey(bob.publicKey));
219→
220→    // Verify recipient appears in the list
221→    expect(await aliceShareDialog.getRecipientCount()).toBe(1);
222→    const recipientKeys = await aliceShareDialog.getRecipientKeys();
223→    expect(recipientKeys[0]).toBe(truncateKey(bob.publicKey));
224→
225→    await aliceShareDialog.close();
226→  });
227→
228→  test('3.2 Bob sees the shared file in ~/shared', async () => {
229→    await navigateToShared(bob);
230→
231→    // Wait for the shared items to load
232→    await bobSharedBrowser.waitForLoaded({ timeout: 30000 });
233→
234→    // Bob should see Alice's shared file
235→    await bobSharedBrowser.waitForSharedItem(sharedFileName, { timeout: 15000 });
236→    expect(await bobSharedBrowser.getSharedItemCount()).toBeGreaterThanOrEqual(1);
237→
238→    // Verify [RO] badge
239→    const roBadge = bobSharedBrowser.getReadOnlyBadge(sharedFileName);
240→    expect(await roBadge.isVisible()).toBe(true);
241→
242→    // Verify "SHARED BY" shows Alice's truncated key
243→    const sharedBy = await bobSharedBrowser.getSharedBy(sharedFileName);
244→    expect(sharedBy).toContain(truncateKey(alice.publicKey));
245→  });
246→
247→  // ============================================
248→  // Phase 4: Folder Sharing with Descendants
249→  // ============================================
250→
251→  test('4.1 Alice shares a folder with Bob', async () => {
252→    // Make sure Alice is on files page
253→    await navigateToFiles(alice);
254→    await aliceFileList.waitForItemToAppear(sharedFolderName, { timeout: 15000 });
255→
256→    await aliceOpenShareDialog(sharedFolderName);
257→
258→    // Share with Bob - folder sharing re-wraps descendant keys
259→    await aliceShareDialog.shareWithKey(bob.publicKey);
260→
261→    // Wait for progress (folder sharing re-wraps keys for each child)
262→    // The progress may be very fast for small folders, so we just wait for success
263→    const successText = await aliceShareDialog.waitForSuccess({ timeout: 60000 });
264→    expect(successText).toContain(truncateKey(bob.publicKey));
265→
266→    await aliceShareDialog.close();
267→  });
268→
269→  test('4.2 Bob navigates the shared folder tree', async () => {
270→    // Navigate to Bob's shared view
271→    await navigateToShared(bob);
272→    await bobSharedBrowser.waitForLoaded({ timeout: 30000 });
273→
274→    // Find and open the shared folder
275→    await bobSharedBrowser.waitForSharedItem(sharedFolderName, { timeout: 15000 });
276→    await bobSharedBrowser.navigateIntoFolder(sharedFolderName);
277→
278→    // Should see folder contents: the file and subfolder
279→    const itemNames = await bobSharedBrowser.getFolderItemNames();
280→    expect(itemNames.some((n) => n.includes(folderFile1Name))).toBe(true);
281→    expect(itemNames.some((n) => n.includes(subFolderName))).toBe(true);
282→
283→    // Navigate into subfolder
284→    await bobSharedBrowser.doubleClickFolderItem(subFolderName);
285→    // Wait for subfolder content to load
286→    await bob.page.waitForTimeout(2000);
287→
288→    const subItems = await bobSharedBrowser.getFolderItemNames();
289→    expect(subItems.some((n) => n.includes(folderFile2Name))).toBe(true);
290→
291→    // Navigate back to shared root
292→    await bobSharedBrowser.navigateToRoot();
293→    await bobSharedBrowser.waitForLoaded({ timeout: 15000 });
294→  });
295→
296→  // ============================================
297→  // Phase 5: Multi-Recipient (Add Charlie)
298→  // ============================================
299→
300→  test('5.1 Alice adds Charlie as a second recipient', async () => {
301→    await navigateToFiles(alice);
302→    await aliceFileList.waitForItemToAppear(sharedFolderName, { timeout: 15000 });
303→
304→    await aliceOpenShareDialog(sharedFolderName);
305→
306→    // Bob should already be a recipient
307→    await aliceShareDialog.waitForRecipientsLoaded();
308→    expect(await aliceShareDialog.getRecipientCount()).toBe(1);
309→
310→    // Add Charlie
311→    await aliceShareDialog.shareWithKey(charlie.publicKey);
312→    const successText = await aliceShareDialog.waitForSuccess({ timeout: 60000 });
313→    expect(successText).toContain(truncateKey(charlie.publicKey));
314→
315→    // Now should have 2 recipients
316→    expect(await aliceShareDialog.getRecipientCount()).toBe(2);
317→
318→    await aliceShareDialog.close();
319→  });
320→
321→  test('5.2 Charlie sees the shared folder', async () => {
322→    await navigateToShared(charlie);
323→    await charlieSharedBrowser.waitForLoaded({ timeout: 30000 });
324→
325→    await charlieSharedBrowser.waitForSharedItem(sharedFolderName, { timeout: 15000 });
326→    expect(await charlieSharedBrowser.getSharedItemCount()).toBeGreaterThanOrEqual(1);
327→  });
328→
329→  // ============================================
330→  // Phase 6: Revoke Access
331→  // ============================================
332→
333→  test("6.1 Alice revokes Bob's access to the shared folder", async () => {
334→    await navigateToFiles(alice);
335→    await aliceFileList.waitForItemToAppear(sharedFolderName, { timeout: 15000 });
336→
337→    await aliceOpenShareDialog(sharedFolderName);
338→    await aliceShareDialog.waitForRecipientsLoaded();
339→
340→    // Should have 2 recipients (Bob + Charlie)
341→    expect(await aliceShareDialog.getRecipientCount()).toBe(2);
342→
343→    // Revoke Bob
344→    const bobTruncated = truncateKey(bob.publicKey);
345→    await aliceShareDialog.revokeRecipient(bobTruncated);
346→
347→    // Should now have 1 recipient (Charlie only)
348→    expect(await aliceShareDialog.getRecipientCount()).toBe(1);
349→
350→    await aliceShareDialog.close();
351→  });
352→
353→  test('6.2 Bob no longer sees the shared folder', async () => {
354→    // Reload Bob's shared view to get fresh data
355→    await navigateToShared(bob);
356→    await bobSharedBrowser.waitForLoaded({ timeout: 30000 });
357→
358→    // The shared folder should no longer appear for Bob
359→    // (may still show the shared file from test 3.1)
360→    const itemNames = await bobSharedBrowser.getSharedItemNames();
361→    const hasFolderShare = itemNames.some((n) => n.includes(sharedFolderName));
362→    expect(hasFolderShare).toBe(false);
363→  });
364→
365→  test('6.3 Charlie still has access', async () => {
366→    await navigateToShared(charlie);
367→    await charlieSharedBrowser.waitForLoaded({ timeout: 30000 });
368→
369→    await charlieSharedBrowser.waitForSharedItem(sharedFolderName, { timeout: 15000 });
370→    expect(await charlieSharedBrowser.getSharedItemCount()).toBeGreaterThanOrEqual(1);
371→  });
372→
373→  // ============================================
374→  // Phase 7: Post-Share Mutations
375→  // ============================================
376→
377→  test('7.1 Alice adds a file to the shared folder after sharing', async () => {
378→    await navigateToFiles(alice);
379→    await aliceFileList.waitForItemToAppear(sharedFolderName, { timeout: 15000 });
380→
381→    // Navigate into the shared folder
382→    await aliceNavigateIntoFolder(sharedFolderName);
383→
384→    // Upload a new file
385→    await aliceUploadFile(postShareFileName, postShareFileContent);
386→    expect(await aliceFileList.isItemVisible(postShareFileName)).toBe(true);
387→
388→    // Navigate back
389→    await aliceNavigateBack();
390→  });
391→
392→  test('7.2 Charlie sees the newly added file', async () => {
393→    // Navigate Charlie into the shared folder to check for the new file
394→    await navigateToShared(charlie);
395→    await charlieSharedBrowser.waitForLoaded({ timeout: 30000 });
396→
397→    await charlieSharedBrowser.navigateIntoFolder(sharedFolderName);
398→
399→    // Wait for folder contents - the new file should be visible
400→    const itemNames = await charlieSharedBrowser.getFolderItemNames();
401→    expect(itemNames.some((n) => n.includes(postShareFileName))).toBe(true);
402→
403→    await charlieSharedBrowser.navigateToRoot();
404→  });
405→
406→  // ============================================
407→  // Phase 8: Error Cases
408→  // ============================================
409→
410→  test('8.1 Invalid public key format shows error', async () => {
411→    await navigateToFiles(alice);
412→    await aliceFileList.waitForItemToAppear(sharedFileName, { timeout: 15000 });
413→
414→    await aliceOpenShareDialog(sharedFileName);
415→
416→    // Try an invalid key
417→    await aliceShareDialog.shareWithKey('0x1234invalid');
418→    const errorText = await aliceShareDialog.waitForError();
419→    expect(errorText).toContain('invalid key format');
420→
421→    await aliceShareDialog.close();
422→  });
423→
424→  test('8.2 Self-sharing shows error', async () => {
425→    await aliceOpenShareDialog(sharedFileName);
426→
427→    // Try sharing with own key
428→    await aliceShareDialog.shareWithKey(alice.publicKey);
429→    const errorText = await aliceShareDialog.waitForError();
430→    expect(errorText).toContain('cannot share with yourself');
431→
432→    await aliceShareDialog.close();
433→  });
434→
435→  test('8.3 Unregistered user shows error', async () => {
436→    await aliceOpenShareDialog(sharedFileName);
437→
438→    // Use a valid-format key that doesn't belong to any user
439→    // 0x04 + 128 hex chars (65 bytes uncompressed secp256k1)
440→    const fakeKey =
441→      '0x04' +
442→      'aa'.repeat(32) + // x coordinate
443→      'bb'.repeat(32); // y coordinate
444→
445→    await aliceShareDialog.shareWithKey(fakeKey);
446→    const errorText = await aliceShareDialog.waitForError({ timeout: 15000 });
447→    expect(errorText).toContain('user not found');
448→
449→    await aliceShareDialog.close();
450→  });
451→
452→  // ============================================
453→  // Phase 9: Hide Share
454→  // ============================================
455→
456→  test('9.1 Bob hides the shared file', async () => {
457→    await navigateToShared(bob);
458→    await bobSharedBrowser.waitForLoaded({ timeout: 30000 });
459→
460→    // Bob should still see the shared file from test 3.1
461→    await bobSharedBrowser.waitForSharedItem(sharedFileName, { timeout: 15000 });
462→
463→    // Right-click and hide
464→    await bobSharedBrowser.rightClickSharedItem(sharedFileName);
465→    await bobContextMenu.waitForOpen();
466→    await bobContextMenu.clickHide();
467→
468→    // Wait for the item to disappear
469→    await bobSharedBrowser.waitForSharedItemGone(sharedFileName, { timeout: 10000 });
470→  });
471→
472→  // ============================================
473→  // Phase 10: Re-share After Revoke
474→  // ============================================
475→
476→  test('10.1 Alice re-shares the folder with Bob after previous revoke', async () => {
477→    await navigateToFiles(alice);
478→    await aliceFileList.waitForItemToAppear(sharedFolderName, { timeout: 15000 });
479→
480→    await aliceOpenShareDialog(sharedFolderName);
481→
482→    // Charlie should be the only recipient (Bob was revoked in 6.1)
483→    await aliceShareDialog.waitForRecipientsLoaded();
484→    expect(await aliceShareDialog.getRecipientCount()).toBe(1);
485→
486→    // Re-share with Bob
487→    await aliceShareDialog.shareWithKey(bob.publicKey);
488→    const successText = await aliceShareDialog.waitForSuccess({ timeout: 60000 });
489→    expect(successText).toContain(truncateKey(bob.publicKey));
490→
491→    // Now should have 2 recipients again
492→    expect(await aliceShareDialog.getRecipientCount()).toBe(2);
493→
494→    await aliceShareDialog.close();
495→  });
496→
497→  test('10.2 Bob sees the re-shared folder', async () => {
498→    await navigateToShared(bob);
499→    await bobSharedBrowser.waitForLoaded({ timeout: 30000 });
500→
501→    await bobSharedBrowser.waitForSharedItem(sharedFolderName, { timeout: 15000 });
502→    const itemNames = await bobSharedBrowser.getSharedItemNames();
503→    const hasFolderShare = itemNames.some((n) => n.includes(sharedFolderName));
504→    expect(hasFolderShare).toBe(true);
505→  });
506→});
507→

