  1→import { Page } from '@playwright/test';
  2→import {
  3→  initializeVault,
  4→  encryptVaultKeys,
  5→  decryptVaultKeys,
  6→  deriveIpnsName,
  7→  hexToBytes,
  8→  bytesToHex,
  9→} from '@cipherbox/crypto';
 10→import * as ed from '@noble/ed25519';
 11→
 12→/**
 13→ * Test credentials from environment variables.
 14→ * These are loaded from .env locally or GitHub Secrets in CI.
 15→ *
 16→ * With Core Kit, the E2E flow uses CipherBox's own login UI:
 17→ * 1. Enter email in CipherBox email input
 18→ * 2. Backend sends OTP (static OTP in test mode)
 19→ * 3. Enter OTP code
 20→ * 4. CipherBox JWT -> Core Kit loginWithJWT -> backend auth -> vault init
 21→ */
