  1→import {
  2→  Controller,
  3→  Post,
  4→  Get,
  5→  Param,
  6→  Body,
  7→  UseGuards,
  8→  UseInterceptors,
  9→  UploadedFile,
 10→  ParseFilePipe,
 11→  MaxFileSizeValidator,
 12→  Inject,
 13→  Res,
 14→  StreamableFile,
 15→  Request,
 16→  PayloadTooLargeException,
 17→} from '@nestjs/common';
 18→import { Response, Request as ExpressRequest } from 'express';
 19→import { FileInterceptor } from '@nestjs/platform-express';
 20→import {
 21→  ApiTags,
 22→  ApiOperation,
 23→  ApiConsumes,
 24→  ApiBody,
 25→  ApiResponse,
 26→  ApiBearerAuth,
 27→} from '@nestjs/swagger';
 28→import { JwtAuthGuard } from '../auth/guards/jwt-auth.guard';
 29→import { IPFS_PROVIDER, IpfsProvider } from './providers';
 30→import { UploadResponseDto, UnpinDto, UnpinResponseDto } from './dto';
 31→import { VaultService } from '../vault/vault.service';
 32→import { MetricsService } from '../metrics/metrics.service';
 33→
 34→const MAX_FILE_SIZE = 100 * 1024 * 1024; // 100MB
 35→
 36→interface RequestWithUser extends ExpressRequest {
 37→  user: { id: string };
 38→}
 39→
 40→@ApiTags('IPFS')
 41→@ApiBearerAuth()
 42→@UseGuards(JwtAuthGuard)
 43→@Controller('ipfs')
 44→export class IpfsController {
 45→  constructor(
 46→    @Inject(IPFS_PROVIDER) private readonly ipfsProvider: IpfsProvider,
 47→    private readonly vaultService: VaultService,
 48→    private readonly metricsService: MetricsService
 49→  ) {}
 50→
 51→  @Post('upload')
 52→  @ApiOperation({
 53→    summary: 'Upload encrypted file to IPFS with quota tracking',
 54→    description:
 55→      'Pins encrypted file to IPFS, checks storage quota, and records the pin for quota tracking. All in one atomic request.',
 56→  })
 57→  @ApiConsumes('multipart/form-data')
 58→  @ApiBody({
 59→    schema: {
 60→      type: 'object',
 61→      properties: {
 62→        file: {
 63→          type: 'string',
 64→          format: 'binary',
 65→          description: 'Encrypted file blob (max 100MB)',
 66→        },
 67→      },
 68→      required: ['file'],
 69→    },
 70→  })
 71→  @ApiResponse({
 72→    status: 201,
 73→    description: 'File uploaded, pinned, and recorded successfully',
 74→    type: UploadResponseDto,
 75→  })
 76→  @ApiResponse({
 77→    status: 401,
 78→    description: 'Unauthorized - JWT token required',
 79→  })
 80→  @ApiResponse({
 81→    status: 413,
 82→    description: 'Storage quota exceeded',
 83→  })
 84→  @UseInterceptors(
 85→    FileInterceptor('file', {
 86→      limits: { fileSize: MAX_FILE_SIZE },
 87→    })
 88→  )
 89→  async upload(
 90→    @Request() req: RequestWithUser,
 91→    @UploadedFile(
 92→      new ParseFilePipe({
 93→        validators: [new MaxFileSizeValidator({ maxSize: MAX_FILE_SIZE })],
 94→      })
 95→    )
 96→    file: Express.Multer.File
 97→  ): Promise<UploadResponseDto> {
 98→    const hasQuota = await this.vaultService.checkQuota(req.user.id, file.size);
 99→    if (!hasQuota) throw new PayloadTooLargeException('Storage quota exceeded');
100→    const result = await this.ipfsProvider.pinFile(file.buffer);
101→    try {
102→      await this.vaultService.recordPin(req.user.id, result.cid, result.size);
103→    } catch (err) {
104→      await this.ipfsProvider.unpinFile(result.cid).catch(() => undefined);
105→      throw err;
106→    }
107→    this.metricsService.fileUploads.inc();
108→    this.metricsService.fileUploadBytes.inc(result.size);
109→    return { cid: result.cid, size: result.size, recorded: true };
110→  }
111→
112→  @Post('unpin')
113→  @ApiOperation({
114→    summary: 'Unpin file from IPFS',
115→    description: 'Remove a pinned file from IPFS using its CID.',
116→  })
117→  @ApiResponse({
118→    status: 201,
119→    description: 'File unpinned successfully',
120→    type: UnpinResponseDto,
121→  })
122→  @ApiResponse({
123→    status: 401,
124→    description: 'Unauthorized - JWT token required',
125→  })
126→  async unpin(@Body() dto: UnpinDto): Promise<UnpinResponseDto> {
127→    await this.ipfsProvider.unpinFile(dto.cid);
128→    this.metricsService.fileUnpins.inc();
129→    return { success: true };
130→  }
131→
132→  @Get(':cid')
133→  @ApiOperation({
134→    summary: 'Get file from IPFS',
135→    description: 'Download an encrypted file from IPFS via the configured gateway.',
136→  })
137→  @ApiResponse({
138→    status: 200,
139→    description: 'File retrieved successfully',
140→    content: {
141→      'application/octet-stream': {
142→        schema: {
143→          type: 'string',
144→          format: 'binary',
145→        },
146→      },
147→    },
148→  })
149→  @ApiResponse({
150→    status: 401,
151→    description: 'Unauthorized - JWT token required',
152→  })
153→  @ApiResponse({
154→    status: 404,
155→    description: 'File not found',
156→  })
157→  async get(
158→    @Param('cid') cid: string,
159→    @Res({ passthrough: true }) res: Response
160→  ): Promise<StreamableFile> {
161→    const buffer = await this.ipfsProvider.getFile(cid);
162→    this.metricsService.fileDownloads.inc();
163→    res.set({
164→      'Content-Type': 'application/octet-stream',
165→      'Content-Length': buffer.length.toString(),
166→    });
167→    return new StreamableFile(buffer);
168→  }
169→}
