  1→import {
  2→  InternalServerErrorException,
  3→  NotFoundException,
  4→  BadRequestException,
  5→} from '@nestjs/common';
  6→import { IpfsProvider } from './ipfs-provider.interface';
  7→
  8→interface KuboAddResponse {
  9→  Name: string;
 10→  Hash: string;
 11→  Size: string;
 12→}
 13→
 14→export class LocalProvider implements IpfsProvider {
 15→  constructor(
 16→    private readonly apiUrl: string,
 17→    // Gateway URL kept for future use (e.g., public read access)
 18→    // Currently using API directly via cat endpoint
 19→    _gatewayUrl: string
 20→  ) {
 21→    if (!apiUrl) {
 22→      throw new Error('IPFS API URL is required');
 23→    }
 24→  }
 25→
 26→  /**
 27→   * Pin a file to local IPFS node via Kubo API.
 28→   * @param data - The file buffer to pin
 29→   * @param metadata - Optional metadata (ignored for local IPFS)
 30→   * @returns The CID and size of the pinned file
 31→   */
 32→  async pinFile(
 33→    data: Buffer,
 34→    _metadata?: Record<string, string>
 35→  ): Promise<{ cid: string; size: number }> {
 36→    if (!data || data.length === 0) {
 37→      throw new BadRequestException('File data cannot be empty');
 38→    }
 39→
 40→    // Use native FormData (available in Node.js 18+) with Blob for compatibility with native fetch
 41→    const formData = new FormData();
 42→    // Create Blob from Buffer using explicit Uint8Array conversion for type safety
 43→    const blob = new Blob([new Uint8Array(data)], { type: 'application/octet-stream' });
 44→    formData.append('file', blob, `file-${Date.now()}`);
 45→
 46→    try {
 47→      // Kubo API uses POST for all operations
 48→      // pin=true ensures the file is pinned, cid-version=1 for CIDv1
 49→      const response = await fetch(`${this.apiUrl}/api/v0/add?pin=true&cid-version=1`, {
 50→        method: 'POST',
 51→        body: formData,
 52→      });
 53→
 54→      if (!response.ok) {
 55→        const errorText = await response.text();
 56→        throw new InternalServerErrorException(
 57→          `IPFS add failed: ${response.status} - ${errorText}`
 58→        );
 59→      }
 60→
 61→      const result: KuboAddResponse = await response.json();
 62→      return {
 63→        cid: result.Hash,
 64→        size: parseInt(result.Size, 10),
 65→      };
 66→    } catch (error) {
 67→      if (error instanceof BadRequestException || error instanceof InternalServerErrorException) {
 68→        throw error;
 69→      }
 70→      throw new InternalServerErrorException(
 71→        `Failed to pin file to IPFS: ${error instanceof Error ? error.message : 'Unknown error'}`
 72→      );
 73→    }
 74→  }
 75→
 76→  /**
 77→   * Unpin a file from local IPFS node.
 78→   * @param cid - The CID of the file to unpin
 79→   */
 80→  async unpinFile(cid: string): Promise<void> {
 81→    if (!cid || typeof cid !== 'string') {
 82→      throw new BadRequestException('CID is required');
 83→    }
 84→
 85→    try {
 86→      // Kubo API uses POST for all operations including unpin
 87→      const response = await fetch(`${this.apiUrl}/api/v0/pin/rm?arg=${cid}`, {
 88→        method: 'POST',
 89→      });
 90→
 91→      if (!response.ok) {
 92→        const errorText = await response.text();
 93→        // "not pinned" means already unpinned - treat as success (idempotent)
 94→        if (errorText.includes('not pinned')) {
 95→          return;
 96→        }
 97→        throw new InternalServerErrorException(
 98→          `IPFS unpin failed: ${response.status} - ${errorText}`
 99→        );
100→      }
101→    } catch (error) {
102→      if (
103→        error instanceof BadRequestException ||
104→        error instanceof InternalServerErrorException ||
105→        error instanceof NotFoundException
106→      ) {
107→        throw error;
108→      }
109→      throw new InternalServerErrorException(
110→        `Failed to unpin file from IPFS: ${error instanceof Error ? error.message : 'Unknown error'}`
111→      );
112→    }
113→  }
114→
115→  /**
116→   * Get a file from local IPFS node via Kubo API.
117→   * @param cid - The CID of the file to retrieve
118→   * @returns The file content as a Buffer
119→   */
120→  async getFile(cid: string): Promise<Buffer> {
121→    if (!cid || typeof cid !== 'string') {
122→      throw new BadRequestException('CID is required');
123→    }
124→
125→    try {
126→      // Kubo API uses POST for cat operation
127→      const response = await fetch(`${this.apiUrl}/api/v0/cat?arg=${cid}`, {
128→        method: 'POST',
129→      });
130→
131→      if (!response.ok) {
132→        const errorText = await response.text();
133→        // Check for not found errors
134→        if (
135→          errorText.includes('not found') ||
136→          errorText.includes('no link') ||
137→          errorText.includes('blockstore: block not found')
138→        ) {
139→          throw new NotFoundException(`File not found: ${cid}`);
140→        }
141→        throw new InternalServerErrorException(
142→          `IPFS cat failed: ${response.status} - ${errorText}`
143→        );
144→      }
145→
146→      const arrayBuffer = await response.arrayBuffer();
147→      return Buffer.from(arrayBuffer);
148→    } catch (error) {
149→      if (
150→        error instanceof BadRequestException ||
151→        error instanceof InternalServerErrorException ||
152→        error instanceof NotFoundException
153→      ) {
154→        throw error;
155→      }
156→      throw new InternalServerErrorException(
157→        `Failed to get file from IPFS: ${error instanceof Error ? error.message : 'Unknown error'}`
158→      );
159→    }
160→  }
161→}
