  1→import { Injectable, ConflictException, NotFoundException } from '@nestjs/common';
  2→import { InjectRepository } from '@nestjs/typeorm';
  3→import { Repository } from 'typeorm';
  4→import { Vault } from './entities/vault.entity';
  5→import { PinnedCid } from './entities/pinned-cid.entity';
  6→import { FolderIpns } from '../ipns/entities/folder-ipns.entity';
  7→import { User } from '../auth/entities/user.entity';
  8→import { InitVaultDto, VaultResponseDto } from './dto/init-vault.dto';
  9→import { VaultExportDto } from './dto/vault-export.dto';
 10→import { QuotaResponseDto } from './dto/quota.dto';
 11→import { TeeKeyStateService } from '../tee/tee-key-state.service';
 12→import { TeeKeysDto } from '../tee/dto/tee-keys.dto';
 13→
 14→/**
 15→ * Storage quota limit: 500 MiB
 16→ */
 17→export const QUOTA_LIMIT_BYTES = 500 * 1024 * 1024; // 524,288,000 bytes
 18→
 19→@Injectable()
 20→export class VaultService {
 21→  constructor(
 22→    @InjectRepository(Vault)
 23→    private readonly vaultRepository: Repository<Vault>,
 24→    @InjectRepository(PinnedCid)
 25→    private readonly pinnedCidRepository: Repository<PinnedCid>,
 26→    @InjectRepository(FolderIpns)
 27→    private readonly folderIpnsRepository: Repository<FolderIpns>,
 28→    @InjectRepository(User)
 29→    private readonly userRepository: Repository<User>,
 30→    private readonly teeKeyStateService: TeeKeyStateService
 31→  ) {}
 32→
 33→  /**
 34→   * Initialize a new vault for a user
 35→   * Creates the vault with encrypted keys on first sign-in
 36→   *
 37→   * @throws ConflictException if vault already exists for user
 38→   */
 39→  async initializeVault(userId: string, dto: InitVaultDto): Promise<VaultResponseDto> {
 40→    // Check if vault already exists
 41→    const existingVault = await this.vaultRepository.findOne({
 42→      where: { ownerId: userId },
 43→    });
 44→
 45→    if (existingVault) {
 46→      throw new ConflictException('Vault already exists for this user');
 47→    }
 48→
 49→    // Decode hex strings to buffers
 50→    const vault = this.vaultRepository.create({
 51→      ownerId: userId,
 52→      ownerPublicKey: Buffer.from(dto.ownerPublicKey, 'hex'),
 53→      encryptedRootFolderKey: Buffer.from(dto.encryptedRootFolderKey, 'hex'),
 54→      encryptedRootIpnsPrivateKey: Buffer.from(dto.encryptedRootIpnsPrivateKey, 'hex'),
 55→      rootIpnsName: dto.rootIpnsName,
 56→      initializedAt: null,
 57→    });
 58→
 59→    const savedVault = await this.vaultRepository.save(vault);
 60→
 61→    // Create root folder IPNS entry for publish tracking
 62→    // This allows IPNS publishes to work without requiring TEE fields on every publish
 63→    const rootFolderIpns = this.folderIpnsRepository.create({
 64→      userId,
 65→      ipnsName: dto.rootIpnsName,
 66→      latestCid: null, // No content yet
 67→      sequenceNumber: '0',
 68→      encryptedIpnsPrivateKey: null, // TEE key added when TEE is implemented
 69→      keyEpoch: null,
 70→      isRoot: true,
 71→    });
 72→    await this.folderIpnsRepository.save(rootFolderIpns);
 73→
 74→    const teeKeys = await this.teeKeyStateService.getTeeKeysDto();
 75→    return this.toVaultResponse(savedVault, teeKeys);
 76→  }
 77→
 78→  /**
 79→   * Get vault for a user
 80→   *
 81→   * @throws NotFoundException if vault does not exist
 82→   */
 83→  async getVault(userId: string): Promise<VaultResponseDto> {
 84→    const vault = await this.vaultRepository.findOne({
 85→      where: { ownerId: userId },
 86→    });
 87→
 88→    if (!vault) {
 89→      throw new NotFoundException('Vault not found');
 90→    }
 91→
 92→    const teeKeys = await this.teeKeyStateService.getTeeKeysDto();
 93→    return this.toVaultResponse(vault, teeKeys);
 94→  }
 95→
 96→  /**
 97→   * Check if vault exists for user (returns null if not)
 98→   */
 99→  async findVault(userId: string): Promise<VaultResponseDto | null> {
100→    const vault = await this.vaultRepository.findOne({
101→      where: { ownerId: userId },
102→    });
103→
104→    if (!vault) return null;
105→
106→    const teeKeys = await this.teeKeyStateService.getTeeKeysDto();
107→    return this.toVaultResponse(vault, teeKeys);
108→  }
109→
110→  /**
111→   * Get current storage quota usage for a user
112→   */
113→  async getQuota(userId: string): Promise<QuotaResponseDto> {
114→    const result = await this.pinnedCidRepository
115→      .createQueryBuilder('pin')
116→      .select('COALESCE(SUM(pin.size_bytes), 0)', 'total')
117→      .where('pin.user_id = :userId', { userId })
118→      .getRawOne<{ total: string }>();
119→
120→    const usedBytes = parseInt(result?.total ?? '0', 10);
121→    const remainingBytes = Math.max(0, QUOTA_LIMIT_BYTES - usedBytes);
122→
123→    return {
124→      usedBytes,
125→      limitBytes: QUOTA_LIMIT_BYTES,
126→      remainingBytes,
127→    };
128→  }
129→
130→  /**
131→   * Check if user has sufficient quota for additional storage
132→   *
133→   * @returns true if (current usage + additionalBytes) <= quota limit
134→   */
135→  async checkQuota(userId: string, additionalBytes: number): Promise<boolean> {
136→    const quota = await this.getQuota(userId);
137→    return quota.usedBytes + additionalBytes <= QUOTA_LIMIT_BYTES;
138→  }
139→
140→  /**
141→   * Record a pinned CID for quota tracking
142→   * Uses upsert (ON CONFLICT DO NOTHING) for idempotency
143→   */
144→  async recordPin(userId: string, cid: string, sizeBytes: number): Promise<void> {
145→    await this.pinnedCidRepository
146→      .createQueryBuilder()
147→      .insert()
148→      .into(PinnedCid)
149→      .values({
150→        userId,
151→        cid,
152→        sizeBytes: sizeBytes.toString(),
153→      })
154→      .orIgnore() // ON CONFLICT DO NOTHING for idempotency
155→      .execute();
156→  }
157→
158→  /**
159→   * Remove a pinned CID record
160→   * Idempotent - no error if CID not found
161→   */
162→  async recordUnpin(userId: string, cid: string): Promise<void> {
163→    await this.pinnedCidRepository.delete({
164→      userId,
165→      cid,
166→    });
167→  }
168→
169→  /**
170→   * Mark vault as initialized (first file uploaded)
171→   */
172→  async markInitialized(userId: string): Promise<void> {
173→    await this.vaultRepository.update({ ownerId: userId }, { initializedAt: new Date() });
174→  }
175→
176→  /**
177→   * Get export data for independent recovery.
178→   * Returns the minimal set of fields needed to reconstruct the vault:
179→   * root IPNS name + encrypted root keys + derivation hints.
180→   */
181→  async getExportData(userId: string): Promise<VaultExportDto> {
182→    const vault = await this.vaultRepository.findOne({
183→      where: { ownerId: userId },
184→    });
185→
186→    if (!vault) {
187→      throw new NotFoundException('Vault not found');
188→    }
189→
190→    const user = await this.userRepository.findOne({
191→      where: { id: userId },
192→    });
193→
194→    return {
195→      format: 'cipherbox-vault-export',
196→      version: '1.0',
197→      exportedAt: new Date().toISOString(),
198→      rootIpnsName: vault.rootIpnsName,
199→      encryptedRootFolderKey: vault.encryptedRootFolderKey.toString('hex'),
200→      encryptedRootIpnsPrivateKey: vault.encryptedRootIpnsPrivateKey.toString('hex'),
201→      derivationMethod: user ? 'web3auth' : null,
202→    };
203→  }
204→
205→  /**
206→   * Convert Vault entity to response DTO with hex-encoded fields
207→   */
208→  private toVaultResponse(vault: Vault, teeKeys: TeeKeysDto | null = null): VaultResponseDto {
209→    return {
210→      id: vault.id,
211→      ownerPublicKey: vault.ownerPublicKey.toString('hex'),
212→      encryptedRootFolderKey: vault.encryptedRootFolderKey.toString('hex'),
213→      encryptedRootIpnsPrivateKey: vault.encryptedRootIpnsPrivateKey.toString('hex'),
214→      rootIpnsName: vault.rootIpnsName,
215→      createdAt: vault.createdAt,
216→      initializedAt: vault.initializedAt,
217→      teeKeys,
218→    };
219→  }
220→}
