 1→import { NestFactory } from '@nestjs/core';
 2→import { SwaggerModule, DocumentBuilder } from '@nestjs/swagger';
 3→import { ValidationPipe, Logger } from '@nestjs/common';
 4→import cookieParser from 'cookie-parser';
 5→import { AppModule } from './app.module';
 6→
 7→async function bootstrap() {
 8→  const isDev = process.env.NODE_ENV === 'development' || !process.env.NODE_ENV;
 9→
10→  // In staging/production, omit debug and verbose log levels for concise output
11→  const app = await NestFactory.create(AppModule, {
12→    logger: isDev ? ['log', 'error', 'warn', 'debug', 'verbose'] : ['log', 'error', 'warn'],
13→  });
14→
15→  const logger = new Logger('Bootstrap');
16→
17→  // [SECURITY: CRITICAL-01] Enable global validation pipe
18→  // Without this, all DTO validation decorators (@IsString, @Matches, etc.) are ignored
19→  app.useGlobalPipes(
20→    new ValidationPipe({
21→      whitelist: true, // Strip properties not in DTO
22→      forbidNonWhitelisted: true, // Reject unexpected properties
23→      transform: true, // Auto-transform types
24→    })
25→  );
26→
27→  app.use(cookieParser());
28→
29→  // CORS_ALLOWED_ORIGINS supports wildcards (e.g. https://cipher-box-pr-*.onrender.com)
30→  // Falls back to WEB_APP_URL for backwards compatibility
31→  const rawOrigins = process.env.CORS_ALLOWED_ORIGINS || process.env.WEB_APP_URL;
32→  const originEntries = rawOrigins
33→    ? rawOrigins.split(',').map((s) => s.trim())
34→    : ['http://localhost:5173', 'http://localhost:4173'];
35→  const exactOrigins = originEntries.filter((o) => !o.includes('*'));
36→  const wildcardPatterns = originEntries
37→    .filter((o) => o.includes('*'))
38→    .map((o) => new RegExp(`^${o.replace(/[.+?^${}()|[\]\\]/g, '\\$&').replace(/\*/g, '.*')}$`));
39→
40→  app.enableCors({
41→    origin: (
42→      origin: string | undefined,
43→      callback: (err: Error | null, allow?: boolean) => void
44→    ) => {
45→      if (!origin) return callback(null, true);
46→      if (exactOrigins.includes(origin)) return callback(null, true);
47→      if (wildcardPatterns.some((re) => re.test(origin))) return callback(null, true);
48→      callback(new Error(`Origin ${origin} not allowed by CORS`));
49→    },
50→    credentials: true,
51→  });
52→
53→  const config = new DocumentBuilder()
54→    .setTitle('CipherBox API')
55→    .setDescription('Zero-knowledge encrypted cloud storage API')
56→    .setVersion('0.1.0')
57→    .addBearerAuth()
58→    .build();
59→  const document = SwaggerModule.createDocument(app, config);
60→  SwaggerModule.setup('api-docs', app, document, {
61→    jsonDocumentUrl: 'api-docs/json',
62→  });
63→
64→  const port = process.env.PORT || 3000;
65→  await app.listen(port);
66→  logger.log(`CipherBox API running on http://localhost:${port}`);
67→  logger.log(`Swagger UI: http://localhost:${port}/api-docs`);
68→}
69→bootstrap();
