 176→  async function navigateIntoFolder(name: string): Promise<void> {
 177→    // Retry once if navigation fails (transient IPNS resolution failures in CI
 178→    // cause the app to navigate back to parent; the folder row reappears and
 179→    // a second attempt usually succeeds).
 180→    for (let attempt = 0; attempt < 2; attempt++) {
 181→      await fileList.doubleClickFolder(name);
 182→      try {
 183→        await breadcrumbs.waitForPathToContain(name, { timeout: 30000 });
 184→        navigationStack.push(name);
