 186→      } catch {
 187→        if (attempt === 1) throw new Error(`navigateIntoFolder("${name}") failed after 2 attempts`);
 188→        // Wait for the app to settle after a failed navigation (it navigates back to parent)
 189→        await page.waitForTimeout(2000);
 190→        // Verify the folder row is still visible before retrying
 191→        await fileList.waitForItemToAppear(name, { timeout: 10000 });
