 530→  test('3.7 Page reload preserves session and reloads root folder', async () => {
 531→    // Reload + re-auth + IPNS sync chain can take >30s in CI
 532→    test.setTimeout(90000);
 533→
 534→    // Navigate to root before reload so we start from a clean state
 535→    await navigateToRoot();
 536→
 537→    // Reload the page — Zustand store is wiped
 538→    await page.reload({ waitUntil: 'domcontentloaded' });
 539→
 540→    // Reset navigation stack since we're starting fresh
 541→    navigationStack.length = 0;
 542→    navigationStack.push('root');
 543→
 544→    // Re-inject test auth state after reload (test-login bypasses Core Kit
 545→    // so there's no persistent session to auto-restore). For real Core Kit
 546→    // flow this is a no-op.
 547→    await reinjectTestAuthAfterReload(page);
 548→
 549→    // Wait for auth to restore (user menu becomes visible)
 550→    await page.locator('[data-testid="user-menu"]').waitFor({
 551→      state: 'visible',
 552→      timeout: 30000,
 553→    });
 554→
 555→    // Wait for initial sync to complete — all root items must appear
 556→    // This proves IPNS root metadata was re-fetched and decrypted
 557→    await fileList.waitForItemToAppear(workspaceFolder, { timeout: 60000 });
 558→
 559→    // Wait for files to appear too (sync may render folders before files)
 560→    await fileList.waitForItemToAppear(rootFiles[0].name, { timeout: 30000 });
 561→
 562→    // Verify other root-level items are also visible
 563→    // rootFiles[0] was moved to workspace in 5.1 — but Phase 5 hasn't run yet at this point
 564→    for (const file of rootFiles) {
 565→      expect(await fileList.isItemVisible(file.name)).toBe(true);
 566→    }
 567→    expect(await fileList.isItemVisible(editableFileName)).toBe(true);
 568→  });
