 1→import { Injectable, NestInterceptor, ExecutionContext, CallHandler } from '@nestjs/common';
 2→import { Observable, tap } from 'rxjs';
 3→import { Request, Response } from 'express';
 4→import { MetricsService } from './metrics.service';
 5→
 6→@Injectable()
 7→export class HttpMetricsInterceptor implements NestInterceptor {
 8→  constructor(private readonly metricsService: MetricsService) {}
 9→
10→  intercept(context: ExecutionContext, next: CallHandler): Observable<unknown> {
11→    const httpContext = context.switchToHttp();
12→    const req = httpContext.getRequest<Request>();
13→    const startTime = process.hrtime.bigint();
14→
15→    return next.handle().pipe(
16→      tap({
17→        next: () => {
18→          const res = httpContext.getResponse<Response>();
19→          this.recordDuration(req, res.statusCode, startTime);
20→        },
21→        error: (err: unknown) => {
22→          // Exception filters haven't run yet, so derive status from the exception
23→          const statusCode =
24→            err &&
25→            typeof err === 'object' &&
26→            'getStatus' in err &&
27→            typeof (err as { getStatus: unknown }).getStatus === 'function'
28→              ? (err as { getStatus: () => number }).getStatus()
29→              : 500;
30→          this.recordDuration(req, statusCode, startTime);
31→        },
32→      })
33→    );
34→  }
35→
36→  private recordDuration(req: Request, statusCode: number, startTime: bigint): void {
37→    // Use the route template when available (already parameterised by Express/NestJS);
38→    // only normalise the raw req.path when the route was not matched.
39→    const route = req.route?.path ?? this.normalizeRoute(req.path);
40→
41→    // Exclude /metrics endpoint to avoid self-referential noise
42→    if (route === '/metrics') return;
43→
44→    const durationNs = Number(process.hrtime.bigint() - startTime);
45→    const durationSec = durationNs / 1e9;
46→
47→    this.metricsService.httpRequestDuration
48→      .labels(req.method, route, String(statusCode))
49→      .observe(durationSec);
50→  }
51→
52→  private normalizeRoute(path: string): string {
53→    // Replace UUIDs and CIDs with placeholders to keep cardinality low
54→    return path
55→      .replace(/[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}/gi, ':id')
56→      .replace(/\/ipfs\/[a-zA-Z0-9]+/, '/ipfs/:cid')
57→      .replace(/\/ipns\/[a-zA-Z0-9]+/, '/ipns/:name');
58→  }
59→}
