  1→import { Injectable, OnModuleInit, Logger } from '@nestjs/common';
  2→import { InjectRepository } from '@nestjs/typeorm';
  3→import { Repository } from 'typeorm';
  4→import * as client from 'prom-client';
  5→import { PinnedCid } from '../vault/entities/pinned-cid.entity';
  6→import { FolderIpns } from '../ipns/entities/folder-ipns.entity';
  7→import { User } from '../auth/entities/user.entity';
  8→import { IpnsRepublishSchedule } from '../republish/republish-schedule.entity';
  9→
 10→/**
 11→ * Central Prometheus metrics registry and collector.
 12→ * Exposes both event-driven counters (incremented by controllers/services)
 13→ * and gauge metrics polled from the database every 30 seconds.
 14→ */
 15→@Injectable()
 16→export class MetricsService implements OnModuleInit {
 17→  private readonly logger = new Logger(MetricsService.name);
 18→  readonly registry: client.Registry;
 19→  private collectInterval: ReturnType<typeof setInterval> | null = null;
 20→
 21→  // --- Gauges (DB-polled state) ---
 22→  readonly usersTotal: client.Gauge;
 23→  readonly filesTotal: client.Gauge;
 24→  readonly storageBytesTotal: client.Gauge;
 25→  readonly ipnsEntriesTotal: client.Gauge;
 26→  readonly republishScheduleTotal: client.Gauge;
 27→
 28→  // --- Counters (event-driven) ---
 29→  readonly fileUploads: client.Counter;
 30→  readonly fileUploadBytes: client.Counter;
 31→  readonly fileDownloads: client.Counter;
 32→  readonly fileUnpins: client.Counter;
 33→  readonly ipnsPublishes: client.Counter;
 34→  readonly ipnsResolves: client.Counter;
 35→  readonly republishRuns: client.Counter;
 36→  readonly republishEntriesProcessed: client.Counter;
 37→  readonly authLogins: client.Counter;
 38→
 39→  // --- Histograms ---
 40→  readonly httpRequestDuration: client.Histogram;
 41→
 42→  constructor(
 43→    @InjectRepository(PinnedCid)
 44→    private readonly pinnedCidRepository: Repository<PinnedCid>,
 45→    @InjectRepository(FolderIpns)
 46→    private readonly folderIpnsRepository: Repository<FolderIpns>,
 47→    @InjectRepository(User)
 48→    private readonly userRepository: Repository<User>,
 49→    @InjectRepository(IpnsRepublishSchedule)
 50→    private readonly republishScheduleRepository: Repository<IpnsRepublishSchedule>
 51→  ) {
 52→    this.registry = new client.Registry();
 53→    this.registry.setDefaultLabels({ app: 'cipherbox-api' });
 54→
 55→    // Gauges
 56→    this.usersTotal = new client.Gauge({
 57→      name: 'cipherbox_users_total',
 58→      help: 'Total registered users',
 59→      registers: [this.registry],
 60→    });
 61→
 62→    this.filesTotal = new client.Gauge({
 63→      name: 'cipherbox_files_total',
 64→      help: 'Total pinned files across all users',
 65→      registers: [this.registry],
 66→    });
 67→
 68→    this.storageBytesTotal = new client.Gauge({
 69→      name: 'cipherbox_storage_bytes_total',
 70→      help: 'Total storage used across all users in bytes',
 71→      registers: [this.registry],
 72→    });
 73→
 74→    this.ipnsEntriesTotal = new client.Gauge({
 75→      name: 'cipherbox_ipns_entries_total',
 76→      help: 'Total IPNS entries by record type',
 77→      labelNames: ['record_type'],
 78→      registers: [this.registry],
 79→    });
 80→
 81→    this.republishScheduleTotal = new client.Gauge({
 82→      name: 'cipherbox_republish_schedule_total',
 83→      help: 'IPNS republish schedule entries by status',
 84→      labelNames: ['status'],
 85→      registers: [this.registry],
 86→    });
 87→
 88→    // Counters
 89→    this.fileUploads = new client.Counter({
 90→      name: 'cipherbox_file_uploads_total',
 91→      help: 'Total file uploads',
 92→      registers: [this.registry],
 93→    });
 94→
 95→    this.fileUploadBytes = new client.Counter({
 96→      name: 'cipherbox_file_upload_bytes_total',
 97→      help: 'Total bytes uploaded',
 98→      registers: [this.registry],
 99→    });
100→
101→    this.fileDownloads = new client.Counter({
102→      name: 'cipherbox_file_downloads_total',
103→      help: 'Total file downloads',
104→      registers: [this.registry],
105→    });
106→
107→    this.fileUnpins = new client.Counter({
108→      name: 'cipherbox_file_unpins_total',
109→      help: 'Total file unpins',
110→      registers: [this.registry],
111→    });
112→
113→    this.ipnsPublishes = new client.Counter({
114→      name: 'cipherbox_ipns_publishes_total',
115→      help: 'Total IPNS publishes',
116→      labelNames: ['type'],
117→      registers: [this.registry],
118→    });
119→
120→    this.ipnsResolves = new client.Counter({
121→      name: 'cipherbox_ipns_resolves_total',
122→      help: 'Total IPNS resolves',
123→      labelNames: ['source'],
124→      registers: [this.registry],
125→    });
126→
127→    this.republishRuns = new client.Counter({
128→      name: 'cipherbox_republish_runs_total',
129→      help: 'Total republish cron runs',
130→      registers: [this.registry],
131→    });
132→
133→    this.republishEntriesProcessed = new client.Counter({
134→      name: 'cipherbox_republish_entries_processed_total',
135→      help: 'Total republish entries processed',
136→      labelNames: ['result'],
137→      registers: [this.registry],
138→    });
139→
140→    this.authLogins = new client.Counter({
141→      name: 'cipherbox_auth_logins_total',
142→      help: 'Total successful logins',
143→      labelNames: ['method', 'new_user'],
144→      registers: [this.registry],
145→    });
146→
147→    // Histograms
148→    this.httpRequestDuration = new client.Histogram({
149→      name: 'cipherbox_http_request_duration_seconds',
150→      help: 'HTTP request duration in seconds',
151→      labelNames: ['method', 'route', 'status_code'],
152→      buckets: [0.01, 0.05, 0.1, 0.25, 0.5, 1, 2.5, 5, 10],
153→      registers: [this.registry],
154→    });
155→  }
156→
157→  async onModuleInit(): Promise<void> {
158→    // Attempt an immediate collection; log and continue if DB isn't ready yet
159→    await this.collectGauges().catch((err: unknown) => {
160→      this.logger.warn(
161→        `Initial gauge collection failed: ${err instanceof Error ? err.message : String(err)}`
162→      );
163→    });
164→    this.collectInterval = setInterval(() => {
165→      this.collectGauges().catch((err: unknown) => {
166→        this.logger.warn(
167→          `Gauge collection failed: ${err instanceof Error ? err.message : String(err)}`
168→        );
169→      });
170→    }, 30_000);
171→    this.logger.log('Prometheus metrics initialized (collecting gauges every 30s)');
172→  }
173→
174→  onModuleDestroy(): void {
175→    if (this.collectInterval) {
176→      clearInterval(this.collectInterval);
177→    }
178→  }
179→
180→  async getMetrics(): Promise<string> {
181→    return this.registry.metrics();
182→  }
183→
184→  getContentType(): string {
185→    return this.registry.contentType;
186→  }
187→
188→  private async collectGauges(): Promise<void> {
189→    const [userCount, fileStats, ipnsByType, republishByStatus] = await Promise.all([
190→      this.userRepository.count(),
191→      this.pinnedCidRepository
192→        .createQueryBuilder('pin')
193→        .select('COUNT(*)', 'count')
194→        .addSelect('COALESCE(SUM(pin.size_bytes), 0)', 'totalBytes')
195→        .getRawOne<{ count: string; totalBytes: string }>(),
196→      this.folderIpnsRepository
197→        .createQueryBuilder('ipns')
198→        .select('ipns.record_type', 'recordType')
199→        .addSelect('COUNT(*)', 'count')
200→        .groupBy('ipns.record_type')
201→        .getRawMany<{ recordType: string; count: string }>(),
202→      this.republishScheduleRepository
203→        .createQueryBuilder('sched')
204→        .select('sched.status', 'status')
205→        .addSelect('COUNT(*)', 'count')
206→        .groupBy('sched.status')
207→        .getRawMany<{ status: string; count: string }>(),
208→    ]);
209→
210→    this.usersTotal.set(userCount);
211→    this.filesTotal.set(parseInt(fileStats?.count ?? '0', 10));
212→    this.storageBytesTotal.set(parseInt(fileStats?.totalBytes ?? '0', 10));
213→
214→    // Reset IPNS gauges before setting to avoid stale labels
215→    this.ipnsEntriesTotal.reset();
216→    for (const row of ipnsByType) {
217→      this.ipnsEntriesTotal.labels(row.recordType).set(parseInt(row.count, 10));
218→    }
219→
220→    // Reset republish gauges before setting
221→    this.republishScheduleTotal.reset();
222→    for (const row of republishByStatus) {
223→      this.republishScheduleTotal.labels(row.status).set(parseInt(row.count, 10));
224→    }
225→  }
226→}
