  1→import { type Page, type Locator } from '@playwright/test';
  2→
  3→/**
  4→ * Page object for ShareDialog component interactions.
  5→ *
  6→ * Encapsulates sharing actions: enter recipient key, submit, view recipients, revoke.
  7→ * Uses CSS class selectors matching the ShareDialog component.
  8→ */
  9→export class ShareDialogPage {
 10→  constructor(private readonly page: Page) {}
 11→
 12→  /**
 13→   * Get the share dialog modal (identified by title starting with "SHARE:").
 14→   */
 15→  dialog(): Locator {
 16→    return this.page.locator('.modal-container', { hasText: /SHARE:/ });
 17→  }
 18→
 19→  /**
 20→   * Get the public key input field.
 21→   */
 22→  pubKeyInput(): Locator {
 23→    return this.page.locator('#share-pubkey-input');
 24→  }
 25→
 26→  /**
 27→   * Get the share submit button ("--share").
 28→   */
 29→  submitButton(): Locator {
 30→    return this.page.locator('.share-submit-btn');
 31→  }
 32→
 33→  /**
 34→   * Get the error message element.
 35→   */
 36→  errorMessage(): Locator {
 37→    return this.page.locator('.share-error[role="alert"]');
 38→  }
 39→
 40→  /**
 41→   * Get the success message element.
 42→   */
 43→  successMessage(): Locator {
 44→    return this.page.locator('.share-success[role="status"]');
 45→  }
 46→
 47→  /**
 48→   * Get the progress indicator (shown during folder sharing key re-wrapping).
 49→   */
 50→  progressIndicator(): Locator {
 51→    return this.page.locator('.share-progress[role="status"]');
 52→  }
 53→
 54→  /**
 55→   * Get the recipients list container.
 56→   */
 57→  recipientsList(): Locator {
 58→    return this.page.locator('.share-recipients-list');
 59→  }
 60→
 61→  /**
 62→   * Get all recipient row elements.
 63→   */
 64→  recipientRows(): Locator {
 65→    return this.page.locator('.share-recipient');
 66→  }
 67→
 68→  /**
 69→   * Get a specific recipient row by its truncated key text.
 70→   */
 71→  recipientRow(truncatedKey: string): Locator {
 72→    return this.page.locator('.share-recipient', { hasText: truncatedKey });
 73→  }
 74→
 75→  /**
 76→   * Get the "no recipients yet" empty state.
 77→   */
 78→  recipientsEmpty(): Locator {
 79→    return this.page.locator('.share-recipients-empty');
 80→  }
 81→
 82→  /**
 83→   * Get the recipients loading indicator.
 84→   */
 85→  recipientsLoading(): Locator {
 86→    return this.page.locator('.share-recipients-loading');
 87→  }
 88→
 89→  /**
 90→   * Wait for the share dialog to be visible.
 91→   */
 92→  async waitForOpen(options?: { timeout?: number }): Promise<void> {
 93→    await this.dialog().waitFor({ state: 'visible', ...options });
 94→  }
 95→
 96→  /**
 97→   * Wait for the share dialog to close.
 98→   */
 99→  async waitForClose(options?: { timeout?: number }): Promise<void> {
100→    await this.dialog().waitFor({ state: 'hidden', ...options });
101→  }
102→
103→  /**
104→   * Check if the dialog is visible.
105→   */
106→  async isVisible(): Promise<boolean> {
107→    return await this.dialog().isVisible();
108→  }
109→
110→  /**
111→   * Enter a public key into the input field and submit the share.
112→   * Waits for the success or error message to appear.
113→   */
114→  async shareWithKey(publicKey: string): Promise<void> {
115→    await this.pubKeyInput().fill(publicKey);
116→    await this.submitButton().click();
117→  }
118→
119→  /**
120→   * Wait for success message to appear after sharing.
121→   */
122→  async waitForSuccess(options?: { timeout?: number }): Promise<string> {
123→    await this.successMessage().waitFor({ state: 'visible', ...options });
124→    return (await this.successMessage().textContent()) ?? '';
125→  }
126→
127→  /**
128→   * Wait for error message to appear.
129→   */
130→  async waitForError(options?: { timeout?: number }): Promise<string> {
131→    await this.errorMessage().waitFor({ state: 'visible', ...options });
132→    return (await this.errorMessage().textContent()) ?? '';
133→  }
134→
135→  /**
136→   * Wait for progress indicator to appear and then disappear (folder sharing complete).
137→   */
