  1→/**
  2→ * Share Service - API integration for user-to-user sharing
  3→ *
  4→ * Wraps the generated Orval API client for share endpoints.
  5→ * All sharing operations flow through these functions.
  6→ *
  7→ * Security: The server never sees plaintext keys. All keys are
  8→ * ECIES-wrapped for the recipient before being sent to the API.
  9→ */
 10→
 11→import {
 12→  sharesControllerCreateShare,
 13→  sharesControllerGetReceivedShares,
 14→  sharesControllerGetSentShares,
 15→  sharesControllerLookupUser,
 16→  sharesControllerGetShareKeys,
 17→  sharesControllerAddShareKeys,
 18→  sharesControllerRevokeShare,
 19→  sharesControllerHideShare,
 20→  sharesControllerGetPendingRotations,
 21→  sharesControllerUpdateShareEncryptedKey,
 22→  sharesControllerCompleteRotation,
 23→} from '../api/shares/shares';
 24→
 25→import { wrapKey, bytesToHex, hexToBytes, generateRandomBytes } from '@cipherbox/crypto';
 26→import type { ReceivedShare, SentShare } from '../stores/share.store';
 27→import { useShareStore } from '../stores/share.store';
 28→import type { FolderNode } from '../stores/folder.store';
 29→
 30→/**
 31→ * Fetch all active, non-hidden shares received by the current user.
 32→ * Updates the share store with the results.
 33→ */
 34→export async function fetchReceivedShares(): Promise<ReceivedShare[]> {
 35→  const response = await sharesControllerGetReceivedShares();
 36→
 37→  return response.map((s) => ({
 38→    shareId: s.shareId,
 39→    sharerPublicKey: s.sharerPublicKey,
 40→    itemType: s.itemType as 'folder' | 'file',
 41→    ipnsName: s.ipnsName,
 42→    itemName: s.itemName,
 43→    encryptedKey: s.encryptedKey,
 44→    createdAt: String(s.createdAt),
 45→  }));
 46→}
 47→
 48→/**
 49→ * Fetch all active shares sent by the current user.
 50→ * Updates the share store with the results.
 51→ */
 52→export async function fetchSentShares(): Promise<SentShare[]> {
 53→  const response = await sharesControllerGetSentShares();
 54→
 55→  return response.map((s) => ({
 56→    shareId: s.shareId,
 57→    recipientPublicKey: s.recipientPublicKey,
 58→    itemType: s.itemType as 'folder' | 'file',
 59→    ipnsName: s.ipnsName,
 60→    itemName: s.itemName,
 61→    createdAt: String(s.createdAt),
 62→  }));
 63→}
 64→
 65→/**
 66→ * Check if a CipherBox user exists with the given secp256k1 public key.
 67→ * Returns false if no user is found (404).
 68→ *
 69→ * @param publicKeyHex - Uncompressed secp256k1 public key (0x04... format)
 70→ */
 71→export async function lookupUser(publicKeyHex: string): Promise<boolean> {
 72→  try {
 73→    await sharesControllerLookupUser({ publicKey: publicKeyHex });
 74→    return true;
 75→  } catch (err: unknown) {
 76→    // Only treat 404 as "user not found"; re-throw network/server errors
 77→    if (
 78→      err &&
 79→      typeof err === 'object' &&
 80→      'status' in err &&
 81→      (err as { status: number }).status === 404
 82→    ) {
 83→      return false;
 84→    }
 85→    throw err;
 86→  }
 87→}
 88→
 89→/**
 90→ * Create a new share, sharing an encrypted folder or file with another user.
 91→ *
 92→ * @param params.recipientPublicKey - Recipient's secp256k1 public key
 93→ * @param params.itemType - 'folder' or 'file'
 94→ * @param params.ipnsName - IPNS name of the shared item
 95→ * @param params.itemName - Display name of the shared item
 96→ * @param params.encryptedKey - Hex-encoded ECIES ciphertext of the item key
 97→ * @param params.childKeys - Optional re-wrapped descendant keys
 98→ */
 99→export async function createShare(params: {
100→  recipientPublicKey: string;
101→  itemType: 'folder' | 'file';
102→  ipnsName: string;
103→  itemName: string;
104→  encryptedKey: string;
105→  childKeys?: Array<{ keyType: 'file' | 'folder'; itemId: string; encryptedKey: string }>;
106→}): Promise<{ shareId: string }> {
107→  const response = await sharesControllerCreateShare({
108→    recipientPublicKey: params.recipientPublicKey,
109→    itemType: params.itemType,
110→    ipnsName: params.ipnsName,
111→    itemName: params.itemName,
112→    encryptedKey: params.encryptedKey,
113→    childKeys: params.childKeys?.map((k) => ({
114→      keyType: k.keyType,
115→      itemId: k.itemId,
116→      encryptedKey: k.encryptedKey,
117→    })),
118→  });
119→
120→  return { shareId: response.shareId };
121→}
122→
123→/**
124→ * Revoke a share (soft-delete). Only the sharer can revoke.
125→ * Keys are kept for lazy rotation.
126→ */
127→export async function revokeShare(shareId: string): Promise<void> {
128→  await sharesControllerRevokeShare(shareId);
129→}
130→
131→/**
132→ * Hide a share from the recipient's view. Only the recipient can hide.
133→ */
134→export async function hideShare(shareId: string): Promise<void> {
135→  await sharesControllerHideShare(shareId);
136→}
137→
138→/**
139→ * Get all re-wrapped child keys for a share.
140→ * Accessible by both sharer and recipient.
141→ */
142→export async function fetchShareKeys(
143→  shareId: string
144→): Promise<Array<{ keyType: 'file' | 'folder'; itemId: string; encryptedKey: string }>> {
145→  const response = await sharesControllerGetShareKeys(shareId);
146→
147→  return response.map((k) => ({
148→    keyType: k.keyType as 'file' | 'folder',
149→    itemId: k.itemId,
150→    encryptedKey: k.encryptedKey,
151→  }));
152→}
153→
154→/**
155→ * Add re-wrapped child keys to an existing share. Only the sharer can add keys.
156→ */
157→export async function addShareKeys(
158→  shareId: string,
159→  keys: Array<{ keyType: 'file' | 'folder'; itemId: string; encryptedKey: string }>
160→): Promise<void> {
161→  await sharesControllerAddShareKeys(shareId, {
162→    keys: keys.map((k) => ({
163→      keyType: k.keyType,
164→      itemId: k.itemId,
165→      encryptedKey: k.encryptedKey,
166→    })),
167→  });
168→}
169→
170→/**
171→ * Get sent shares for a specific item (by IPNS name).
172→ * Fetches all sent shares and filters by ipnsName.
173→ * Uses the store cache if available and fresh.
174→ */
175→export async function getSentSharesForItem(ipnsName: string): Promise<SentShare[]> {
176→  const shares = await ensureFreshSentShares();
177→  return shares.filter((s) => s.ipnsName === ipnsName);
178→}
179→
180→// ---------------------------------------------------------------------------
181→// Post-upload / post-create share key propagation
182→// ---------------------------------------------------------------------------
183→
184→/**
185→ * Ensure sent shares cache is fresh (fetched within last 30s).
186→ * Returns the current sent shares array.
187→ */
188→async function ensureFreshSentShares(): Promise<SentShare[]> {
189→  const store = useShareStore.getState();
190→  if (store.lastFetchedAt && Date.now() - store.lastFetchedAt < 30_000) {
191→    return store.sentShares;
192→  }
193→  const shares = await fetchSentShares();
194→  useShareStore.getState().setSentShares(shares);
195→  return shares;
196→}
197→
198→/**
199→ * Check if a folder (by IPNS name) has any active shares.
200→ * Used to decide whether post-upload re-wrapping is needed.
201→ */
202→export async function hasActiveShares(folderIpnsName: string): Promise<boolean> {
203→  const shares = await ensureFreshSentShares();
204→  return shares.some((s) => s.ipnsName === folderIpnsName);
205→}
206→
207→/**
208→ * Find active shares that cover a given folder, including ancestor shares.
209→ * A folder is "covered" if it or any of its ancestor folders is shared.
210→ *
211→ * Walks the ancestor chain and checks each folder's IPNS name against sent shares.
212→ *
213→ * @param folderIpnsName - IPNS name of the current folder
214→ * @param folders - Current folder tree from the folder store
215→ * @param currentFolderId - ID of the current folder in the tree
216→ * @returns Array of sent shares covering this folder (may be from ancestor)
217→ */
218→export async function findCoveringShares(
219→  folderIpnsName: string,
220→  folders: Record<string, FolderNode>,
221→  currentFolderId: string | null
222→): Promise<SentShare[]> {
223→  const shares = await ensureFreshSentShares();
224→  if (shares.length === 0) return [];
225→
226→  // Collect all IPNS names from this folder up to root
227→  const ipnsNames = new Set<string>();
228→  ipnsNames.add(folderIpnsName);
229→
230→  let walkId = currentFolderId;
231→  while (walkId) {
232→    const node = folders[walkId];
233→    if (!node) break;
234→    ipnsNames.add(node.ipnsName);
235→    walkId = node.parentId;
236→  }
237→
238→  return shares.filter((s) => ipnsNames.has(s.ipnsName));
239→}
240→
241→/**
242→ * After adding a file or subfolder to a shared folder, re-wrap the new key
243→ * for all existing share recipients.
244→ *
245→ * This is a fire-and-forget operation -- failures are logged but don't block
246→ * the primary upload/create flow.
247→ *
248→ * IMPORTANT: Callers are responsible for zeroing `newItems[*].plaintextKey`
249→ * after this function completes. This function does NOT zero the keys because
250→ * some callers (e.g., subfolder creation) keep the key alive in the store.
251→ * Use a `finally` block to ensure zeroing even on errors.
252→ *
253→ * @param params.folderIpnsName - IPNS name of the folder being modified
254→ * @param params.folders - Current folder tree from the store
255→ * @param params.currentFolderId - ID of the current folder in the tree
256→ * @param params.newItems - New items whose keys need re-wrapping
257→ */
258→export async function reWrapForRecipients(params: {
259→  folderIpnsName: string;
260→  folders: Record<string, FolderNode>;
261→  currentFolderId: string | null;
262→  newItems: Array<{
263→    keyType: 'file' | 'folder';
264→    itemId: string;
265→    plaintextKey: Uint8Array;
266→  }>;
267→}): Promise<void> {
268→  const coveringShares = await findCoveringShares(
269→    params.folderIpnsName,
270→    params.folders,
271→    params.currentFolderId
272→  );
273→
274→  if (coveringShares.length === 0) return;
275→
276→  // For each share recipient, re-wrap all new item keys
277→  for (const share of coveringShares) {
278→    try {
279→      const recipientPubKey = hexToBytes(
280→        share.recipientPublicKey.startsWith('0x')
281→          ? share.recipientPublicKey.slice(2)
282→          : share.recipientPublicKey
283→      );
284→
285→      const wrappedKeys: Array<{
286→        keyType: 'file' | 'folder';
287→        itemId: string;
288→        encryptedKey: string;
289→      }> = [];
290→
291→      for (const item of params.newItems) {
292→        const wrapped = await wrapKey(item.plaintextKey, recipientPubKey);
293→        wrappedKeys.push({
294→          keyType: item.keyType,
295→          itemId: item.itemId,
296→          encryptedKey: bytesToHex(wrapped),
297→        });
298→      }
299→
300→      // Add the wrapped keys to this share via API
301→      await addShareKeys(share.shareId, wrappedKeys);
302→    } catch (err) {
303→      console.warn(
304→        `[share] Failed to re-wrap keys for recipient ${share.recipientPublicKey.slice(0, 10)}...:`,
305→        err
306→      );
307→    }
308→  }
309→}
310→
311→// ---------------------------------------------------------------------------
312→// Lazy key rotation after revocation
313→// ---------------------------------------------------------------------------
314→
315→/** A revoked share pending key rotation. */
316→export type PendingRotation = {
317→  shareId: string;
318→  recipientPublicKey: string;
319→  itemType: 'folder' | 'file';
320→  ipnsName: string;
321→  itemName: string;
322→  revokedAt: string;
323→};
324→
325→/**
326→ * Fetch revoked shares that are pending key rotation from the server.
327→ * These are shares where revokedAt is set but the share has not been hard-deleted.
328→ */
329→export async function fetchPendingRotations(): Promise<PendingRotation[]> {
330→  const response = await sharesControllerGetPendingRotations();
331→
332→  return response.map((r) => ({
333→    shareId: r.shareId,
334→    recipientPublicKey: r.recipientPublicKey,
335→    itemType: r.itemType as 'folder' | 'file',
336→    ipnsName: r.ipnsName,
337→    itemName: r.itemName,
338→    revokedAt: String(r.revokedAt),
339→  }));
340→}
341→
342→/**
343→ * Check if a folder has pending rotations (revoked shares awaiting key rotation).
344→ * Called before any folder modification.
345→ *
346→ * @param folderIpnsName - IPNS name of the folder being modified
347→ * @returns true if there are revoked shares for this folder that need rotation
348→ */
349→export async function checkPendingRotation(folderIpnsName: string): Promise<boolean> {
350→  const pendingRotations = await fetchPendingRotations();
351→  return pendingRotations.some((r) => r.ipnsName === folderIpnsName);
352→}
353→
354→/**
355→ * Update the encrypted key on a share record after lazy key rotation.
356→ * Re-wraps the new folder key for a remaining (non-revoked) recipient.
357→ */
358→export async function updateShareKey(shareId: string, encryptedKey: string): Promise<void> {
359→  await sharesControllerUpdateShareEncryptedKey(shareId, { encryptedKey });
360→}
361→
362→/**
363→ * Hard-delete a revoked share after rotation is complete.
364→ */
365→export async function completeShareRotation(shareId: string): Promise<void> {
366→  await sharesControllerCompleteRotation(shareId);
367→}
368→
369→/**
370→ * Execute lazy key rotation for a folder.
371→ * Called when a folder modification is about to happen and pending rotations exist.
372→ *
373→ * Protocol:
374→ * 1. Generate new random folderKey
375→ * 2. Re-wrap new folderKey for each REMAINING (non-revoked) active recipient
376→ * 3. Update remaining shares with the new encrypted key
377→ * 4. Hard-delete revoked share records (rotation complete)
378→ * 5. Invalidate share cache
379→ *
380→ * NOTE: The actual folder metadata re-encryption (decrypt with old key, re-encrypt
381→ * with new key, re-publish IPNS) is handled by the caller (folder.service.ts)
382→ * since it has access to the folder's IPNS private key and publishing infrastructure.
383→ *
384→ * @returns The new folderKey for the caller to use
385→ */
386→export async function executeLazyRotation(params: {
387→  folderIpnsName: string;
388→  oldFolderKey: Uint8Array;
389→  ownerPublicKey: Uint8Array;
390→}): Promise<{ newFolderKey: Uint8Array }> {
391→  // 1. Generate new random 32-byte folderKey
392→  const newFolderKey = generateRandomBytes(32);
393→
394→  // 2. Fetch pending rotations and active shares for this folder
395→  const [pendingRotations, activeSentShares] = await Promise.all([
396→    fetchPendingRotations(),
397→    getSentSharesForItem(params.folderIpnsName),
398→  ]);
399→
400→  const revokedForFolder = pendingRotations.filter((r) => r.ipnsName === params.folderIpnsName);
401→  const revokedShareIds = new Set(revokedForFolder.map((r) => r.shareId));
402→
403→  // Active shares that are NOT revoked -- these recipients keep access
404→  const remainingShares = activeSentShares.filter((s) => !revokedShareIds.has(s.shareId));
405→
406→  // 3. Re-wrap new folderKey for each remaining recipient
407→  const reWrapFailures: string[] = [];
408→  for (const share of remainingShares) {
409→    try {
410→      const recipientPubKey = hexToBytes(
411→        share.recipientPublicKey.startsWith('0x')
412→          ? share.recipientPublicKey.slice(2)
413→          : share.recipientPublicKey
414→      );
415→      const wrapped = await wrapKey(newFolderKey, recipientPubKey);
416→      await updateShareKey(share.shareId, bytesToHex(wrapped));
417→    } catch (err) {
418→      console.warn(
419→        `[share] Failed to update share key for remaining recipient ${share.recipientPublicKey.slice(0, 10)}...:`,
420→        err
421→      );
422→      reWrapFailures.push(share.shareId);
423→    }
424→  }
425→
426→  if (reWrapFailures.length > 0) {
427→    newFolderKey.fill(0);
428→    throw new Error(
429→      `Key rotation failed: could not re-wrap key for ${reWrapFailures.length} recipient(s). ` +
430→        'Aborting to prevent inconsistent state.'
431→    );
432→  }
433→
434→  // 4. Hard-delete all revoked shares for this folder
435→  for (const revoked of revokedForFolder) {
436→    try {
437→      await completeShareRotation(revoked.shareId);
438→    } catch (err) {
439→      console.warn(`[share] Failed to complete rotation for share ${revoked.shareId}:`, err);
440→    }
441→  }
442→
443→  // 5. Invalidate the sent shares cache so next check fetches fresh state
444→  useShareStore.getState().setSentShares([]);
445→
446→  return { newFolderKey };
447→}
448→

