133→   * Includes recipient relation for publicKey display.
134→   */
135→  async getSentShares(sharerId: string): Promise<Share[]> {
136→    return this.shareRepo.find({
137→      where: {
138→        sharerId,
139→        revokedAt: IsNull(),
140→      },
141→      relations: ['recipient'],
142→      order: { createdAt: 'DESC' },
143→    });
144→  }
