// Phase 7: Post-Share Mutations
// ============================================

test('7.1 Alice adds a file to the shared folder after sharing', async () => {
  await navigateToFiles(alice);
  await aliceFileList.waitForItemToAppear(sharedFolderName, { timeout: 15000 });

