  1→import { type Page, type Locator } from '@playwright/test';
  2→
  3→/**
  4→ * Page object for SharedFileBrowser component interactions.
  5→ *
  6→ * Encapsulates the ~/shared view: listing received shares, navigating into
  7→ * shared folders, and read-only operations (download, hide).
  8→ */
  9→export class SharedFileBrowserPage {
 10→  constructor(private readonly page: Page) {}
 11→
 12→  /**
 13→   * Get the shared browser container.
 14→   */
 15→  container(): Locator {
 16→    return this.page.locator('.shared-browser');
 17→  }
 18→
 19→  /**
 20→   * Get the empty state element (no shared items).
 21→   */
 22→  emptyState(): Locator {
 23→    return this.page.getByTestId('shared-empty-state');
 24→  }
 25→
 26→  /**
 27→   * Get the empty folder state element.
 28→   */
 29→  emptyFolderState(): Locator {
 30→    return this.page.getByTestId('shared-empty-folder');
 31→  }
 32→
 33→  /**
 34→   * Get the breadcrumbs navigation.
 35→   */
 36→  breadcrumbs(): Locator {
 37→    return this.page.getByTestId('breadcrumbs');
 38→  }
 39→
 40→  /**
 41→   * Get the loading indicator.
 42→   */
 43→  loadingIndicator(): Locator {
 44→    return this.page.locator('.file-browser-loading');
 45→  }
 46→
 47→  /**
 48→   * Get the error alert.
 49→   */
 50→  errorAlert(): Locator {
 51→    return this.page.locator('.shared-error[role="alert"]');
 52→  }
 53→
 54→  /**
 55→   * Get all shared item rows (top-level list view).
 56→   */
 57→  sharedItemRows(): Locator {
 58→    return this.page.locator('.shared-list-row');
 59→  }
 60→
 61→  /**
 62→   * Get a shared item row by name.
 63→   */
 64→  getSharedItem(name: string): Locator {
 65→    return this.page.locator('.shared-list-row', { hasText: name });
 66→  }
 67→
 68→  /**
 69→   * Get the [RO] badge on a shared item.
 70→   */
 71→  getReadOnlyBadge(name: string): Locator {
 72→    return this.getSharedItem(name).locator('.shared-ro-badge');
 73→  }
 74→
 75→  /**
 76→   * Get the "SHARED BY" text for a shared item.
 77→   */
 78→  async getSharedBy(name: string): Promise<string> {
 79→    const row = this.getSharedItem(name);
 80→    return (await row.locator('.shared-by-cell').textContent()) ?? '';
 81→  }
 82→
 83→  /**
 84→   * Get all file/folder rows in a shared folder view (not the shared list).
 85→   */
 86→  folderItemRows(): Locator {
 87→    return this.page.locator('.file-list-row:not(.file-list-row--parent):not(.shared-list-row)');
 88→  }
 89→
 90→  /**
 91→   * Get a folder item row by name (inside a shared folder).
 92→   */
 93→  getFolderItem(name: string): Locator {
 94→    return this.page.locator('.file-list-row:not(.file-list-row--parent):not(.shared-list-row)', {
 95→      hasText: name,
 96→    });
 97→  }
 98→
 99→  /**
100→   * Get the parent directory row ([..] PARENT_DIR).
101→   */
102→  parentDirRow(): Locator {
103→    return this.page.locator('.file-list-row--parent');
104→  }
105→
106→  /**
107→   * Wait for the shared items list to load.
108→   */
109→  async waitForLoaded(options?: { timeout?: number }): Promise<void> {
110→    await this.loadingIndicator().waitFor({ state: 'hidden', ...options });
111→  }
112→
113→  /**
114→   * Check if the shared list is empty (no shared items).
115→   */
116→  async isEmpty(): Promise<boolean> {
117→    return await this.emptyState().isVisible();
118→  }
119→
120→  /**
121→   * Get the count of shared items in the list.
122→   */
123→  async getSharedItemCount(): Promise<number> {
124→    return await this.sharedItemRows().count();
125→  }
126→
127→  /**
128→   * Get all shared item names.
129→   */
130→  async getSharedItemNames(): Promise<string[]> {
131→    const rows = this.sharedItemRows();
132→    const count = await rows.count();
133→    const names: string[] = [];
134→    for (let i = 0; i < count; i++) {
135→      const nameText = await rows.nth(i).locator('.file-name').textContent();
136→      if (nameText) names.push(nameText.trim());
137→    }
138→    return names;
139→  }
140→
141→  /**
142→   * Click a shared item to open it (navigate into folder or download file).
143→   */
144→  async openSharedItem(name: string): Promise<void> {
145→    await this.getSharedItem(name).click();
146→  }
147→
148→  /**
149→   * Right-click a shared item to open context menu.
150→   */
151→  async rightClickSharedItem(name: string): Promise<void> {
152→    await this.getSharedItem(name).click({ button: 'right' });
153→  }
154→
155→  /**
156→   * Double-click a shared item.
157→   */
158→  async doubleClickSharedItem(name: string): Promise<void> {
159→    await this.getSharedItem(name).dblclick();
160→  }
161→
162→  /**
163→   * Wait for a shared item to appear in the list.
164→   */
165→  async waitForSharedItem(name: string, options?: { timeout?: number }): Promise<void> {
166→    await this.getSharedItem(name).waitFor({ state: 'visible', ...options });
167→  }
168→
169→  /**
170→   * Wait for a shared item to disappear from the list.
171→   */
172→  async waitForSharedItemGone(name: string, options?: { timeout?: number }): Promise<void> {
173→    await this.getSharedItem(name).waitFor({ state: 'hidden', ...options });
174→  }
175→
176→  /**
177→   * Navigate into a shared folder by clicking on it.
178→   * Waits for the folder contents to load.
179→   */
180→  async navigateIntoFolder(name: string): Promise<void> {
181→    await this.openSharedItem(name);
182→    // Wait for folder view to appear (parent dir row indicates we're inside a folder)
183→    await this.parentDirRow().waitFor({ state: 'visible', timeout: 30000 });
184→  }
185→
186→  /**
187→   * Navigate up to parent (click [..] PARENT_DIR).
188→   */
189→  async navigateUp(): Promise<void> {
190→    await this.parentDirRow().click();
191→  }
192→
193→  /**
194→   * Navigate back to the shared root by clicking "shared" in breadcrumbs.
195→   */
196→  async navigateToRoot(): Promise<void> {
197→    await this.breadcrumbs().locator('button', { hasText: /^shared$/ }).click();
198→  }
199→
200→  /**
201→   * Get the breadcrumb path text.
202→   */
203→  async getBreadcrumbPath(): Promise<string> {
204→    return (await this.breadcrumbs().textContent()) ?? '';
205→  }
206→
207→  /**
208→   * Get the count of items in the current folder view.
209→   */
210→  async getFolderItemCount(): Promise<number> {
211→    return await this.folderItemRows().count();
212→  }
213→
214→  /**
