  1→/**
  2→ * SharedFileBrowser -- Read-only file browser for shared content.
  3→ *
  4→ * Shows items shared with the current user at ~/shared.
  5→ * - Top-level: flat list of received shares with SHARED BY column
  6→ * - Inside folder: standard file list with [RO] badges, no write actions
  7→ * - Context menu: Download, Preview, Details, Hide only
  8→ * - No upload, no create folder, no rename, no move, no delete
  9→ */
 10→
 11→import { useState, useCallback, type MouseEvent } from 'react';
 12→import type { FolderChild, FilePointer } from '@cipherbox/crypto';
 13→import { useSharedNavigation, type SharedListItem } from '../../hooks/useSharedNavigation';
 14→import { useContextMenu } from '../../hooks/useContextMenu';
 15→import { ContextMenu } from './ContextMenu';
 16→import { DetailsDialog } from './DetailsDialog';
 17→import { ImagePreviewDialog } from './ImagePreviewDialog';
 18→import { PdfPreviewDialog } from './PdfPreviewDialog';
 19→import { AudioPlayerDialog } from './AudioPlayerDialog';
 20→import { VideoPlayerDialog } from './VideoPlayerDialog';
 21→import '../../styles/shared-browser.css';
 22→
 23→/** Extensions recognized as previewable image files. */
 24→const IMAGE_EXTENSIONS = new Set([
 25→  '.png',
 26→  '.jpg',
 27→  '.jpeg',
 28→  '.gif',
 29→  '.webp',
 30→  '.svg',
 31→  '.bmp',
 32→  '.ico',
 33→  '.avif',
 34→]);
 35→
 36→/** Extensions recognized as PDF files. */
 37→const PDF_EXTENSIONS = new Set(['.pdf']);
 38→
 39→/** Extensions recognized as playable audio files. */
 40→const AUDIO_EXTENSIONS = new Set(['.mp3', '.wav', '.ogg', '.m4a', '.flac']);
 41→
 42→/** Extensions recognized as playable video files. */
 43→const VIDEO_EXTENSIONS = new Set(['.mp4', '.webm', '.mov', '.mkv']);
 44→
 45→function isImageFile(name: string): boolean {
 46→  const lower = name.toLowerCase();
 47→  const lastDot = lower.lastIndexOf('.');
 48→  if (lastDot === -1) return false;
 49→  return IMAGE_EXTENSIONS.has(lower.slice(lastDot));
 50→}
 51→
 52→function isPdfFile(name: string): boolean {
 53→  const lower = name.toLowerCase();
 54→  const lastDot = lower.lastIndexOf('.');
 55→  if (lastDot === -1) return false;
 56→  return PDF_EXTENSIONS.has(lower.slice(lastDot));
 57→}
 58→
 59→function isAudioFile(name: string): boolean {
 60→  const lower = name.toLowerCase();
 61→  const lastDot = lower.lastIndexOf('.');
 62→  if (lastDot === -1) return false;
 63→  return AUDIO_EXTENSIONS.has(lower.slice(lastDot));
 64→}
 65→
 66→function isVideoFile(name: string): boolean {
 67→  const lower = name.toLowerCase();
 68→  const lastDot = lower.lastIndexOf('.');
 69→  if (lastDot === -1) return false;
 70→  return VIDEO_EXTENSIONS.has(lower.slice(lastDot));
 71→}
 72→
 73→function isPreviewableFile(name: string): boolean {
 74→  return isImageFile(name) || isPdfFile(name) || isAudioFile(name) || isVideoFile(name);
 75→}
 76→
 77→function isFilePointer(item: FolderChild): item is FilePointer {
 78→  return item.type === 'file';
 79→}
 80→
 81→/**
 82→ * Truncate a public key for display: 0x{first4}...{last4}
 83→ */
 84→function truncatePubkey(pubkey: string): string {
 85→  if (pubkey.length <= 12) return pubkey;
 86→  const hex = pubkey.startsWith('0x') ? pubkey.slice(2) : pubkey;
 87→  return `0x${hex.slice(0, 4)}...${hex.slice(-4)}`;
 88→}
 89→
 90→/**
 91→ * Sort items: folders first, then files, both alphabetically.
 92→ */
 93→function sortItems(items: FolderChild[]): FolderChild[] {
 94→  return [...items].sort((a, b) => {
 95→    if (a.type === 'folder' && b.type !== 'folder') return -1;
 96→    if (a.type !== 'folder' && b.type === 'folder') return 1;
 97→    return a.name.localeCompare(b.name, undefined, { sensitivity: 'base' });
 98→  });
 99→}
100→
101→type DialogState = {
102→  open: boolean;
103→  item: FolderChild | null;
104→};
105→
106→/**
107→ * Terminal-style ASCII art for shared empty state.
108→ */
109→const sharedEmptyArt = `\u250C\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2510
110→\u2502 $ ls ~/shared        \u2502
111→\u2502 total 0              \u2502
112→\u2502 $ \u2588                  \u2502
113→\u2514\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2500\u2518`;
114→
115→export function SharedFileBrowser() {
116→  const {
117→    currentView,
118→    sharedItems,
119→    folderChildren,
120→    folderKey,
121→    breadcrumbs,
122→    isLoading,
123→    error,
124→    navigateToShare,
125→    navigateToSubfolder,
126→    navigateUp,
127→    navigateToRoot,
128→    navigateToBreadcrumb,
129→    downloadSharedFile,
130→    hideSharedItem,
131→  } = useSharedNavigation();
132→
133→  const contextMenu = useContextMenu();
134→
135→  // Dialog states
136→  const [detailsDialog, setDetailsDialog] = useState<DialogState>({ open: false, item: null });
137→  const [imagePreviewDialog, setImagePreviewDialog] = useState<DialogState>({
138→    open: false,
139→    item: null,
140→  });
141→  const [pdfPreviewDialog, setPdfPreviewDialog] = useState<DialogState>({
142→    open: false,
143→    item: null,
144→  });
145→  const [audioPlayerDialog, setAudioPlayerDialog] = useState<DialogState>({
146→    open: false,
147→    item: null,
148→  });
149→  const [videoPlayerDialog, setVideoPlayerDialog] = useState<DialogState>({
150→    open: false,
151→    item: null,
152→  });
153→
154→  // Track which shared item the context menu is for (for hide action)
155→  const [contextShareId, setContextShareId] = useState<string | null>(null);
156→
157→  // Context menu handlers for folder view
158→  const handleContextMenu = useCallback(
159→    (event: MouseEvent, item: FolderChild) => {
160→      contextMenu.show(event, item);
161→      setContextShareId(null);
162→    },
163→    [contextMenu]
164→  );
165→
166→  // Context menu for top-level shared items
167→  const handleSharedItemContextMenu = useCallback(
168→    (event: MouseEvent, item: FolderChild, shareId: string) => {
169→      contextMenu.show(event, item);
170→      setContextShareId(shareId);
171→    },
172→    [contextMenu]
173→  );
174→
175→  const handleDownload = useCallback(async () => {
176→    const item = contextMenu.item;
177→    if (!item || !isFilePointer(item)) return;
178→
179→    // In list view, context menu download needs to use navigateToShare
180→    // because downloadSharedFile requires currentShareId/folderKey (null in list view)
181→    if (currentView === 'list' && contextShareId) {
182→      await navigateToShare(contextShareId);
183→      return;
184→    }
185→
186→    await downloadSharedFile(item);
187→  }, [contextMenu.item, downloadSharedFile, currentView, contextShareId, navigateToShare]);
188→
189→  const handleDetailsClick = useCallback(() => {
190→    if (contextMenu.item) {
191→      setDetailsDialog({ open: true, item: contextMenu.item });
192→    }
193→  }, [contextMenu.item]);
194→
195→  const handlePreviewClick = useCallback(() => {
196→    const item = contextMenu.item;
197→    if (!item || !isFilePointer(item)) return;
198→    const name = item.name;
199→    if (isImageFile(name)) {
200→      setImagePreviewDialog({ open: true, item });
201→    } else if (isPdfFile(name)) {
202→      setPdfPreviewDialog({ open: true, item });
203→    } else if (isAudioFile(name)) {
204→      setAudioPlayerDialog({ open: true, item });
205→    } else if (isVideoFile(name)) {
206→      setVideoPlayerDialog({ open: true, item });
207→    }
208→  }, [contextMenu.item]);
209→
210→  const handleHide = useCallback(async () => {
211→    if (contextShareId) {
212→      await hideSharedItem(contextShareId);
213→    }
214→  }, [contextShareId, hideSharedItem]);
215→
216→  // Render top-level shared list
217→  if (currentView === 'list') {
218→    return (
219→      <div className="file-browser-content shared-browser">
220→        {/* Toolbar with breadcrumbs */}
221→        <div className="file-browser-toolbar">
222→          <nav className="breadcrumb-nav" aria-label="Current location" data-testid="breadcrumbs">
223→            <span className="breadcrumb-prefix">~</span>
224→            <span className="breadcrumb-separator">/</span>
225→            <span className="breadcrumb-item breadcrumb-item--current" aria-current="page">
226→              shared
227→            </span>
228→          </nav>
229→        </div>
230→
231→        {/* Loading state */}
232→        {isLoading && (
233→          <div className="file-browser-loading">
234→            <span className="file-browser-loading-spinner">{'// loading shared items...'}</span>
235→          </div>
236→        )}
237→
238→        {/* Error state */}
239→        {error && (
240→          <div className="shared-error" role="alert">
241→            <span>
242→              {'// ERROR: '}
243→              {error}
244→            </span>
245→          </div>
246→        )}
247→
248→        {/* Empty state */}
249→        {!isLoading && sharedItems.length === 0 && (
250→          <div className="empty-state" data-testid="shared-empty-state">
251→            <div className="empty-state-content">
252→              <pre className="empty-state-ascii" aria-hidden="true">
253→                {sharedEmptyArt}
254→              </pre>
255→              <p className="empty-state-text">{'// NO SHARED ITEMS'}</p>
256→              <p className="empty-state-hint">
257→                ask others to share files using your public key from Settings
258→              </p>
259→            </div>
260→          </div>
261→        )}
262→
263→        {/* Shared items list */}
264→        {!isLoading && sharedItems.length > 0 && (
265→          <div className="file-list" role="grid">
266→            {/* Header row */}
267→            <div className="file-list-header" role="row">
268→              <div className="file-list-header-name" role="columnheader">
269→                [NAME]
270→              </div>
271→              <div className="file-list-header-shared-by" role="columnheader">
272→                [SHARED BY]
273→              </div>
274→              <div className="file-list-header-date" role="columnheader">
275→                [DATE]
276→              </div>
277→            </div>
278→
279→            {/* Item rows */}
280→            <div className="file-list-body" role="rowgroup">
281→              {sharedItems.map((sharedItem) => (
282→                <SharedListRow
283→                  key={sharedItem.share.shareId}
284→                  sharedItem={sharedItem}
285→                  onOpen={() => navigateToShare(sharedItem.share.shareId)}
286→                  onContextMenu={(e) => {
287→                    // Create a synthetic FolderChild for context menu
288→                    const fakeItem: FolderChild =
289→                      sharedItem.share.itemType === 'folder'
290→                        ? {
291→                            type: 'folder' as const,
292→                            id: sharedItem.share.shareId,
293→                            name: sharedItem.share.itemName,
294→                            ipnsName: sharedItem.share.ipnsName,
295→                            ipnsPrivateKeyEncrypted: '',
296→                            folderKeyEncrypted: '',
297→                            createdAt: Date.parse(sharedItem.share.createdAt),
298→                            modifiedAt: Date.parse(sharedItem.share.createdAt),
299→                          }
300→                        : {
301→                            type: 'file' as const,
302→                            id: sharedItem.share.shareId,
303→                            name: sharedItem.share.itemName,
304→                            fileMetaIpnsName: sharedItem.share.ipnsName,
305→                            createdAt: Date.parse(sharedItem.share.createdAt),
306→                            modifiedAt: Date.parse(sharedItem.share.createdAt),
307→                          };
308→                    handleSharedItemContextMenu(e, fakeItem, sharedItem.share.shareId);
309→                  }}
310→                />
311→              ))}
312→            </div>
313→          </div>
314→        )}
315→
316→        {/* Read-only context menu */}
317→        {contextMenu.visible && contextMenu.item && (
318→          <ContextMenu
319→            x={contextMenu.x}
320→            y={contextMenu.y}
321→            item={contextMenu.item}
322→            selectedCount={1}
323→            onClose={contextMenu.hide}
324→            onDownload={contextMenu.item.type === 'file' ? handleDownload : undefined}
325→            onRename={() => {}}
326→            onDelete={() => {}}
327→            onDetails={handleDetailsClick}
328→            readOnly
329→            onHide={contextShareId ? handleHide : undefined}
330→          />
331→        )}
332→
333→        {/* Details dialog */}
334→        <DetailsDialog
335→          open={detailsDialog.open}
336→          onClose={() => setDetailsDialog({ open: false, item: null })}
337→          item={detailsDialog.item}
338→          folderKey={null}
339→          parentFolderId=""
340→        />
341→      </div>
342→    );
343→  }
344→
345→  // Render folder view (inside a shared folder)
346→  const sortedChildren = sortItems(folderChildren);
347→  const hasChildren = folderChildren.length > 0;
348→
349→  return (
350→    <div className="file-browser-content shared-browser">
351→      {/* Toolbar with breadcrumbs */}
352→      <div className="file-browser-toolbar">
353→        <nav className="breadcrumb-nav" aria-label="Current location" data-testid="breadcrumbs">
354→          <span className="breadcrumb-prefix">~</span>
355→          <span className="breadcrumb-separator">/</span>
356→          <button type="button" className="breadcrumb-item" onClick={navigateToRoot}>
357→            shared
358→          </button>
359→          {breadcrumbs.map((crumb, index) => {
360→            const isLast = index === breadcrumbs.length - 1;
361→            return (
362→              <span key={crumb.id}>
363→                <span className="breadcrumb-separator">/</span>
364→                {isLast ? (
365→                  <span className="breadcrumb-item breadcrumb-item--current" aria-current="page">
366→                    {crumb.name.toLowerCase()}
367→                  </span>
368→                ) : (
369→                  <button
370→                    type="button"
371→                    className="breadcrumb-item"
372→                    onClick={() => navigateToBreadcrumb(breadcrumbs.indexOf(crumb))}
373→                  >
374→                    {crumb.name.toLowerCase()}
375→                  </button>
376→                )}
377→              </span>
378→            );
379→          })}
380→        </nav>
381→      </div>
382→
383→      {/* Loading state */}
384→      {isLoading && (
385→        <div className="file-browser-loading">
386→          <span className="file-browser-loading-spinner">{'// loading...'}</span>
387→        </div>
388→      )}
389→
390→      {/* Error state */}
391→      {error && (
392→        <div className="shared-error" role="alert">
393→          <span>
394→            {'// ERROR: '}
395→            {error}
396→          </span>
397→        </div>
398→      )}
399→
400→      {/* File list */}
401→      {!isLoading && hasChildren && (
402→        <div className="file-list" role="grid">
403→          {/* Header row */}
404→          <div className="file-list-header" role="row">
405→            <div className="file-list-header-name" role="columnheader">
406→              [NAME]
407→            </div>
408→            <div className="file-list-header-size" role="columnheader">
409→              [SIZE]
410→            </div>
411→            <div className="file-list-header-date" role="columnheader">
412→              [MODIFIED]
413→            </div>
414→          </div>
415→
416→          {/* [..] PARENT_DIR row */}
417→          <div className="file-list-body" role="rowgroup">
418→            <div
419→              className="file-list-row file-list-row--parent"
420→              role="row"
421→              tabIndex={0}
422→              onClick={navigateUp}
423→              onKeyDown={(e) => {
424→                if (e.key === 'Enter' || e.key === ' ') {
425→                  e.preventDefault();
426→                  navigateUp();
427→                }
428→              }}
429→              onDoubleClick={navigateUp}
430→            >
431→              <div className="file-list-cell file-list-cell-name" role="gridcell">
432→                <span className="file-icon">{'<-'}</span>
433→                <span className="file-name">[..] PARENT_DIR</span>
434→              </div>
435→              <div className="file-list-cell file-list-cell-size" role="gridcell">
436→                --
437→              </div>
438→              <div className="file-list-cell file-list-cell-date" role="gridcell">
439→                --
440→              </div>
441→            </div>
442→
443→            {/* File/folder rows */}
444→            {sortedChildren.map((item) => (
445→              <SharedFolderRow
446→                key={item.id}
447→                item={item}
448→                onOpen={() => {
449→                  if (item.type === 'folder') {
450→                    navigateToSubfolder(item.id, item.name);
451→                  }
452→                }}
453→                onContextMenu={(e) => handleContextMenu(e, item)}
454→                onDoubleClick={() => {
455→                  if (item.type === 'folder') {
456→                    navigateToSubfolder(item.id, item.name);
457→                  } else if (isFilePointer(item)) {
458→                    downloadSharedFile(item);
459→                  }
460→                }}
461→              />
462→            ))}
463→          </div>
464→        </div>
465→      )}
466→
467→      {/* Empty folder */}
468→      {!isLoading && !hasChildren && !error && (
469→        <div className="empty-state" data-testid="shared-empty-folder">
470→          <div className="empty-state-content">
471→            <pre className="empty-state-ascii" aria-hidden="true">
472→              {sharedEmptyArt}
473→            </pre>
474→            <p className="empty-state-text">{'// EMPTY SHARED FOLDER'}</p>
475→            <p className="empty-state-hint">this shared folder has no contents</p>
476→          </div>
477→        </div>
478→      )}
479→
480→      {/* Read-only context menu */}
481→      {contextMenu.visible && contextMenu.item && (
482→        <ContextMenu
483→          x={contextMenu.x}
484→          y={contextMenu.y}
485→          item={contextMenu.item}
486→          selectedCount={1}
487→          onClose={contextMenu.hide}
488→          onDownload={contextMenu.item.type === 'file' ? handleDownload : undefined}
489→          onPreview={
490→            contextMenu.item.type === 'file' && isPreviewableFile(contextMenu.item.name)
491→              ? handlePreviewClick
492→              : undefined
493→          }
494→          onRename={() => {}}
495→          onDelete={() => {}}
496→          onDetails={handleDetailsClick}
497→          readOnly
498→        />
499→      )}
500→
501→      {/* Details dialog */}
502→      <DetailsDialog
503→        open={detailsDialog.open}
504→        onClose={() => setDetailsDialog({ open: false, item: null })}
505→        item={detailsDialog.item}
506→        folderKey={folderKey}
507→        parentFolderId=""
508→      />
509→
510→      {/* Image preview dialog */}
511→      <ImagePreviewDialog
512→        open={imagePreviewDialog.open}
513→        onClose={() => setImagePreviewDialog({ open: false, item: null })}
514→        item={
515→          imagePreviewDialog.item && isFilePointer(imagePreviewDialog.item)
516→            ? imagePreviewDialog.item
517→            : null
518→        }
519→        folderKey={folderKey}
520→      />
521→
522→      {/* PDF preview dialog */}
523→      <PdfPreviewDialog
524→        open={pdfPreviewDialog.open}
