  1→/**
  2→ * useSharedNavigation -- Navigation hook for browsing shared content.
  3→ *
  4→ * Similar to useFolderNavigation but with a different key source:
  5→ * - Top level: shares received via share records
  6→ * - Folder browsing: re-wrapped keys from share_keys table
  7→ * - File download: re-wrapped fileKey from share_keys table
  8→ *
  9→ * Security: All keys are ECIES-wrapped for the current user.
 10→ * The server never sees plaintext keys.
 11→ */
 12→
 13→import { useState, useCallback, useEffect, useRef } from 'react';
 14→import {
 15→  unwrapKey,
 16→  hexToBytes,
 17→  decryptFolderMetadata,
 18→  decryptFileMetadata,
 19→  type FolderChild,
 20→  type FolderEntry,
 21→  type FilePointer,
 22→  type EncryptedFolderMetadata,
 23→  type EncryptedFileMetadata,
 24→} from '@cipherbox/crypto';
 25→import { useAuthStore } from '../stores/auth.store';
 26→import { useShareStore, type ReceivedShare } from '../stores/share.store';
 27→import { fetchReceivedShares, fetchShareKeys, hideShare } from '../services/share.service';
 28→import { resolveIpnsRecord } from '../services/ipns.service';
 29→import { fetchFromIpfs } from '../lib/api/ipfs';
 30→import { downloadFile, triggerBrowserDownload } from '../services/download.service';
 31→import { useDownloadStore } from '../stores/download.store';
 32→
 33→/**
 34→ * Breadcrumb entry for shared navigation.
 35→ */
 36→export type SharedBreadcrumb = {
 37→  id: string;
 38→  name: string;
 39→};
 40→
 41→/**
 42→ * A shared item displayed in the top-level shared list.
 43→ * Extends the original FolderChild with sharing metadata.
 44→ */
 45→export type SharedListItem = {
 46→  share: ReceivedShare;
 47→  /** Resolved folder children (for folders), or null (for files / unresolved) */
 48→  children: FolderChild[] | null;
 49→  /** Folder key for this shared item (decrypted from share record) */
 50→  folderKey: Uint8Array | null;
 51→};
 52→
 53→type SharedView = 'list' | 'folder';
 54→
 55→type UseSharedNavigationReturn = {
 56→  currentView: SharedView;
 57→  currentShareId: string | null;
 58→  sharedItems: SharedListItem[];
 59→  folderChildren: FolderChild[];
 60→  folderKey: Uint8Array | null;
 61→  breadcrumbs: SharedBreadcrumb[];
 62→  isLoading: boolean;
 63→  error: string | null;
 64→  navigateToShare: (shareId: string) => Promise<void>;
 65→  navigateToSubfolder: (folderId: string, folderName: string) => Promise<void>;
 66→  navigateUp: () => void;
 67→  navigateToRoot: () => void;
 68→  navigateToBreadcrumb: (crumbIndex: number) => void;
 69→  downloadSharedFile: (item: FilePointer) => Promise<void>;
 70→  hideSharedItem: (shareId: string) => Promise<void>;
 71→};
 72→
 73→/**
 74→ * Hook for browsing shared content.
 75→ *
 76→ * Manages the "Shared with me" browsing experience.
 77→ * Top-level view shows received shares as a flat list.
 78→ * Clicking a shared folder navigates into it using re-wrapped keys.
 79→ */
 80→export function useSharedNavigation(): UseSharedNavigationReturn {
 81→  const [currentView, setCurrentView] = useState<SharedView>('list');
 82→  const [currentShareId, setCurrentShareId] = useState<string | null>(null);
 83→  const [sharedItems, setSharedItems] = useState<SharedListItem[]>([]);
 84→  const [folderChildren, setFolderChildren] = useState<FolderChild[]>([]);
 85→  const [folderKey, setFolderKey] = useState<Uint8Array | null>(null);
 86→  const [breadcrumbs, setBreadcrumbs] = useState<SharedBreadcrumb[]>([]);
 87→  const [isLoading, setIsLoading] = useState(true);
 88→  const [error, setError] = useState<string | null>(null);
 89→
 90→  // Navigation stack for folder browsing within a share
 91→  const navStackRef = useRef<
 92→    Array<{
 93→      folderId: string;
 94→      folderName: string;
 95→      children: FolderChild[];
 96→      folderKey: Uint8Array;
 97→    }>
 98→  >([]);
 99→
100→  // Cache share keys per shareId with TTL to avoid refetching
101→  const shareKeysCache = useRef<
102→    Map<
103→      string,
104→      {
105→        keys: Array<{ keyType: 'file' | 'folder'; itemId: string; encryptedKey: string }>;
106→        fetchedAt: number;
107→      }
108→    >
109→  >(new Map());
110→
111→  /**
112→   * Load received shares on mount.
113→   */
114→  useEffect(() => {
115→    let cancelled = false;
116→
117→    async function loadShares() {
118→      setIsLoading(true);
119→      setError(null);
120→
121→      try {
122→        const shares = await fetchReceivedShares();
123→        if (cancelled) return;
124→
125→        useShareStore.getState().setReceivedShares(shares);
126→
127→        const items: SharedListItem[] = shares.map((share) => ({
128→          share,
129→          children: null,
130→          folderKey: null,
131→        }));
132→
133→        setSharedItems(items);
134→      } catch (err) {
135→        if (cancelled) return;
136→        console.error('Failed to load shared items:', err);
137→        setError('Failed to load shared items');
138→      } finally {
139→        if (!cancelled) {
140→          setIsLoading(false);
141→        }
142→      }
143→    }
144→
145→    loadShares();
146→    return () => {
147→      cancelled = true;
148→      // Zero all decrypted folder keys on unmount
149→      setFolderKey((prev) => {
150→        if (prev) prev.fill(0);
151→        return null;
152→      });
153→      for (const entry of navStackRef.current) {
154→        entry.folderKey.fill(0);
155→      }
156→      navStackRef.current = [];
157→    };
158→  }, []);
159→
160→  /** Cache TTL for share keys (60 seconds). */
161→  const SHARE_KEYS_CACHE_TTL = 60_000;
162→
163→  /**
164→   * Get share keys for a share, with TTL-based caching.
165→   */
166→  const getShareKeys = useCallback(async (shareId: string) => {
167→    const cached = shareKeysCache.current.get(shareId);
168→    if (cached && Date.now() - cached.fetchedAt < SHARE_KEYS_CACHE_TTL) {
169→      return cached.keys;
170→    }
171→
172→    const keys = await fetchShareKeys(shareId);
173→    shareKeysCache.current.set(shareId, { keys, fetchedAt: Date.now() });
174→    return keys;
175→  }, []);
176→
177→  /**
178→   * Navigate into a shared folder from the top-level list.
179→   */
180→  const navigateToShare = useCallback(
181→    async (shareId: string) => {
182→      const auth = useAuthStore.getState();
183→      if (!auth.vaultKeypair) {
184→        setError('No keypair available');
185→        return;
186→      }
187→
188→      const shareItem = sharedItems.find((s) => s.share.shareId === shareId);
189→      if (!shareItem) return;
190→
191→      const share = shareItem.share;
192→
193→      setIsLoading(true);
194→      setError(null);
195→
196→      try {
197→        // Unwrap the shared item's key with our private key
198→        const itemKey = await unwrapKey(
199→          hexToBytes(share.encryptedKey),
200→          auth.vaultKeypair.privateKey
201→        );
202→
203→        if (share.itemType === 'folder') {
204→          let folderKeyStored = false;
205→          try {
206→            // Resolve folder IPNS to get metadata
207→            const resolved = await resolveIpnsRecord(share.ipnsName);
208→            if (!resolved) {
209→              throw new Error('Could not resolve shared folder IPNS');
210→            }
211→
212→            // Fetch and decrypt folder metadata
213→            const encryptedBytes = await fetchFromIpfs(resolved.cid);
214→            const encryptedJson = new TextDecoder().decode(encryptedBytes);
215→            const encrypted: EncryptedFolderMetadata = JSON.parse(encryptedJson);
216→            const metadata = await decryptFolderMetadata(encrypted, itemKey);
217→
218→            // Set folder state
219→            setCurrentView('folder');
220→            setCurrentShareId(shareId);
221→            setFolderChildren(metadata.children ?? []);
222→            setFolderKey(itemKey);
223→            folderKeyStored = true;
224→            setBreadcrumbs([{ id: shareId, name: share.itemName }]);
225→            navStackRef.current = [];
226→          } finally {
227→            if (!folderKeyStored) itemKey.fill(0);
228→          }
229→        } else {
230→          // For files, trigger download directly
231→          // itemKey is the parent folder key; downloadSharedFileFromShare unwraps its own copy
232→          itemKey.fill(0);
233→          await downloadSharedFileFromShare(share, auth.vaultKeypair.privateKey);
234→        }
235→      } catch (err) {
236→        console.error('Failed to navigate to shared item:', err);
237→        setError('Failed to open shared item');
238→      } finally {
239→        setIsLoading(false);
240→      }
241→    },
242→    [sharedItems]
243→  );
244→
245→  /**
246→   * Navigate into a subfolder within a shared folder.
247→   */
248→  const navigateToSubfolder = useCallback(
249→    async (folderId: string, folderName: string) => {
250→      const auth = useAuthStore.getState();
251→      if (!auth.vaultKeypair || !currentShareId) return;
252→
253→      setIsLoading(true);
254→      setError(null);
255→
256→      try {
257→        // Get re-wrapped keys for this share
258→        const keys = await getShareKeys(currentShareId);
259→
260→        // Find the re-wrapped folder key for this subfolder
261→        const keyRecord = keys.find((k) => k.keyType === 'folder' && k.itemId === folderId);
262→        if (!keyRecord) {
263→          throw new Error('No key available for this subfolder');
264→        }
265→
266→        // Unwrap the subfolder key
267→        const subfolderKey = await unwrapKey(
268→          hexToBytes(keyRecord.encryptedKey),
269→          auth.vaultKeypair.privateKey
270→        );
271→        let subfolderKeyStored = false;
272→
273→        try {
274→          // Find the subfolder entry to get its IPNS name
275→          const folderEntry = folderChildren.find(
276→            (c): c is FolderEntry => c.type === 'folder' && c.id === folderId
277→          );
278→          if (!folderEntry) {
279→            throw new Error('Subfolder not found in current children');
280→          }
281→
282→          // Resolve subfolder IPNS
283→          const resolved = await resolveIpnsRecord(folderEntry.ipnsName);
284→          if (!resolved) {
285→            throw new Error('Could not resolve subfolder IPNS');
286→          }
287→
288→          // Fetch and decrypt subfolder metadata
289→          const encryptedBytes = await fetchFromIpfs(resolved.cid);
290→          const encryptedJson = new TextDecoder().decode(encryptedBytes);
291→          const encrypted: EncryptedFolderMetadata = JSON.parse(encryptedJson);
292→          const metadata = await decryptFolderMetadata(encrypted, subfolderKey);
293→
294→          // Push current state to nav stack
295→          if (folderKey) {
296→            navStackRef.current.push({
297→              folderId: breadcrumbs[breadcrumbs.length - 1]?.id ?? '',
298→              folderName: breadcrumbs[breadcrumbs.length - 1]?.name ?? '',
299→              children: folderChildren,
300→              folderKey,
301→            });
302→          }
303→
304→          // Update state
305→          setFolderChildren(metadata.children ?? []);
306→          setFolderKey(subfolderKey);
307→          subfolderKeyStored = true;
308→          setBreadcrumbs((prev) => [...prev, { id: folderId, name: folderName }]);
309→        } finally {
310→          if (!subfolderKeyStored) subfolderKey.fill(0);
311→        }
312→      } catch (err) {
313→        console.error('Failed to navigate to subfolder:', err);
314→        setError('Failed to open subfolder');
315→      } finally {
316→        setIsLoading(false);
317→      }
318→    },
319→    [currentShareId, folderChildren, folderKey, breadcrumbs, getShareKeys]
320→  );
321→
322→  /**
323→   * Navigate back to the top-level shared list.
324→   * Zeroes all decrypted folder keys from memory before clearing state.
325→   */
326→  const navigateToRoot = useCallback(() => {
327→    // Zero current folder key
328→    if (folderKey) folderKey.fill(0);
329→    // Zero all nav stack folder keys
330→    for (const entry of navStackRef.current) {
331→      entry.folderKey.fill(0);
332→    }
333→    setCurrentView('list');
334→    setCurrentShareId(null);
335→    setFolderChildren([]);
336→    setFolderKey(null);
337→    setBreadcrumbs([]);
338→    navStackRef.current = [];
339→    setError(null);
340→  }, [folderKey]);
341→
342→  /**
343→   * Navigate up one level.
344→   */
345→  const navigateUp = useCallback(() => {
346→    if (navStackRef.current.length > 0) {
347→      // Zero current folder key before replacing
348→      if (folderKey) folderKey.fill(0);
349→      // Pop from nav stack
350→      const prev = navStackRef.current.pop()!;
351→      setFolderChildren(prev.children);
352→      setFolderKey(prev.folderKey);
353→      setBreadcrumbs((crumbs) => crumbs.slice(0, -1));
354→    } else if (currentView === 'folder') {
355→      // Back to top-level list
356→      navigateToRoot();
357→    }
358→  }, [currentView, folderKey, navigateToRoot]);
359→
360→  /**
361→   * Navigate directly to a breadcrumb level, zeroing all intermediate keys.
362→   * More efficient than calling navigateUp() in a loop.
363→   */
364→  const navigateToBreadcrumb = useCallback(
365→    (crumbIndex: number) => {
366→      const popsNeeded = breadcrumbs.length - 1 - crumbIndex;
367→      if (popsNeeded <= 0) return;
368→      // Zero the current folder key
369→      if (folderKey) folderKey.fill(0);
370→      // Zero and discard all intermediate keys
371→      for (let i = 0; i < popsNeeded - 1; i++) {
372→        const entry = navStackRef.current.pop();
373→        if (entry) entry.folderKey.fill(0);
374→      }
375→      // Restore the target level
376→      const target = navStackRef.current.pop();
377→      if (target) {
378→        setFolderChildren(target.children);
379→        setFolderKey(target.folderKey);
380→        setBreadcrumbs((crumbs) => crumbs.slice(0, crumbIndex + 1));
381→      }
382→    },
383→    [breadcrumbs, folderKey]
384→  );
385→
386→  /**
387→   * Download a shared file from the top-level list.
388→   * The share's encryptedKey wraps the parent folder key (needed to decrypt file metadata).
389→   * The actual file key is stored as a child key in share_keys.
390→   */
391→  async function downloadSharedFileFromShare(
392→    share: ReceivedShare,
393→    privateKey: Uint8Array
394→  ): Promise<void> {
395→    if (share.itemType !== 'file') return;
396→
397→    const downloadStore = useDownloadStore.getState();
398→
399→    try {
400→      downloadStore.startDownload(share.itemName);
401→
402→      // Unwrap the parent folder key from the share record
403→      const parentFolderKey = await unwrapKey(hexToBytes(share.encryptedKey), privateKey);
404→
405→      let fileMeta: Awaited<ReturnType<typeof decryptFileMetadata>>;
406→      try {
407→        // Resolve file IPNS metadata and decrypt with parent folder key
408→        const resolved = await resolveIpnsRecord(share.ipnsName);
409→        if (!resolved) {
410→          throw new Error('File metadata IPNS not found');
411→        }
412→
413→        const encryptedBytes = await fetchFromIpfs(resolved.cid);
414→        const encryptedJson = new TextDecoder().decode(encryptedBytes);
415→        const encrypted: EncryptedFileMetadata = JSON.parse(encryptedJson);
416→        fileMeta = await decryptFileMetadata(encrypted, parentFolderKey);
417→      } finally {
418→        parentFolderKey.fill(0);
419→      }
420→
421→      // Get the re-wrapped file key from share_keys
422→      const keys = await fetchShareKeys(share.shareId);
423→      const fileKeyRecord = keys.find((k) => k.keyType === 'file');
424→      if (!fileKeyRecord) {
425→        throw new Error('No re-wrapped file key available for this file');
426→      }
427→
428→      // Download and decrypt using the re-wrapped file key
429→      const plaintext = await downloadFile(
430→        {
431→          cid: fileMeta.cid,
432→          iv: fileMeta.fileIv,
433→          wrappedKey: fileKeyRecord.encryptedKey,
434→          originalName: share.itemName,
435→          encryptionMode: fileMeta.encryptionMode,
436→        },
437→        privateKey
438→      );
439→
440→      downloadStore.setDecrypting();
441→      triggerBrowserDownload(plaintext, share.itemName);
442→      downloadStore.setSuccess();
443→    } catch (err) {
444→      const message = (err as Error).message || 'Download failed';
445→      downloadStore.setError(message);
446→      console.error('Shared file download failed:', err);
447→    }
448→  }
449→
450→  /**
451→   * Download a shared file from within a shared folder.
452→   * Uses re-wrapped file keys from share_keys.
453→   */
454→  const downloadSharedFile = useCallback(
455→    async (item: FilePointer) => {
456→      const auth = useAuthStore.getState();
457→      if (!auth.vaultKeypair || !currentShareId || !folderKey) return;
458→
459→      const downloadStore = useDownloadStore.getState();
460→
461→      try {
462→        downloadStore.startDownload(item.name);
463→
464→        // Get share keys for file key lookup
465→        const keys = await getShareKeys(currentShareId);
466→
467→        // First resolve the file metadata using the parent folder key
468→        const resolved = await resolveIpnsRecord(item.fileMetaIpnsName);
469→        if (!resolved) {
470→          throw new Error('File metadata IPNS not found');
471→        }
472→
473→        const encryptedBytes = await fetchFromIpfs(resolved.cid);
474→        const encryptedJson = new TextDecoder().decode(encryptedBytes);
475→        const encrypted: EncryptedFileMetadata = JSON.parse(encryptedJson);
476→        const fileMeta = await decryptFileMetadata(encrypted, folderKey);
477→
478→        // Look for a re-wrapped file key in share_keys
479→        const fileKeyRecord = keys.find((k) => k.keyType === 'file' && k.itemId === item.id);
480→
481→        if (!fileKeyRecord) {
482→          throw new Error('No re-wrapped file key available for this file');
483→        }
484→
485→        // Use re-wrapped file key from share_keys
486→        // downloadFile handles unwrapping internally via wrappedKey + privateKey
487→        const plaintext = await downloadFile(
488→          {
489→            cid: fileMeta.cid,
490→            iv: fileMeta.fileIv,
491→            wrappedKey: fileKeyRecord.encryptedKey,
492→            originalName: item.name,
493→            encryptionMode: fileMeta.encryptionMode,
494→          },
495→          auth.vaultKeypair.privateKey
496→        );
497→
498→        downloadStore.setDecrypting();
499→        triggerBrowserDownload(plaintext, item.name);
500→        downloadStore.setSuccess();
501→      } catch (err) {
502→        const message = (err as Error).message || 'Download failed';
503→        downloadStore.setError(message);
504→        console.error('Shared file download failed:', err);
505→      }
506→    },
507→    [currentShareId, folderKey, getShareKeys]
508→  );
509→
510→  /**
511→   * Hide a shared item from the user's view.
512→   */
513→  const hideSharedItem = useCallback(async (shareId: string) => {
514→    try {
515→      await hideShare(shareId);
516→      useShareStore.getState().removeReceivedShare(shareId);
517→      setSharedItems((prev) => prev.filter((s) => s.share.shareId !== shareId));
518→    } catch (err) {
519→      console.error('Failed to hide share:', err);
520→      setError('Failed to hide shared item');
521→    }
522→  }, []);
523→
524→  return {
525→    currentView,
526→    currentShareId,
527→    sharedItems,
528→    folderChildren,
529→    folderKey,
530→    breadcrumbs,
531→    isLoading,
532→    error,
533→    navigateToShare,
534→    navigateToSubfolder,
535→    navigateUp,
536→    navigateToRoot,
537→    navigateToBreadcrumb,
538→    downloadSharedFile,
539→    hideSharedItem,
540→  };
541→}
542→

