  1→import {
  2→  Controller,
  3→  Post,
  4→  Get,
  5→  Delete,
  6→  Patch,
  7→  Body,
  8→  Param,
  9→  Query,
 10→  UseGuards,
 11→  Request,
 12→  ParseUUIDPipe,
 13→  HttpCode,
 14→  HttpStatus,
 15→  NotFoundException,
 16→  BadRequestException,
 17→} from '@nestjs/common';
 18→import { ApiTags, ApiOperation, ApiResponse, ApiBearerAuth, ApiQuery } from '@nestjs/swagger';
 19→import { ThrottlerGuard } from '@nestjs/throttler';
 20→import { JwtAuthGuard } from '../auth/guards/jwt-auth.guard';
 21→import { SharesService } from './shares.service';
 22→import { CreateShareDto } from './dto/create-share.dto';
 23→import { AddShareKeysDto } from './dto/share-key.dto';
 24→import { UpdateEncryptedKeyDto } from './dto/update-encrypted-key.dto';
 25→import {
 26→  CreateShareResponseDto,
 27→  ReceivedShareResponseDto,
 28→  SentShareResponseDto,
 29→  PendingRotationResponseDto,
 30→  ShareKeyResponseDto,
 31→} from './dto/share-response.dto';
 32→
 33→interface RequestWithUser extends Request {
 34→  user: {
 35→    id: string;
 36→  };
 37→}
 38→
 39→@ApiTags('shares')
 40→@ApiBearerAuth()
 41→@UseGuards(JwtAuthGuard, ThrottlerGuard)
 42→@Controller('shares')
 43→export class SharesController {
 44→  constructor(private readonly sharesService: SharesService) {}
 45→
 46→  @Post()
 47→  @ApiOperation({
 48→    summary: 'Create a share',
 49→    description:
 50→      'Share an encrypted folder or file with another user. ' +
 51→      'The encryptedKey is the item key re-wrapped for the recipient via ECIES.',
 52→  })
 53→  @ApiResponse({ status: 201, description: 'Share created', type: CreateShareResponseDto })
 54→  @ApiResponse({ status: 401, description: 'Unauthorized' })
 55→  @ApiResponse({ status: 404, description: 'Recipient not found' })
 56→  @ApiResponse({ status: 409, description: 'Share already exists or self-share' })
 57→  async createShare(
 58→    @Request() req: RequestWithUser,
 59→    @Body() dto: CreateShareDto
 60→  ): Promise<{
 61→    shareId: string;
 62→    itemType: string;
 63→    ipnsName: string;
 64→    itemName: string;
 65→    encryptedKey: string;
 66→    createdAt: Date;
 67→  }> {
 68→    const share = await this.sharesService.createShare(req.user.id, dto);
 69→    return {
 70→      shareId: share.id,
 71→      itemType: share.itemType,
 72→      ipnsName: share.ipnsName,
 73→      itemName: share.itemName,
 74→      encryptedKey: share.encryptedKey.toString('hex'),
 75→      createdAt: share.createdAt,
 76→    };
 77→  }
 78→
 79→  @Get('received')
 80→  @ApiOperation({
 81→    summary: 'List received shares',
 82→    description: 'Get all active, non-hidden shares received by the authenticated user.',
 83→  })
 84→  @ApiResponse({
 85→    status: 200,
 86→    description: 'List of received shares',
 87→    type: [ReceivedShareResponseDto],
 88→  })
 89→  @ApiResponse({ status: 401, description: 'Unauthorized' })
 90→  async getReceivedShares(@Request() req: RequestWithUser): Promise<
 91→    Array<{
 92→      shareId: string;
 93→      sharerPublicKey: string;
 94→      itemType: string;
 95→      ipnsName: string;
 96→      itemName: string;
 97→      encryptedKey: string;
 98→      createdAt: Date;
 99→    }>
100→  > {
101→    const shares = await this.sharesService.getReceivedShares(req.user.id);
102→    return shares.map((s) => ({
103→      shareId: s.id,
104→      sharerPublicKey: s.sharer.publicKey,
105→      itemType: s.itemType,
106→      ipnsName: s.ipnsName,
107→      itemName: s.itemName,
108→      encryptedKey: s.encryptedKey.toString('hex'),
109→      createdAt: s.createdAt,
110→    }));
111→  }
112→
113→  @Get('sent')
114→  @ApiOperation({
115→    summary: 'List sent shares',
116→    description: 'Get all active shares created by the authenticated user.',
117→  })
