  // Debug: capture console errors to diagnose "Failed to open shared item"
  const consoleMessages: string[] = [];
  bob.page.on('console', (msg) => {
    consoleMessages.push(`[${msg.type()}] ${msg.text()}`);
  });

  // Navigate to Bob's shared view
  await navigateToShared(bob);
  await bobSharedBrowser.waitForLoaded({ timeout: 30000 });

  // Find and open the shared folder
  await bobSharedBrowser.waitForSharedItem(sharedFolderName, { timeout: 15000 });

  // Click the shared item and wait for folder view
  await bobSharedBrowser.openSharedItem(sharedFolderName);

  // Wait a bit for the navigation to process
  await bob.page.waitForTimeout(3000);

  // Dump console to see the actual error
  console.log('[4.2] Console messages:', consoleMessages.filter(m =>
    m.includes('error') || m.includes('Error') || m.includes('fail') || m.includes('Failed') || m.includes('warn')
  ));

  // Check if error displayed
  const errorVisible = await bob.page.locator('.shared-error[role="alert"]').isVisible();
  if (errorVisible) {
    const errorText = await bob.page.locator('.shared-error[role="alert"]').textContent();
    console.log('[4.2] Error alert text:', errorText);
  }

  // Check if parent dir row appeared (folder navigation succeeded)
  const parentDirVisible = await bobSharedBrowser.parentDirRow().isVisible();
  console.log('[4.2] Parent dir visible:', parentDirVisible);

  if (!parentDirVisible) {
