/**
 * Wait for recipients to finish loading.
 * Waits for actual content (recipient rows or empty state) to appear,
 * avoiding the race where the loading indicator is never rendered.
 */
async waitForRecipientsLoaded(): Promise<void> {
  // The loading state (recipientsLoading) starts false and is set to true
  // in a useEffect, so the loading indicator may never appear in the DOM.
  // Instead, wait for definitive content: either recipient rows or empty state.
  await this.page
    .locator('.share-recipient, .share-recipients-empty')
    .first()
    .waitFor({ state: 'visible', timeout: 10000 });
}

