1085→      <MoveDialog
1086→        open={batchMoveDialog.open}
1087→        onClose={closeBatchMoveDialog}
1088→        onConfirm={handleBatchMoveConfirm}
1089→        item={null}
1090→        items={batchMoveDialog.items}
1091→        currentFolderId={currentFolderId}
1092→        isLoading={isOperating}
1093→      />
1094→
1095→      {/* Upload modal (self-manages visibility) */}
1096→      <UploadModal />
1097→    </div>
1098→  );
1099→}
