  1→import { type DragEvent, type MouseEvent } from 'react';
  2→import type { FolderChild } from '@cipherbox/crypto';
  3→import { FileListItem, type DragItem } from './FileListItem';
  4→import { ParentDirRow } from './ParentDirRow';
  5→
  6→type FileListProps = {
  7→  /** Items to display (files and folders) */
  8→  items: FolderChild[];
  9→  /** Set of currently selected item IDs */
 10→  selectedIds: Set<string>;
 11→  /** Parent folder ID (for drag operations) */
 12→  parentId: string;
 13→  /** Whether to show [..] PARENT_DIR row (non-root folders) */
 14→  showParentRow?: boolean;
 15→  /** Callback when [..] row is clicked to navigate up */
 16→  onNavigateUp?: () => void;
 17→  /** Callback when an item is selected (with modifier key info) */
 18→  onSelect: (
 19→    itemId: string,
 20→    event: { ctrlKey: boolean; shiftKey: boolean; metaKey: boolean }
 21→  ) => void;
 22→  /** Callback to select/deselect all items */
 23→  onSelectAll: () => void;
 24→  /** Callback when navigating into a folder */
 25→  onNavigate: (folderId: string) => void;
 26→  /** Callback when context menu is requested */
 27→  onContextMenu: (event: MouseEvent, item: FolderChild) => void;
 28→  /** Callback when drag starts */
 29→  onDragStart: (event: DragEvent, item: FolderChild) => void;
 30→  /** Callback when items are dropped onto a folder */
 31→  onDropOnFolder?: (items: DragItem[], sourceParentId: string, destFolderId: string) => void;
 32→  /** Callback when external files are dropped onto a folder */
 33→  onExternalFileDrop?: (files: File[], destFolderId: string) => void;
 34→};
 35→
 36→/**
 37→ * Sort items: folders first, then files, both alphabetically.
 38→ */
 39→function sortItems(items: FolderChild[]): FolderChild[] {
 40→  return [...items].sort((a, b) => {
 41→    // Folders come first
 42→    if (a.type === 'folder' && b.type !== 'folder') return -1;
 43→    if (a.type !== 'folder' && b.type === 'folder') return 1;
 44→
 45→    // Within same type, sort alphabetically (case-insensitive)
 46→    return a.name.localeCompare(b.name, undefined, { sensitivity: 'base' });
 47→  });
 48→}
