 50→/**
 51→ * File list component displaying files and folders in a columnar layout.
 52→ *
 53→ * Renders items sorted by type (folders first) then alphabetically.
 54→ * Supports selection, navigation, context menu, and drag operations.
 55→ *
 56→ * @example
 57→ * ```tsx
 58→ * function FileBrowser() {
 59→ *   const { currentFolder } = useFolderNavigation();
 60→ *   const [selectedId, setSelectedId] = useState<string | null>(null);
 61→ *
 62→ *   return (
 63→ *     <FileList
 64→ *       items={currentFolder?.children ?? []}
 65→ *       selectedId={selectedId}
 66→ *       parentId={currentFolder?.id ?? 'root'}
 67→ *       onSelect={setSelectedId}
 68→ *       onNavigate={navigateTo}
 69→ *       onContextMenu={handleContextMenu}
 70→ *       onDragStart={handleDragStart}
 71→ *     />
 72→ *   );
 73→ * }
 74→ * ```
 75→ */
 76→export function FileList({
 77→  items,
 78→  selectedIds,
 79→  parentId,
 80→  showParentRow,
 81→  onNavigateUp,
 82→  onSelect,
 83→  onSelectAll,
 84→  onNavigate,
 85→  onContextMenu,
 86→  onDragStart,
 87→  onDropOnFolder,
 88→  onExternalFileDrop,
 89→}: FileListProps) {
 90→  const sortedItems = sortItems(items);
 91→  const allSelected = items.length > 0 && selectedIds.size === items.length;
 92→
 93→  return (
 94→    <div className="file-list" role="grid">
 95→      {/* Header row */}
 96→      <div className="file-list-header" role="row">
 97→        <div className="file-list-header-name" role="columnheader">
 98→          <span
 99→            className="file-list-header-checkbox"
100→            onClick={onSelectAll}
101→            role="checkbox"
102→            aria-checked={allSelected}
103→            aria-label={allSelected ? 'Deselect all' : 'Select all'}
104→            tabIndex={0}
105→            onKeyDown={(e) => {
106→              if (e.key === 'Enter' || e.key === ' ') {
107→                e.preventDefault();
108→                onSelectAll();
109→              }
110→            }}
111→          >
112→            {allSelected ? '[x]' : '[ ]'}
113→          </span>
114→          [NAME]
115→        </div>
116→        <div className="file-list-header-size" role="columnheader">
117→          [SIZE]
118→        </div>
119→        <div className="file-list-header-date" role="columnheader">
120→          [MODIFIED]
121→        </div>
122→      </div>
123→
124→      {/* Item rows */}
125→      <div className="file-list-body" role="rowgroup">
126→        {showParentRow && onNavigateUp && <ParentDirRow onClick={onNavigateUp} />}
127→        {sortedItems.map((item) => (
128→          <FileListItem
129→            key={item.id}
130→            item={item}
131→            isSelected={selectedIds.has(item.id)}
132→            parentId={parentId}
133→            selectedIds={selectedIds}
134→            allItems={items}
135→            onSelect={onSelect}
136→            onNavigate={onNavigate}
137→            onContextMenu={onContextMenu}
138→            onDragStart={onDragStart}
139→            onDrop={
140→              onDropOnFolder && item.type === 'folder'
141→                ? (dragItems, sourceParentId) => onDropOnFolder(dragItems, sourceParentId, item.id)
142→                : undefined
143→            }
144→            onExternalFileDrop={item.type === 'folder' ? onExternalFileDrop : undefined}
145→          />
146→        ))}
147→      </div>
148→    </div>
149→  );
150→}
