  99→/**
 100→ * Check if a filename has a text-editable extension.
 101→ */
 102→function isTextFile(name: string): boolean {
 103→  const lower = name.toLowerCase();
 104→  // Handle dotfiles like .gitignore, .editorconfig
 105→  if (TEXT_EXTENSIONS.has(lower)) return true;
 106→  // Handle well-known extensionless filenames like Dockerfile, Makefile
 107→  if (TEXT_FILENAMES.has(lower)) return true;
 108→  const lastDot = lower.lastIndexOf('.');
 109→  if (lastDot === -1) return false;
 110→  return TEXT_EXTENSIONS.has(lower.slice(lastDot));
 111→}
