 144→
 145→/**
 146→ * Check if a filename has a PDF extension.
 147→ */
 148→function isPdfFile(name: string): boolean {
 149→  const lower = name.toLowerCase();
 150→  const lastDot = lower.lastIndexOf('.');
 151→  if (lastDot === -1) return false;
 152→  return PDF_EXTENSIONS.has(lower.slice(lastDot));
 153→}
 154→
 155→/**
 156→ * Check if a filename has a playable audio extension.
 157→ */
 158→function isAudioFile(name: string): boolean {
 159→  const lower = name.toLowerCase();
 160→  const lastDot = lower.lastIndexOf('.');
 161→  if (lastDot === -1) return false;
 162→  return AUDIO_EXTENSIONS.has(lower.slice(lastDot));
 163→}
 164→
 165→/**
 166→ * Check if a filename has a playable video extension.
 167→ */
 168→function isVideoFile(name: string): boolean {
 169→  const lower = name.toLowerCase();
 170→  const lastDot = lower.lastIndexOf('.');
 171→  if (lastDot === -1) return false;
 172→  return VIDEO_EXTENSIONS.has(lower.slice(lastDot));
 173→}
 174→
 175→/**
 176→ * Check if a filename is any previewable type (image, PDF, audio, video).
 177→ */
 178→function isPreviewableFile(name: string): boolean {
 179→  return isImageFile(name) || isPdfFile(name) || isAudioFile(name) || isVideoFile(name);
