 190→/**
 191→ * Main file browser container component.
 192→ *
 193→ * Manages the file list area with in-place navigation.
 194→ * Selection state, context menu, and file/folder actions.
 195→ *
 196→ * Navigation:
 197→ * - Double-click folders to navigate into them
 198→ * - Click [..] PARENT_DIR row to navigate up
 199→ * - Browser history integration via URL routing
 200→ *
 201→ * Actions:
 202→ * - Download: Downloads file from IPFS, decrypts, and triggers browser download
 203→ * - Rename: Opens dialog to rename file/folder
 204→ * - Delete: Shows confirmation, then removes from folder metadata
 205→ * - Move: Drag-drop to folder rows in list
 206→ *
 207→ * @example
 208→ * ```tsx
 209→ * function FilesPage() {
 210→ *   return (
 211→ *     <main className="app-main">
 212→ *       <FileBrowser />
 213→ *     </main>
 214→ *   );
 215→ * }
 216→ * ```
 217→ */
 218→
 219→export function FileBrowser() {
 220→  // Navigation state from hook
 221→  const { currentFolderId, currentFolder, breadcrumbs, isLoading, navigateTo, navigateUp } =
 222→    useFolderNavigation();
 223→
 224→  // Folder operations
 225→  const {
 226→    createFolder,
 227→    renameItem,
 228→    moveItem,
 229→    moveItems,
 230→    deleteItem,
 231→    deleteItems,
 232→    isLoading: isOperating,
 233→  } = useFolder();
 234→
 235→  // File download
 236→  const { download, isDownloading } = useFileDownload();
 237→
 238→  // External file drop upload
 239→  const { handleFileDrop } = useDropUpload();
 240→
 241→  // Context menu state
 242→  const contextMenu = useContextMenu();
 243→
 244→  // Vault and folder stores for sync
 245→  const { rootIpnsName } = useVaultStore();
 246→  const initialSyncComplete = useSyncStore((state) => state.initialSyncComplete);
 247→  const syncStatus = useSyncStore((state) => state.status);
 248→
 249→  // Sync callback - compare remote sequence with local, refresh if different
 250→  const handleSync = useCallback(async () => {
 251→    if (!rootIpnsName) return;
 252→
 253→    // Resolve root folder IPNS to get remote CID and sequence number
 254→    const resolved = await resolveIpnsRecord(rootIpnsName);
 255→    if (!resolved) {
 256→      // If initial sync hasn't completed yet, signal that IPNS isn't available
 257→      // so useSyncPolling keeps the syncing state visible and retries
 258→      if (!useSyncStore.getState().initialSyncComplete) {
 259→        throw new Error('IPNS not resolved yet');
 260→      }
 261→      return;
 262→    }
 263→
 264→    // Read fresh state from store — avoid stale closure from render cycle.
 265→    // On initial load, the root folder may not be in the closure's `folders`
 266→    // yet because useFolderNavigation's useEffect sets it in the same commit.
 267→    const rootFolder = useFolderStore.getState().folders['root'];
 268→    if (!rootFolder) return;
 269→
 270→    // Compare sequence numbers - if remote > local, we need to refresh
 271→    // Sequence number comparison is more reliable than CID since we
 272→    // don't cache the local CID, and sequence always increments
 273→    if (resolved.sequenceNumber <= rootFolder.sequenceNumber) {
 274→      // No changes, already up to date
 275→      return;
 276→    }
 277→
 278→    // Remote has newer version - fetch and decrypt new metadata
 279→    try {
 280→      const metadata = await fetchAndDecryptMetadata(resolved.cid, rootFolder.folderKey);
 281→
 282→      // Update folder store with new children
 283→      // Per CONTEXT.md: last write wins, instant refresh (no toast/prompt)
 284→      useFolderStore.getState().updateFolderChildren('root', metadata.children);
 285→      useFolderStore.getState().updateFolderSequence('root', resolved.sequenceNumber);
 286→    } catch (err) {
 287→      console.error('Sync refresh failed:', err);
 288→      // During initial sync, propagate so useSyncPolling keeps the
 289→      // loading UI visible and retries instead of showing empty state
 290→      if (!useSyncStore.getState().initialSyncComplete) {
 291→        throw err;
 292→      }
 293→    }
 294→  }, [rootIpnsName]);
 295→
 296→  // Start sync polling (30s interval, pauses when backgrounded/offline)
 297→  useSyncPolling(handleSync);
 298→
 299→  // External drag state — tracks when files from OS are being dragged over
 300→  const [isDraggingExternal, setIsDraggingExternal] = useState(false);
 301→  const dragCounterRef = useRef(0);
 302→
 303→  /**
 304→   * Detect external file drag entering the content area.
 305→   * Uses a counter to handle nested dragenter/dragleave events correctly.
 306→   */
 307→  const handleContentDragEnter = useCallback((e: DragEvent) => {
 308→    if (isExternalFileDrag(e.dataTransfer)) {
 309→      dragCounterRef.current += 1;
 310→      if (dragCounterRef.current === 1) {
 311→        setIsDraggingExternal(true);
 312→      }
 313→    }
 314→  }, []);
 315→
 316→  const handleContentDragOver = useCallback((e: DragEvent) => {
 317→    if (isExternalFileDrag(e.dataTransfer)) {
 318→      e.preventDefault();
 319→      e.dataTransfer.dropEffect = 'copy';
 320→    }
 321→  }, []);
 322→
 323→  const handleContentDragLeave = useCallback((e: DragEvent) => {
 324→    if (isExternalFileDrag(e.dataTransfer)) {
 325→      dragCounterRef.current -= 1;
 326→      if (dragCounterRef.current <= 0) {
 327→        dragCounterRef.current = 0;
 328→        setIsDraggingExternal(false);
 329→      }
 330→    }
 331→  }, []);
 332→
 333→  // Reset drag state if user abandons drag outside the window (e.g. drops on desktop
 334→  // or presses Escape). Without this the counter/overlay can get stuck.
 335→  useEffect(() => {
 336→    const resetDragState = () => {
 337→      dragCounterRef.current = 0;
 338→      setIsDraggingExternal(false);
 339→    };
 340→    const handleWindowDrop = (e: Event) => {
 341→      e.preventDefault();
 342→      resetDragState();
 343→    };
 344→    const handleWindowDragLeave = (e: globalThis.DragEvent) => {
 345→      // relatedTarget is null when the drag leaves the browser window entirely
 346→      if (!e.relatedTarget) {
 347→        resetDragState();
 348→      }
 349→    };
 350→    window.addEventListener('dragend', resetDragState);
 351→    window.addEventListener('drop', handleWindowDrop);
 352→    window.addEventListener('dragleave', handleWindowDragLeave as EventListener);
 353→    return () => {
 354→      window.removeEventListener('dragend', resetDragState);
 355→      window.removeEventListener('drop', handleWindowDrop);
 356→      window.removeEventListener('dragleave', handleWindowDragLeave as EventListener);
 357→    };
 358→  }, []);
 359→
 360→  /**
 361→   * Catch-all drop handler for the content area.
 362→   * If files from OS are dropped on general area (not intercepted by a child),
 363→   * upload them to the current folder.
 364→   */
 365→  const handleContentDrop = useCallback(
 366→    (e: DragEvent) => {
 367→      dragCounterRef.current = 0;
 368→      setIsDraggingExternal(false);
 369→
 370→      // Only handle external file drops
 371→      if (!e.dataTransfer.files || e.dataTransfer.files.length === 0) return;
 372→      if (!isExternalFileDrag(e.dataTransfer)) return;
 373→
 374→      e.preventDefault();
 375→      const files = Array.from(e.dataTransfer.files);
 376→      handleFileDrop(files, currentFolderId);
 377→    },
 378→    [handleFileDrop, currentFolderId]
 379→  );
 380→
 381→  /**
 382→   * Handle external file drops targeting a specific folder.
 383→   * Called by FileListItem (folder rows) and Breadcrumbs.
 384→   */
 385→  const handleExternalFileDrop = useCallback(
 386→    (files: File[], targetFolderId: string) => {
 387→      dragCounterRef.current = 0;
 388→      setIsDraggingExternal(false);
 389→      handleFileDrop(files, targetFolderId);
 390→    },
 391→    [handleFileDrop]
 392→  );
 393→
 394→  // Get current folder's children (needed early for selection logic)
 395→  const children = currentFolder?.children ?? [];
 396→  const hasChildren = children.length > 0;
 397→
 398→  // Multi-selection state
 399→  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
 400→  const lastSelectedIdRef = useRef<string | null>(null);
 401→
 402→  // Prune stale selectedIds when children change (sync refresh, upload, external delete)
 403→  const childIds = useMemo(() => new Set(children.map((c) => c.id)), [children]);
 404→  useEffect(() => {
 405→    setSelectedIds((prev) => {
 406→      if (prev.size === 0) return prev;
 407→      const pruned = new Set([...prev].filter((id) => childIds.has(id)));
 408→      if (pruned.size === prev.size) return prev;
 409→      return pruned;
 410→    });
 411→  }, [childIds]);
 412→
 413→  // Compute selected items from IDs for action bar and batch operations
 414→  const selectedItems = useMemo(
 415→    () => children.filter((c) => selectedIds.has(c.id)),
 416→    [children, selectedIds]
 417→  );
 418→  const multiSelectActive = selectedIds.size > 0;
 419→
 420→  // Dialog states
 421→  const [confirmDialog, setConfirmDialog] = useState<DialogState>({ open: false, item: null });
 422→  const [renameDialog, setRenameDialog] = useState<DialogState>({ open: false, item: null });
 423→  const [moveDialog, setMoveDialog] = useState<DialogState>({ open: false, item: null });
 424→  const [detailsDialog, setDetailsDialog] = useState<DialogState>({ open: false, item: null });
 425→  const [editorDialog, setEditorDialog] = useState<DialogState>({ open: false, item: null });
 426→  const [imagePreviewDialog, setImagePreviewDialog] = useState<DialogState>({
 427→    open: false,
 428→    item: null,
 429→  });
 430→  const [pdfPreviewDialog, setPdfPreviewDialog] = useState<DialogState>({
 431→    open: false,
 432→    item: null,
 433→  });
 434→  const [audioPlayerDialog, setAudioPlayerDialog] = useState<DialogState>({
 435→    open: false,
 436→    item: null,
 437→  });
 438→  const [videoPlayerDialog, setVideoPlayerDialog] = useState<DialogState>({
 439→    open: false,
 440→    item: null,
 441→  });
 442→  const [createFolderDialogOpen, setCreateFolderDialogOpen] = useState(false);
 443→
 444→  // Clear selection when navigating to a new folder
 445→  const handleNavigate = useCallback(
 446→    (folderId: string) => {
 447→      setSelectedIds(new Set());
 448→      lastSelectedIdRef.current = null;
 449→      navigateTo(folderId);
 450→    },
 451→    [navigateTo]
 452→  );
 453→
 454→  /**
 455→   * Handle item selection with modifier keys.
 456→   * - Plain click: single select (clears others)
 457→   * - Ctrl/Cmd+click: toggle selection
 458→   * - Shift+click: range select from last clicked to current
 459→   */
 460→  const handleSelect = useCallback(
 461→    (itemId: string, event: { ctrlKey: boolean; shiftKey: boolean; metaKey: boolean }) => {
 462→      const isCtrl = event.ctrlKey || event.metaKey;
 463→      const isShift = event.shiftKey;
 464→
 465→      if (isShift && lastSelectedIdRef.current) {
 466→        // Range select: select all items between lastSelected and current
 467→        // Use sorted children order so the range respects visual order
 468→        const sortedChildren = [...children].sort((a, b) => {
 469→          if (a.type === 'folder' && b.type !== 'folder') return -1;
 470→          if (a.type !== 'folder' && b.type === 'folder') return 1;
 471→          return a.name.localeCompare(b.name, undefined, { sensitivity: 'base' });
 472→        });
 473→
 474→        const ids = sortedChildren.map((c) => c.id);
 475→        const startIdx = ids.indexOf(lastSelectedIdRef.current);
 476→        const endIdx = ids.indexOf(itemId);
 477→
 478→        if (startIdx !== -1 && endIdx !== -1) {
 479→          const rangeStart = Math.min(startIdx, endIdx);
 480→          const rangeEnd = Math.max(startIdx, endIdx);
 481→          const rangeIds = ids.slice(rangeStart, rangeEnd + 1);
 482→
 483→          setSelectedIds((prev) => {
 484→            const next = new Set(prev);
 485→            for (const id of rangeIds) {
 486→              next.add(id);
 487→            }
 488→            return next;
 489→          });
 490→        }
 491→      } else if (isCtrl) {
 492→        // Toggle: add or remove from selection
 493→        setSelectedIds((prev) => {
 494→          const next = new Set(prev);
 495→          if (next.has(itemId)) {
 496→            next.delete(itemId);
 497→          } else {
 498→            next.add(itemId);
 499→          }
 500→          return next;
 501→        });
 502→        lastSelectedIdRef.current = itemId;
 503→      } else {
 504→        // Plain click: single select
 505→        setSelectedIds(new Set([itemId]));
 506→        lastSelectedIdRef.current = itemId;
 507→      }
 508→    },
 509→    [children]
 510→  );
 511→
 512→  // Select/deselect all items
 513→  const handleSelectAll = useCallback(() => {
 514→    if (selectedIds.size === children.length) {
 515→      // All selected -> deselect all
 516→      setSelectedIds(new Set());
 517→      lastSelectedIdRef.current = null;
 518→    } else {
 519→      // Select all
 520→      setSelectedIds(new Set(children.map((c) => c.id)));
 521→    }
 522→  }, [children, selectedIds.size]);
 523→
 524→  // Clear selection
 525→  const clearSelection = useCallback(() => {
 526→    setSelectedIds(new Set());
 527→    lastSelectedIdRef.current = null;
 528→  }, []);
 529→
 530→  // Context menu handler - show context menu
 531→  // If right-clicked item is not in selection, select only that item
 532→  const handleContextMenu = useCallback(
 533→    (event: MouseEvent, item: FolderChild) => {
 534→      if (!selectedIds.has(item.id)) {
 535→        setSelectedIds(new Set([item.id]));
 536→        lastSelectedIdRef.current = item.id;
 537→      }
 538→      contextMenu.show(event, item);
 539→    },
 540→    [contextMenu, selectedIds]
