 3→type SelectionActionBarProps = {
 4→  /** Selected items */
 5→  selectedItems: FolderChild[];
 6→  /** Whether an operation is in progress */
 7→  isLoading: boolean;
 8→  /** Callback to clear selection */
 9→  onClearSelection: () => void;
10→  /** Callback to download selected files */
11→  onDownload: () => void;
12→  /** Callback to move selected items */
13→  onMove: () => void;
14→  /** Callback to delete selected items */
15→  onDelete: () => void;
16→};
17→
18→/**
19→ * Action bar shown when multiple items are selected.
20→ * Displays selection count and batch action buttons.
21→ */
22→export function SelectionActionBar({
23→  selectedItems,
24→  isLoading,
25→  onClearSelection,
26→  onDownload,
27→  onMove,
28→  onDelete,
29→}: SelectionActionBarProps) {
30→  const fileCount = selectedItems.filter((i) => i.type === 'file').length;
31→  const folderCount = selectedItems.filter((i) => i.type === 'folder').length;
32→
33→  // Build description like "3 files", "2 folders", or "2 files, 1 folder"
34→  const parts: string[] = [];
35→  if (fileCount > 0) parts.push(`${fileCount} file${fileCount !== 1 ? 's' : ''}`);
36→  if (folderCount > 0) parts.push(`${folderCount} folder${folderCount !== 1 ? 's' : ''}`);
37→  const description = parts.join(', ');
38→
39→  return (
40→    <div className="selection-action-bar" role="toolbar" aria-label="Selection actions">
41→      <div className="selection-action-bar-info">
42→        <span className="selection-action-bar-count">{description} selected</span>
43→        <button
44→          type="button"
45→          className="selection-action-bar-clear"
46→          onClick={onClearSelection}
47→          aria-label="Clear selection"
48→        >
49→          [clear]
50→        </button>
51→      </div>
52→      <div className="selection-action-bar-actions">
53→        {fileCount > 0 && (
54→          <button
55→            type="button"
56→            className="toolbar-btn toolbar-btn--secondary"
57→            onClick={onDownload}
58→            disabled={isLoading}
59→            aria-label={`Download ${fileCount} file${fileCount !== 1 ? 's' : ''}`}
60→          >
61→            &#8595; download
62→          </button>
63→        )}
64→        <button
65→          type="button"
66→          className="toolbar-btn toolbar-btn--secondary"
67→          onClick={onMove}
68→          disabled={isLoading}
69→          aria-label={`Move ${description}`}
70→        >
71→          &#8594; move
72→        </button>
73→        <button
74→          type="button"
75→          className="toolbar-btn toolbar-btn--secondary selection-action-bar-delete"
76→          onClick={onDelete}
77→          disabled={isLoading}
78→          aria-label={`Delete ${description}`}
79→        >
80→          &#128465; delete
81→        </button>
82→      </div>
83→    </div>
84→  );
85→}
