 518→  test('3.7 Page reload preserves session and reloads root folder', async () => {
 519→    // Reload + re-auth + IPNS sync chain can take >30s in CI
 520→    test.setTimeout(90000);
 521→
 522→    // Navigate to root before reload so we start from a clean state
 523→    await navigateToRoot();
 524→
 525→    // Reload the page — Zustand store is wiped
 526→    await page.reload({ waitUntil: 'domcontentloaded' });
 527→
 528→    // Reset navigation stack since we're starting fresh
 529→    navigationStack.length = 0;
 530→    navigationStack.push('root');
 531→
 532→    // Re-inject test auth state after reload (test-login bypasses Core Kit
 533→    // so there's no persistent session to auto-restore). For real Core Kit
 534→    // flow this is a no-op.
 535→    await reinjectTestAuthAfterReload(page);
