 537→    // Wait for auth to restore (user menu becomes visible)
 538→    await page.locator('[data-testid="user-menu"]').waitFor({
 539→      state: 'visible',
 540→      timeout: 30000,
 541→    });
 542→
 543→    // Wait for initial sync to complete — all root items must appear
 544→    // This proves IPNS root metadata was re-fetched and decrypted
 545→    await fileList.waitForItemToAppear(workspaceFolder, { timeout: 60000 });
